css:
@-moz-document url-prefix("https://blutopia.xyz") {
.chat-messages {margin-top:-10px;}
.chat-messages .list-group-item {background-color:#222222;border-style:solid;border-bottom:0;clear:both;padding-left:30px;padding-top:3px;border-top:1px lines #ddd;margin:10;overflow:hidden;}
.chat-messages .list-group-item .list-group-item-heading .badge-user {font-size:8px;border:50;box-shadow:50 50 50;float:none;}

.chat-messages .list-group-item .list-group-item-heading {float:left;padding-top:3px;padding-right:0;}
.chat-messages .list-group-item .list-group-item-heading, .shoutbox p {margin-left:5px;}

.chat-messages .list-group-item .list-group-item-heading .text-muted {float:right;width:auto;font-size:11px;padding-top:2px;}
.chat-messages .list-group-item .message-content {font-size:12px; line-height:10px;display:inline;}

.chat-messages .profile-avatar { border-radius:50; border:50; width:30px;height:30px;position:absolute;top:-5px;left:1px;}

.shoutbox .panel-footer {padding:0;font-size:12px;background-color:transparent;}
.shoutbox .panel-footer .badge-extra{font-size:12px;}
.shoutbox .form-group {margin:0;}
.shoutbox.panel-chat {padding:0px;}
.panel-heading {margin:-0px;margin-bottom:0px;}
}
