css:
@-moz-document domain("youtube.com") {
:root {
    --yt-swatch-primary: #333 !important;
    --yt-swatch-logo-override: white !important;
    --yt-swatch-text: white !important;

    --yt-app-background: black !important;
    --yt-main-app-background: black !important;
    --yt-main-app-background-tmp: black !important;
    --yt-sidebar-background: #222 !important;
    
    --yt-searchbox-background: black !important;
    --yt-searchbox-text-color: white !important;
    --ytd-searchbox-legacy-border-color: #999 !important;
    --ytd-searchbox-legacy-border-shadow-color: #333 !important;
    --ytd-searchbox-legacy-button-color: black !important;
    --ytd-searchbox-legacy-button-icon-color: white !important;
    --ytd-searchbox-legacy-button-hover-color: #333 !important;
    
    --yt-guide-background: #333 !important;
    --yt-playlist-background-header: #222 !important;
    --yt-playlist-message-text: #DDD !important;
    --yt-playlist-background-item: #333 !important;
    
    --yt-primary-color: white !important;
    --yt-primary-text-color: white !important;
    --yt-secondary-text-color: #DDD !important;
    --yt-endpoint-color: white !important;
    --yt-endpoint-hover-color: #DDD !important;
    --yt-metadata-color: white !important;
    --yt-expand-color: white !important;
    --yt-placeholder-text-color: white !important;
    --yt-button-text-color: white !important;
    --ytd-metadata-line-color: white !important;
    --ytd-video-publish-date-color: white !important;
    --ytd-comment-text-color: white !important;
    --yt-icon-color: white !important;
    --iron-icon-fill-color: white !important;
    
    --paper-menu-background-color: #333 !important;
    --yt-menu-hover-backgound-color: #444 !important;
    
    --yt-lightsource-section1-color: #222 !important;
    --yt-dialog-background: #333 !important;
    
    --yt-copyright-text: #999 !important;
}

ytd-menu-popup-renderer {
    box-shadow: 0 2px 2px 0 rgba(255, 255, 255, 0.1), 0 1px 5px 0 rgba(255, 255, 255, 0.1), 0 3px 1px -2px rgba(255, 255, 255, 0.2);
}

a.yt-simple-endpoint.yt-formatted-string {
    color: white !important;
}

#channel-header.ytd-c4-tabbed-header-renderer {
    background: #222 !important;
}

#tabs-inner-container.ytd-c4-tabbed-header-renderer {
    background: #222 !important;
}

yt-icon, .yt-icon-container.yt-icon {
    fill: white;
}

paper-button.yt-next-continuation {
    background: #333 !important;
    color: white !important;
}

ytd-subscribe-button-renderer[button-style="COMPACT_GRAY"] paper-button.ytd-subscribe-button-renderer {
    background: #333 !important;
    color: white !important;
}

paper-button.ytd-subscribe-button-renderer[subscribed] {
    background-color: #333 !important;
    color: white !important;
}

.deemphasize.yt-formatted-string {
    color: white !important;
}
}
