css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("lioden.com") {
    body {
        background: url('http://orig04.deviantart.net/2716/f/2016/019/b/d/coffee_by_roselynn1214-d9oki4d.png')!important;
        background-size: 100%!important;
        background-attachment: fixed!important;
    }
    h1,
    h2,
    h3 {
        color: black!important;
    }
    .table>td,
    .pagination>span {
        background: #be9b7b!important;
        color: black!important;
    }
    td.right,
    .sub_menu,
    .right_odd.xsmall,
    .right_locked,
    td.right_odd,
    span.left,
    .customiser-sortable li:nth-child(2n+1) {
        background: #be9b7b!important;
        color: black!important;
    }
    th,
    td.top,
    td.bottom,
    div.left {
        background: #4b3832!important;
        color: black!important;
    }
    td.left.b.center,
    td.left.b,
    td.left,
    td.right_cub,
    .sub_menu_item_selected {
        background: #854442!important;
        color: black!important;
    }
    a:link,
    a:visited {
        color: black!important;
    }
    a:hover,
    a:active {
        color: white!important;
        transition: all ease 2s;
    }
    .sidebar,
    .navbar {
        background: #4b3832!important;
        color: black!important;
    }
    .navbar {
        border-top-right-radius: 15px;
    }
    .sidebar {
        border-bottom-right-radius: 15px;
        border-left: 1px solid black;
        text-align: justify;
    }
    .navbar-default .navbar-nav > li > a {
        color: black!important;
    }
    .navbar-default .navbar-nav > li > a:hover {
        background: #854442!important;
        transition: all ease 3s;
    }
    .xsmall {
        background: none;
    }
    .panel,
    .sub_menu,
    .sub_sub_menu,
    .pagination span {
        box-shadow: none!important;
        border: 1px solid black!important;
        background: #be9b7b!important;
        border-radius: 15px;
        color: black!important;
    }
    div.feature {
        box-shadow: none!important;
        border: 1px solid black!important;
        background: #be9b7b!important;
        border-radius: 15px;
        color: black!important;
    }
    .sub_menu_item_selected {
        background: #854442!important;
    }
    .chat_light {
        background: #be9b7b!important;
        color: black!important;
    }
    .chat_dark {
        background: #3c2f2f!important;
        color: black!important;
    }
    td.unread-msg.small,td.unread-msg.center {
        background: #fff4e6!important;
    }
    .topbar,
    .breadcrumb {
        background: #4b3832!important;
        color: black!important;
    }
    .breadcrumb > .active {
        color: white!important;
    }
    input[type="submit"],
    input[type="button"] {
        color: #fff4e6!important;
        background: #854442!important;
        border: 2px solid black!important;
        box-shadow: none!important;
        text-shadow: none!important;
        transition: all ease 1s;
        border-radius: 15px;
    }
    input[type="submit"]:hover,
    input[type="button"]:hover {
        color: #3c2f2f!important;
        background: #854442!important;
        border: 2px solid black;
        box-shadow: none;
        text-shadow: none;
        border-radius: 15px!important;
    }
    .progress-bar-warning,
    .progress-bar-danger,
    .progress-bar-success,
    .progress-bar {
        background: #854442!important;
    }
    .progress {
        background: #fff4e6!important;
        border: 1px solid black;
        border-radius: 15px;
    }
    .progress div {
        color: black!important;
    }
    div#lion_image>div {
        background: url('http://orig03.deviantart.net/85f9/f/2016/072/e/9/coffee_lion_bg_by_roselynn1214-d9uwjoz.png')!important;
    }
    table.table {
        border: 1px solid black!important;
    }
    input[type="text"] {
        color: black!important;
    }
    input[type="password"] {
        color: black!important;
    }
    textarea {
        color: black!important;
    }
    select {
        color: black!important;
    }
    div.container.main {
        background: #fff4e6!important;
        box-shadow: none!important;
        border: 2px solid black!important;
        border-top-right-radius: 15px;
        border-bottom-right-radius: 15px;
        border-bottom-left-radius: 15px;
    }
    div.col-md-9.col-xs-12 {
        background: none!important;
        border: none!important;
        border-top: 2px solid black;
    }
    .item {
        border: 1px black solid!important;
        background: #be9b7b!important;
    }
    .item-footer {
        background: #4b3832!important;
        color: black!important;
    }
    .item-header {
        background: #4b3832!important;
        color: black!important;
    }
    input[type="text"] {
        background: #3c2f2f!important;
        color: black!important;
        border: 1px solid black!important;
        transition: all ease 1s;
        border-radius: 15px!important;
    }
    input[type="text"]:focus {
        background: #fff4e6!important;
        color: black!important;
        border: 1px solid black!important;
        transition: all ease 1s;
        border-radius: 15px!important;
    }
    input[type="password"] {
        background: #3c2f2f!important;
        color: black!important;
        border: 1px solid black;
        transition: all ease 1s;
        border-radius: 15px!important;
    }
    input[type="password"]:focus {
        background: #fff4e6!important;
        color: black!important;
        border: 1px solid black!important;
        transition: all ease 1s;
        border-radius: 15px!important;
    }
    textarea {
        background: #3c2f2f!important;
        color: black!important;
        border: 1px solid black!important;
        transition: all ease 1s;
    }
    textarea:focus {
        background: #fff4e6!important;
        color: black!important;
        border: 1px solid black!important;
        transition: all ease 1s;
    }
    select {
        background: #fff4e6!important;
        color: black!important;
        transition: all ease 1s;
    }
}
