css:
@-moz-document domain("www.spigotmc.org") {
#logo img {
    filter: hue-rotate(325deg);
    -webkit-filter: hue-rotate(325deg);
    -moz-filter: hue-rotate(300deg);
    -o-filter: hue-rotate(300deg);
    -ms-filter: hue-rotate(300deg);
}

#logoBlock
.spigot_colorOverlay {
    background-color: rgba(193, 61, 61, .8);
}

#navigation
.navTabs, .breadcrumb, .uix_breadCrumb_toggleList li.toggleList_item {
    background: #e12020 linear-gradient(to bottom, rgba(255,255,255,0.2) 0%,rgba(255,255,255,0) 100%);
}
.sectionMain {
    border: 1px solid #fcd7d7;
}

.button.primary {
    background-color: #e12020;
}
#userBar
.navTabs .navTab.selected.PopupOpen .navLink {
    background-color: #e12020;
}

.Menu {
    border-top: 2px solid #e12020;
}

.heading, .xenForm .formHeader {
    background-color: #e12020;
}

.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background-color: #e12020;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .resourceListSidebar h3 {
    background: #e12020 linear-gradient(to bottom, rgba(255,255,255,0.2) 0%,rgba(255,255,255,0) 100%);
}

.footer .pageContent {
    border-top: 3px solid #e12020;
}
.navbar-inverse {
    background-color: #e12020;
}
.box-icon {
    background-color: #e12020;
}
.btn-warning {
    background-image: radial-gradient(circle, #f76161 80%, #e12020 81%);
}
.btn-warning:hover, .btn-warning:focus, .btn-warning.focus, .btn-warning:active, .btn-warning.active, .open>.dropdown-toggle.btn-warning {
    background-color: #e12020;
}

.btn-warning {
    background-color: #e12020;
}

.gadget.color3 .dashboard-item-header, .gadget-colors .color3>a {
    background-color: #e12020;
}
.aui-header {
    background: #e12020;
    border-bottom-color: #e12020;
}
.gadget.color1 .dashboard-item-header, .gadget-colors .color1>a {
    background-color: #f76161;
}
.aui-dropdown2.aui-style-default .active {
    background-color: #f76161;
}
.aui-header .aui-button.aui-button-primary.aui-style {
    background-color: #f99898;
    background-image: linear-gradient(to bottom, #98d6f9, #62bef7);
}
.button.primary {
    background-color: #e12020;
}

#userBar
.navTabs .navTab.Popup.PopupContainerControl.PopupOpen,
#userBar
.navTabs .navTab.selected.PopupOpen .navLink {
    background-color: #e12020;
}
.Menu {
    border-top: 2px solid #e12020;
}

.heading, .xenForm .formHeader {
    background-color: #e12020;
}

.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background-color: #e12020;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .resourceListSidebar h3 {
    background: #e12020 linear-gradient(to bottom, rgba(255,255,255,0.2) 0%,rgba(255,255,255,0) 100%);
}

.footer .pageContent {
    border-top: 3px solid #e12020;
}
#QuickSearch
.primaryControls .uix_icon {
    background-color: #ff8383;
}

.navTabs .navTab.selected .navLink, .navTabs .navTab.PopupClosed.selected .navLink {
    color: #a53333;
}

.navTabs .navTab.Popup.PopupContainerControl.PopupOpen, .navTabs .navTab.selected.PopupOpen .navLink {
    background-color: #ff8383;
}
.primaryContent a {
    color: #b21010;
}

.button {
    background-color: #fce0e0;
}

.sidebar .visitorPanel .username {
    color: #b21010;
}

.discussionListItems .unread .title a {
    color: #b21010;
}

.button:hover, .button[href]:hover, .buttonProxy:hover .button {
    background-color: #ffeaea;
    color: #535353;
}

.navTabs .navTab.selected .navLink:hover {
    color: #a53333;
}

.footerLinks a[href]:hover {
    color: #a53333;
}

a:link, a:visited {
    color: #a53333;
}

.footer .choosers a:hover {
    color: #a53333;
}

.footer a, .footer a:visited {
    color: #a53333;
}

.discussionListItems .unread .lastPostInfo .username {
    color: #a53333;
}
}
