css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('phone.firertc.com') {
/** Color Pallette **/
/*
Background Grey: #333;
Surrounding Black: #222;
Text Boxes: #222;
Text Color: #e6e6e6;
Button Color: #777;
*/

/** Phone **/
/* Jumbotron -or- Main */
.jumbotron {
  background-color: #333;
  }

body .container .inner-container .jumbotron {
  background-color: #333;
  }

/* Surrounding Background */
body {
  background-color: #222;
  color: #e6e6e6;
  }

/* %s "is on line" Box*/
body[data-controller="home"][data-action="phone"] .phone-container .phone header.header .status {
  background-color: #777;
  border: none;
  }

/* Call Button */
body[data-controller="home"][data-action="phone"] .phone-container .phone .body .buttons a.btn, body[data-controller="home"][data-action="phone"] .phone-container .phone .body .buttons button, body[data-controller="home"][data-action="phone"] .phone-container .phone .body .buttons input.btn {
  background-color: #777;
  }

/* Standard Button */
button {
  background-color: #777 !important;
  }

/* Dialer button */
body[data-controller="home"][data-action="phone"] .phone-container .phone .body .toggle-bar .btn.clicked {
  background-color: #e6e6e6;
  color: #e6e6e6;
  border-color: #333;
  }

body[data-controller="home"][data-action="phone"] .phone-container .phone .body .toggle-bar .btn {
  color: #e6e6e6;
  }

/* dialpad buttons */
.dialer .dialpad .buttons {
  background-color: #333;
  }

/* Dialpad single button */
.dialer .dialpad .buttons dl.button {
  background-color: #222;
  }

.dialer .dialpad .buttons dl.button:hover {
  background-color: #444;
  }

/* Number Bar thing */
.form-control {
  background-color: #222;
  color: #e6e6e6;
  }

/* Poweroff Phone button*/
body[data-controller="home"][data-action="phone"] .phone-container .phone .btn[data-action="reject"], body[data-controller="home"][data-action="phone"] .phone-container .phone .btn[data-action="logout"] {
  background-color: #e27c79 !important;
  color: #e6e6e6;
  }

/* Call Button */
body[data-controller="home"][data-action="phone"] .phone-container .phone .btn[data-action="call"], body[data-controller="home"][data-action="phone"] .phone-container .phone .btn[data-action="answer"] {
  background-color: #80c780 !important;
  color: #e6e6e6;
  }

/* Number Bar Thing right side  dropdown button */
.btn .caret {
  color: #e6e6e6;
  }

/** Dropdown-Menu **/
.dropdown-menu {
  background-color: #333;
  }

/* list-item on dropdown-menu */
.dropdown-menu > li > a {
  color: #e6e6e6;
  background-color: #333;
  }

/* Hovered Item */
.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
  color: #327cbb;
  background-color: #333;
  }

/* Phone list-item */
body .container .inner-container .jumbotron .menu ul.dropdown-menu li.active a {
  background-color: #333;
  }

/** Recent Calls **/
.recents-container .recents .recents-list div.calls-container .calls-list-container {
  background-color: #333;
  border-color: #333;
  }

/* recent calls list-item */
.recents-container .recents .recents-list div.calls-container .calls-list-container ul.calls-list li.recents-item {
  background-color: #222;
  }

/* Recent calls list-item :hover */
.recents-container .recents .recents-list div.calls-container .calls-list-container ul.calls-list li.recents-item:hover {
  background-color: #444;
  }

/* "All" Button */
.recents-container .recents .btn-group-wrapper .btn-group .recents-button {
  background-color: #e6e6e6;
  color: #e6e6e6;
  }

/* "Next" button */
.recents-container .recents .recents-list div.calls-container div.pager-info a {
  color: #e6e6e6;
  background-color: #777;
  border-color: #777;
  }
/* hover */
.recents-container .recents .recents-list div.calls-container div.pager-info a:hover, .recents-container .recents .recents-list div.calls-container div.pager-info a:focus {
  background-color: #222;
  color: #e6e6e6;
}

/** Phone (Calling) **/
/* Mute Button */
.media-container .flex-video .buttons .btn.unmuted {
  color: #e6e6e6;
  border-color: #777;
  }

.dialer .dropdown ul.dropdown-menu li.list-item.heading {
  background-color: #333 !important;
  }

/* Clear Calls */
.recents-container .recents .btn-group-wrapper .btn-group #clear-recents {
  color: #e6e6e6;
  background-color: #d9534f !important;
  }

/* Hangup */
body[data-controller="home"][data-action="phone"] .phone-container .phone .btn[data-action="cancel"], body[data-controller="home"][data-action="phone"] .phone-container .phone .btn[data-action="hangup"] {
  color: #e6e6e6;
  background-color: #e27c79 !important;
  }

/*  */
.dialer .dropdown ul.dropdown-menu li.list-item.list-item:hover, .dialer .dropdown ul.dropdown-menu li.list-item.selected {
  background-color: #444 !important;
  }

body[data-controller="home"][data-action="phone"] .phone-container .phone .body .toggle-bar .btn:hover {
  border-color: none;
  }

/* The dropdown-menu for the calls-list with all the standard numbers (i.e. Senators) */
.dialer .dropdown ul.dropdown-menu li.list-item.list-item:nth-child(2n+1) {
  border: none;
  }

/* Soundboard */
.soundboard-container .soundboard .soundboard-button-container li .soundboard-button {
  color: #e6e6e6 !important;
  background-color: #222 !important;
  }

/** Sign In **/
/* Sign In Button */
body[data-controller="sessions"][data-action="new"] .jumbotron-body a.btn, body[data-controller="sessions"][data-action="new"] .jumbotron-body button, body[data-controller="sessions"][data-action="new"] .jumbotron-body input.btn {
  background-color: #777;
  border-color: #777;
  }

.btn-default {
  color: #e6e6e6;
  background-color: #777;
  border-color: #777;
  }

/* Sign Up & Forgot Password */
body .container .inner-container .jumbotron .nav-links a {
  color: #e6e6e6;
  }

body .container .inner-container .jumbotron .nav-links a:hover, body .container .inner-container .jumbotron .nav-links a:focus {
  color: #e6e6e6;
  }

.nav > li > a:hover, .nav > li > a:focus {
  background-color: transparent;
}

/* Sign Up Button */
.btn-default:hover, .btn-default:focus, .btn-default.focus, .btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
  background-color: #222 !important;
  color: #e6e6e6;
}
/* Forgot Password Button */
body[data-controller="passwords"][data-action="new"] .jumbotron-body a.btn, body[data-controller="passwords"][data-action="new"] .jumbotron-body button, body[data-controller="passwords"][data-action="new"] .jumbotron-body input.btn {
  background-color: #777;
}

/** Dashboard **/
/* Phone Button */
body[data-controller="home"][data-action="dashboard"] .nav-container a.btn, body[data-controller="home"][data-action="dashboard"] .nav-container button, body[data-controller="home"][data-action="dashboard"] .nav-container input.btn {
  background-color: #777;
  color: #e6e6e6;
  border-color: #777;
  }

/* Phone Button (Hover) */
body[data-controller="home"][data-action="dashboard"] .nav-container a.btn:hover, body[data-controller="home"][data-action="dashboard"] .nav-container button:hover, body[data-controller="home"][data-action="dashboard"] .nav-container input.btn:hover {
  background-color: #222;
  }

/** Settings **/

body[data-controller="home"][data-action="settings"] .settings-container a.btn:hover, body[data-controller="home"][data-action="settings"] .settings-container button:hover, body[data-controller="home"][data-action="settings"] .settings-container input.btn:hover {
  background-color: #222 !important;
  color: #e6e6e6;
  }

body[data-controller="home"][data-action="settings"] .settings-container a.btn, body[data-controller="home"][data-action="settings"] .settings-container button, body[data-controller="home"][data-action="settings"] .settings-container input.btn {
  background-color: #777;
  border-color: #777;
  }
}
