/* 文章正文内容 */
.typo-title {
   font-weight: normal !important;
   line-height: 1.125 !important;
   word-wrap: break-word !important;
   font-size: 32px !important;
}

.typo-content {
   font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
   font-size: 14px !important;
   line-height: 1.5 !important;
   word-wrap: break-word !important;
}

.typo-content blockquote, .typo-content dl, .typo-content ol, .typo-content p, .typo-content pre, .typo-content table, .typo-content ul {
    margin-top: 0 !important;
    margin-bottom: 16px !important;
}

.typo-content hr {
    height: 0.25em !important;
    padding: 0 !important;
    margin: 24px 0 !important;
    background-color: #e1e4e8 !important;
    border: 0 !important;
}

.typo-content h1, .typo-content h2, .typo-content h3, .typo-content h4, .typo-content h5, .typo-content h6 {
   margin-top: 24px !important;
   margin-bottom: 16px !important;
   font-weight: 600 !important;
   line-height: 1.25 !important;
}

.typo-content h2 {
   padding-bottom: 0.3em !important;
   font-size: 1.5em !important;
   border-bottom: 1px solid #eaecef !important;
}

.typo-content a, .typo-content a:active {
   color: #3c9f4e !important;
}

.typo-content .highlight pre, .typo-content pre {
   padding: 16px !important;
   overflow: auto !important;
   font-size: 85% !important;
   line-height: 1.45 !important;
   background-color: #f6f8fa !important;
   border-radius: 3px !important;
   border: none !important;
}

.typo-bi .typo-content > h1:first-child,
.typo-bi .typo-content > h2:first-child,
.typo-bi .typo-content > h3:first-child,
.typo-bi .typo-content > h4:first-child,
.typo-bi .typo-content > h5:first-child,
.typo-bi .typo-content > h6:first-child {
	margin-top: 24px !important;
}

/**
 * GHColors theme by Avi Aryan (http://aviaryan.in)
 * Inspired by Github syntax coloring
 */

code[class*="language-"],
pre[class*="language-"] {
    color: #393A34 !important;
    font-family: "Consolas", "Bitstream Vera Sans Mono", "Courier New", Courier, monospace !important;
    direction: ltr !important;
    text-align: left !important;
    white-space: pre !important;
    word-spacing: normal !important;
    word-break: normal !important;
    font-size: 0.95em !important;
    line-height: 1.2em !important;

    -moz-tab-size: 4 !important;
    -o-tab-size: 4 !important;
    tab-size: 4 !important;

    -webkit-hyphens: none !important;
    -moz-hyphens: none !important;
    -ms-hyphens: none !important;
    hyphens: none !important;
}

pre[class*="language-"]::-moz-selection, pre[class*="language-"] ::-moz-selection,
code[class*="language-"]::-moz-selection, code[class*="language-"] ::-moz-selection {
    background: #b3d4fc !important;
}

pre[class*="language-"]::selection, pre[class*="language-"] ::selection,
code[class*="language-"]::selection, code[class*="language-"] ::selection {
    background: #b3d4fc !important;
}

/* Inline code */
:not(pre) > code[class*="language-"] {
    padding: .2em !important;
    padding-top: 1px !important;
	 padding-bottom: 1px !important;
    background: #f8f8f8 !important;
    border: 1px solid #dddddd !important;
}

.token.comment,
.token.prolog,
.token.doctype,
.token.cdata {
    color: #999988 !important;
    font-style: italic !important;
}

.token.namespace {
    opacity: .7 !important;
}

.token.string,
.token.attr-value {
    color: #e3116c !important;
}
.token.punctuation,
.token.operator {
    color: #393A34 !important; /* no highlight */
}

.token.entity,
.token.url,
.token.symbol,
.token.number,
.token.boolean,
.token.variable,
.token.constant,
.token.property,
.token.regex,
.token.inserted {
    color: #c89651 !important;
}

.token.atrule,
.token.keyword,
.token.attr-name,
.language-autohotkey .token.selector {
    color: #52b063 !important;
}

.token.function,
.token.deleted,
.language-autohotkey .token.tag {
    color: #9a050f !important;
}

.token.tag,
.token.selector,
.language-autohotkey .token.keyword {
    color: #00009f !important;
}

.token.important,
.token.function,
.token.bold {
    font-weight: bold !important;
}

.token.italic {
    font-style: italic !important;
}

.hljs {
    display: block;
    overflow-x: auto;
    color: #333333;
    padding: 0.5em
}

.hljs-comment,.hljs-quote {
    color: #999999
}

.hljs-variable,.hljs-template-variable,.hljs-tag,.hljs-name,.hljs-selector-id,.hljs-selector-class,.hljs-regexp,.hljs-deletion {
    color: #f2777a
}

.hljs-number,.hljs-built_in,.hljs-builtin-name,.hljs-literal,.hljs-type,.hljs-params,.hljs-meta,.hljs-link {
    color: #f99157
}

.hljs-attribute {
    color: #ffcc66
}

.hljs-string,.hljs-symbol,.hljs-bullet,.hljs-addition {
    color: #99cc99
}

.hljs-title,.hljs-section {
    color: #6699cc
}

.hljs-keyword,.hljs-selector-tag {
    color: #cc99cc
}

.hljs-emphasis {
    font-style: italic
}

.hljs-strong {
    font-weight: bold
}


/* 评论区优化 */

.doc-contents .doc-comment .ant-list-split .ant-list-item,
.comment-list.lark-list.ant-list .ant-list-item{
	padding: 10px 20px 15px;
   position: relative;
   border: 0 none;
}

.doc-contents .doc-comment .ant-list-split .ant-list-item:not(:first-child)::after,
.comment-list.lark-list.ant-list .ant-list-item:not(:first-child)::after{
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    display: block;
    margin: 0 20px;
    border-bottom-style: solid;
    border-bottom-color: #f6f6f6;
    border-bottom-width: 1px;
    content: "";
}

.comment-list.ant-list-split {
   border-left: 1px solid #ebebeb !important;
   border-right: 1px solid #ebebeb !important;
}

.doc-contents .doc-comment .ant-card {
   border-top: 1px solid #ebebeb !important;
}

.comment-list-item-inner {
	padding: 0;
}

.ant-card-head-title {
	font-size: 15px;
    font-weight: 600;
    color: #1a1a1a;
}

.doc-contents .doc-comment .ant-card-head, .ant-card.card-with-list .ant-card-head {
	padding: 0 20px;
   border-bottom: 1px solid #f6f6f6;
}

.comment-list-item .avatar, .comment-list-item .avatar img {
	width: 36px !important;
   height: 36px !important;
   border-radius: 18px !important;
}

.doc-contents .doc-comment .comment-header, .comment-list-item:first-child .comment-header {
display: flex!important;
align-items: center!important;
   padding-bottom: 15px;
   margin-top: 5px;
}

.doc-contents .doc-comment .comment-list-item-inner {
	padding: 0;
}

.comment-list-item .avatar {
    flex: 0 0 auto;
   margin-right: 0;
}

.comment-list-item .commenter {
    flex: 1 1 auto;
}

.comment-list-item .commenter .time {
	display: block;
}

.comment-list-item .content {
	padding-left: 0;
}

.typo-content {
	font-size: 15px !important;
   line-height: 1.67 !important;
   -webkit-tap-highlight-color: rgba(26,26,26,0) !important;
}

