css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(twitter.com) {
  .MomentsPage.MomentsPermalinkPage .MomentsPermalinkPage-content, .MomentsPage.MomentsGuidePage .MomentsGuidePage-content {
    margin: 0 auto;
    padding-left: 10px;
    padding-right: 10px;
    width: 500px;
  }
  .MomentsPermalinkPage-tweets,.MomentCapsuleCover,.MomentMakerCapsuleCover,.MomentMakerPage-existingTweets{width:500px;}
  .MomentMakerRecommendations-module{width:520px;}
  .MomentMakerPage-recommendedTweets{margin:0px auto;width:420px;}
  
  /*サムネを小さい画面に合わせる*/
  .MomentCapsuleCover .MomentCapsuleCover-media,
  .MomentCapsuleCover .MomentMediaItem {
    width: 480px!important;
    height: auto!important;
    padding-bottom:auto;
    background-color:transparent;
  }
  .MomentCapsuleCover .MomentMediaItem img{
    width:470px!important;height:auto!important;padding:5px;border-radius:10px;}
  .MomentCapsuleCover .MomentMediaItem img{
    width:470px!important;height:auto!important;margin:0px!important;border-radius:10px;}
  .MomentCapsuleItem{
    border-bottom:1px solid #cccccc;padding-bottom:20px;margin-bottom:30px;}
  
  /*編集画面(トップ)*/
  .MomentMakerCapsuleCover .MomentCapsuleCover-media,
  .MomentMakerCapsuleCover .MomentMediaItem {
    width: 480px!important;
    height: auto!important;
    padding-bottom:auto;
    background-color:transparent;
  }
  .MomentMakerCapsuleCover .MomentMediaItem img{
    width:470px!important;height:auto!important;padding:5px;border-radius:10px;}
  .MomentMakerCapsuleCover .MomentMediaItem img{
    width:470px!important;height:auto!important;margin:0px!important;border-radius:10px;}
  .MomentMakerCapsuleCover{
    border-bottom:1px solid #cccccc;padding-bottom:20px;margin-bottom:30px;}
  
  /*ツイート編集画面*/
  .MomentMakerMediaContainer-mediaContainer{width:220px;}
  .MomentMakerPage-existingTweets .MomentCapsuleCover-media,
  .MomentMakerPage-existingTweets .MomentMediaItem {
    width: 220px!important;
    height: auto!important;
    padding-bottom:auto;
    background-color:transparent;
  }
  .MomentMakerPage-existingTweets .MomentMediaItem img{
    width:210px!important;height:auto!important;padding:5px;border-radius:10px;}
  .MomentMakerPage-existingTweets .MomentMediaItem img{
    width:210px!important;height:auto!important;margin:0px!important;border-radius:10px;}
  .MomentMakerPage-existingTweets{
    border-bottom:1px solid #cccccc;padding-bottom:20px;margin-bottom:30px;}
  
  /*ツイート追加*/
  .MomentMakerPage-recommendedTweets .MomentCapsuleCover-media,
  .MomentMakerPage-recommendedTweets .MomentMediaItem {
    width: 220px!important;
    height: auto!important;
    padding-bottom:auto;
    background-color:transparent;
  }
  .MomentMakerPage-recommendedTweets .MomentMediaItem img{
    width:210px!important;height:auto!important;padding:5px;border-radius:10px;}
  .MomentMakerPage-recommendedTweets .MomentMediaItem img{
    width:210px!important;height:auto!important;margin:0px!important;border-radius:10px;}
  .MomentMakerPage-recommendedTweets{
    border-bottom:1px solid #cccccc;padding-bottom:20px;margin-bottom:30px;}
  
  /*編集用ツールバー*/
  .u-bgUserColor, .u-bgUserColorHover:hover, .u-bgUserColorHover:focus {
    background-color: #2C495C!important;
}

  /*左メニューを上に*/
  .MomentFloatingMenu{position:static;width:500px;z-index:auto;opacity:1;visibility:visible!important;}
  .MomentFloatingMenu.is-fixed{transform:none;margin:10px 0px;}
  .MomentButton{width:auto!important;display:inline-block!important;font-size:12px;padding:3px 5px;}
  

  
}
