css:
/* --- yaaay transparent likes container --- */
.rapid-recs-container .rapid-recs {
    background-image: linear-gradient(180deg,rgba(255,255,255,0),rgba(255,255,255, 0),rgba(255,255,255,0));
}

/* --- this makes the title opaque --- */
.rapid-recs-container .rapid-recs .rapid-rec-title {
    color: rgba(255,255,255, 1.0);
}

/* --- drop shadow --- */
.post_micro .post_content {
    box-shadow: 5px 5px rgba(0,0,0,0.1);
}

/* i really want this to be global */
