css:
@-moz-document domain("www.thegharstation.com") {
.wide-layout #header {
    margin: 0px;
    padding-top: 0px;
}
body {
    background: url("https://steamcdn-a.akamaihd.net/steam/apps/39120/ss_e823832a3eb7ccf9afb61a86a4bd9460c57b906c.1920x1080.jpg");
    /*   background-position: 0px -160px;*/
    
    background-attachment: fixed;
}
#header-wrap {
    background-color: transparent;
    background-color: #111111BB;
}
#header {
    height: 160px;
    width: 100%;
    position: relative;
    max-width: 100%;
}
#header img {
    display: none;
}
#logo {
    position: absolute;
    bottom: 10px;
}
#logo .site-description {
    color: white;
    text-shadow: -2px 2px 4px black;
}
#header:before {
    content: "The Ghar Station";
    font-family: "FlareSerif";
    color: white;
    display: block;
    position: absolute;
    bottom: 30%;
    width: 100%;
    text-align: center;
    text-shadow: -2px 2px 4px black;
    font-size: 5em;
}
article {
    border-radius: 25px;
}
article.type-post {
    padding-top: 0.5em;
    padding-bottom: 0.5em;
}
.type-post {
    margin-bottom: 0.5em;
}
aside {
    border-radius: 25px;
}
aside.widget {
    padding-top: 1em;
    padding-bottom: 0.5em;
    margin-bottom: 0.5em;
}
h2.entry-title.post-title {
    padding: 0px;
}
article .wp-post-image {
    padding: 1em;
    margin: 0px;
}
.main-navigation-menu a {
    padding: 0.5em;
}
.type-post {
    padding: 1em;
}
div.entry-meta.postmeta {
    padding: 0.2em;
}
div.postinfo {
    font-size: 0.7em;
    border-radius: 15px;
    transition: font-size 1s;
    float: right;
    position: relative;
    top: -3em;
}
a.more-link {
    padding: 5px;
    margin: 5px;
    font-size: 0.7em;
    border-radius: 10px;
    transition: font-size 0.2s;
    float: left;
}
a.more-link:hover {
    font-size: 1em;
    box-shadow: 0 12px 16px 0 rgba(0, 0, 0, 0.24), 0 17px 50px 0 rgba(0, 0, 0, 0.19);
}
div.entry {
    margin-bottom: 0px;
    margin-top: 0px;
}
div.entry p {
    margin-bottom: 0.5em;
}
article>a {
    display: block;
    float: left;
}
article>a img {
    display: block;
    width: 200px;
}
}
