css:
@-moz-document domain("utkonos.ru") {
/* @import url('https://fonts.googleapis.com/css?family=Jura:300,400,700&subset=cyrillic'); */
/* @import url('https://fonts.googleapis.com/css?family=Yanone+Kaffeesatz:300,400,700&subset=cyrillic'); */
/* @import url('https://fonts.googleapis.com/css?family=Cousine:400,400i,700,700i&subset=cyrillic'); */
/****************************************/
/*	  Name: Utkonos (Spring)	*/
/*	- Recolored by ReColManiac -	*/
/*	  (License CC BY-NC-SA)		*/
/****************************************/
html, body {
	font-size: large;
}
body, .navigate_left, .navigate_left .catalogue_filters label, label {
	font: normal large "Yanone Kaffeesatz", Cousine, "Roboto Condensed", "Ubuntu Condensed", Tahoma;
}
body > [rel="main"] > article, [style*="background-color: white"], [style*="background: white"],
p > span[style$="background:white"] {
	background-color: #BCDF9A !important;
}
.module_floating_header, .theme_page_bg {background-color: #83cb25 !important}
.decor_header_articles {
	font-family: "Yanone Kaffeesatz", Cousine, "Roboto Condensed", "Ubuntu Condensed", Tahoma;
}
.module_infoblock_bottom, .module_infoblock_bottom header, .color_dark, .color_dark a, .color_dark a:visited {
	background-color: #222;
	color: #BCDF9A;
	text-decoration: none;
}
.color_dark a:hover, .color_dark a:active {
	color: #D6DF9A;
	text-decoration: underline;
}
.goods_view_box-caption {
	font-size: large;
	}
i.signature {
	font-weight: bold;
	color: #DFCE9A;
}
.module_infoblock_promo-pic {
	filter: opacity(55%);
	transition: all 1500ms;
}
.module_infoblock_promo-pic:hover {
	filter: opacity(100%);
}
.module_floating_header-equal_search-list a {
	filter: brightness(150%) !important;
}

/* new site feedback */
.module_site_migration_return {
	margin-left: -510px;
}
.module_site_migration_return-label_feedback {
	background-color: rgba(234,252,209,0.2);
	border-radius: 8px;
	border: 1px dashed rgb(168,224,94);
	transition: linear .8s 2.5s;
	color: rgba(168,168,168,0.4);
	box-shadow: 2px 2px 4px rgba(0,0,0,0.1);
}
.module_site_migration_return-label_feedback:hover {
	background-color: rgba(131,203,37,0.8);
	border-radius: 8px;
	border: 1px dashed rgba(131,203,37,0.1);
	transition: linear .8s .5s;
	box-shadow: 2px 2px 4px rgba(0,0,0,0.5);
}
.module_site_migration_return-label_back {
	background-color: rgba(255,255,255,0.2);
	border-radius: 8px;
	border: 1px dashed rgb(168,168,168);
	transition: linear .8s 2.5s;
	color: rgba(168,168,168,0.4);
	box-shadow: 2px 2px 4px rgba(0,0,0,0.1);
}
.module_site_migration_return-label_back:hover {
	background-color: rgb(196,196,196,0.8);
	border-radius: 8px;
	border: 1px dashed rgba(128,128,128,0.1);
	transition: linear .8s .5s;
	box-shadow: 2px 2px 4px rgba(0,0,0,0.5);
}
.module_floating_header-nav {
	background-color: rgba(109, 156, 46, 0.68);
}
.module_scroll_top {
	background-color: rgba(109, 156, 46, 0.8);
	border-radius: 6px;
	border: 1px dashed rgb(168,224,94);
}
.page_basket_control-next {
	font: bold 16px/2.5 Jura, 'DejaVu Sans Mono', mono;
	background: linear-gradient(to bottom, rgba(255,125,25,1) 0%,rgba(255,125,25,.5) 38%,rgba(255,125,25,1) 100%);
	border-radius: 8px;
	box-shadow: 2px 2px 4px #888888;
}
.page_basket_control-next:active, .page_basket_control-next:hover {
	border-radius: 8px;
	box-shadow: none;
}
.module_promocode-action-btn {
	font-size: 10pt;
	font-family: Jura, 'DejaVu Sans mono', mono;
}

/* Buttons Add Reduce */
.basket_goods_line-quantity_increase, .basket_goods_line-quantity_reduce {
	border-radius: 6px;
	margin-left: 2px;
	margin-right: 2px;
}
.basket_goods_line-quantity_reduce:hover {
	box-shadow: 0 0 2px red;
}
.basket_goods_line-quantity_increase:hover {
	box-shadow: 0 0 2px green;
}
}
