css:
@-moz-document domain("myprojectfree.tv"), domain("myprojectfreetv.net"), domain("my-project-free.tv"), domain("www1.project-free-tv.ag"), domain("www0.project-free-tv.ag"), domain("www3.project-free-tv.ag") {
/* ===================== */
/*   MAKE DISAPPEAR !!   */

iframe,
.shareit,
#myButton3,
.box input.myButton2,
#blog > center:nth-child(2) > a:nth-child(1) > img:nth-child(1),
.myButton,
.thecontent > div:nth-child(2) {
	
	display: none !important;
}

div#sidebar-meta.widget center a img {
	display: none;
}

/*exceptions*/

.video-container iframe {
	display: inherit !important;
}

/*   END ~ MAKE DISAPPEAR   */
/* ======================== */

body {
	color: #999;
}

body center div {
	background-color: #000 !important;
}

.menu4 {
	background-color: #323232 !important;
	box-shadow: 0px 5px 30px rgba(0, 0, 0, .3);
}

body,
.main-container {
	background: rgb(42, 9, 39) !important;
}

#page,
.single_post {
	background-color: #000 !important;
}

#page {
	box-shadow: 0px 40px 80px -25px #94218A;
}

.single_post .single-title, .title, .entry-title,.single_page header h1 {
	color: #DD8500 !important;
}


table.alternate_color tr:nth-of-type(2n+1) {
	background-color: #000 !important;
}

table.alternate_color tr:nth-of-type(2n) {
	background-color: #323232 !important;
}

tbody th,
tbody td,
.navlinks {
	border: 0px solid #4d4d4d !important;
}

tbody th,
td {
	border: 2px solid #3e3c3c !important;
}

tbody th {
	background-color: #DD8500;
	color: #000;
	margin-bottom: 10px !important;
	border-bottom: 5px solid #000 !important;
}

table,
table tr {
	border: 0px solid #f1f4f6 !important;
}

table tr {
	background-color: #333
}

.content a:link,
td a:link {
	color: #d7872e !important;
	text-decoration: none;
}

.navlinks {
	overflow: inherit !important;
	margin-bottom: 60px;
}

#blog .button a {
	background-color: #0000;
	padding: 0;
	margin: 0
}

.navlinks span.button a:visited {
	color: #aaa !important;
}

.navlinks span.button {
	padding: 13px 22px;
}

.navlinks span a:link {
	color: #fff !important;
}

.navlinks span.button {
	background: #fa35ea;
	background: -moz-linear-gradient(top, #fa35ea 0%, #94218a 100%);
	background: -webkit-gradient(left top, left bottom, color-stop(0%, #fa35ea), color-stop(100%, #94218a));
	background: -webkit-linear-gradient(top, #fa35ea 0%, #94218a 100%);
	background: -o-linear-gradient(top, #fa35ea 0%, #94218a 100%);
	background: -ms-linear-gradient(top, #fa35ea 0%, #94218a 100%);
	background: linear-gradient(to bottom, #fa35ea 0%, #94218a 100%);
	/* background: #fa35ea;
background: -moz-linear-gradient(top, #fa35ea 0%, #94218a 10%, #94218a 100%);
background: -webkit-gradient(left top, left bottom, color-stop(0%, #fa35ea), color-stop(10%, #94218a), color-stop(100%, #94218a));
background: -webkit-linear-gradient(top, #fa35ea 0%, #94218a 10%, #94218a 100%);
background: -o-linear-gradient(top, #fa35ea 0%, #94218a 10%, #94218a 100%);
background: -ms-linear-gradient(top, #fa35ea 0%, #94218a 10%, #94218a 100%);
background: linear-gradient(to bottom, #fa35ea 0%, #94218a 10%, #94218a 100%);
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#fa35ea', endColorstr='#94218a', GradientType=0 ); */
	
	box-shadow: 0 0 45px -10px #0000;
	transition: all 1s ease-in-out 0ms;
}

.navlinks span.button:hover {
	box-shadow: 0 0 45px -10px #fffc;
	transition: all 300ms ease-in-out;
}

header h1{
	padding: 3% !important;
	width: 95% !important;
	font-style: italic
}
header h1:after{
	width:100%;
	height: 3px;
	content: ' ';
	background-color: #94218a;
	position: absolute;
	top:100%;
	left:0;
	border-radius: 50%;
	box-shadow: 0 0 10px 1px #94218a !important;
	
}

header span a:link,
header span a:visited {
	color: #d7872e !important;
}

.menu3 {
	background-color: #94218a;
	width: 73%;
	text-align: center;
	color: #ffffff;
	font-size: 18px;
	height: 50px;
	margin: -15px 13.5% 0 13.5%;
	line-height: 30pt;
	border-radius: 10px 10px 0 0;
}

.sidebar input#navbar_search,
.sidebar input#navbar_search2,
.sidebar textarea,
.sidebar select {
	background-color: #333 !important;
	border: 0 !important;
}

#navbar_submit {
	background-color: #fc6100;
	border: 0px;
}

#blog > center:nth-child(2) > a:nth-child(1)::before {
	content: 'Project Free TV';
	font-size: 28pt;
	font-family: 'Arial', sans-serif;
	line-height: 70pt;
}

.main-container > div:nth-child(2) {
	background-color: #333 !important;
}

.breadcrumb > div:nth-child(1) {
	background-color: #0000 !important;
}
}
