css:
@-moz-document regexp('.*wikia\.com.*') {
body,
body.background-dynamic,
body.background-dynamic:before,
body.background-dynamic:after {
    background: url("https://www.toptal.com/designers/subtlepatterns/patterns/cream_pixels.png") !important;
    background-image: url("https://www.toptal.com/designers/subtlepatterns/patterns/cream_pixels.png") !important;
}
.WikiaPage .WikiaPageBackground {
    background: #fff;
    opacity: 1;
}
header.wds-community-header {
    background-image: none !important;
}
.wds-community-header {
    margin-top: 20px !important;
}
.WikiaSiteWrapper {
    background: none !important;
    background-color: transparent !important;
    background-image: none !important;
}
#ad-skin,
#WikiaTopAds,
.WikiaTopAds,
.wikia-ad,
#WikiaAdInContentPlaceHolder {
    display: none;
}
.wds-global-footer__main {
    display: none;
}
/* FANDOM Fan Feed */

#mixed-content-footer {
    display: none;
}
/* FANDOM Fixed Bottom Bar */

#WikiaBar {
    display: none
}
/* FANDOM Discord Ad */

#rsw-discord {
    display: none;
}
/* Trending FANDOM Articles */

#recirculation-rail {
    display: none;
}
body.wikia-bar-visible {
    padding-bottom: inherit;
}
/* FANDOM Navigation Header */

.wds-global-navigation-wrapper .wds-global-navigation__links-and-search > *:not(form) {
    display: none;
}
.wds-global-navigation-wrapper .wds-global-navigation__account-menu,
.wds-global-navigation-wrapper .wds-global-navigation__start-a-wiki {
    display: none;
}
}
