css:
@-moz-document domain("electromine.fr") {
body, .table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
    background-color: #000;
    color: #ddd;
}

footer {
    background-color: #091B26;
}

.form-control {
    background-color: #111;
    border: 1px solid #444;
    color: #999;
}

.input-group-addon, .btn-default, button {
    color: #ddd;
    background-color: #333;
    border: 1px solid #444;
}

.yourStats span, .stats span {
	color: #0FA286;    
}

.fa {
    color: #1F94B1;
}

.btn-primary {
    background-color: #0f3a44;
    border-color: #0c4754;
}

.alert-info {
    background-color: #0c1f29;
    border-color: #0f3042;
    color: #668c9e;
}

.alert-danger {
	background: #111;    
}

.alert-success {
    background-color: #0d0e0c;
    border-color: #161716;
    color: #3c763d;
}

.alert-warning {
    color: #b98e12;
    background-color: #330606;
    border-color: #3c0707;
}

.btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open .dropdown-toggle.btn-default {
    color: #ddd;
    background-color: #0f3a44;
    border-color: #0f3a44;
}

.btn:hover, .btn:focus {
    color: #ddd;
    text-decoration: none;
}

.form-control:focus {
    border-color: #0f3a44;
    -webkit-box-shadow: none;
    box-shadow: none;
}

.table>thead>tr>th {
 	border-bottom: 1px solid #222; 
}
  
hr, .table>thead>tr>th, 
.table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, 
.table>tbody>tr>td, .table>tfoot>tr>td {
    border-top: 1px solid #222;
}

.box, .widget {
    background: #111;
}

.site-menubar {
    background: #222;
    border-bottom: 1px solid #333;
}

.table-bordered th, .table-bordered td {
    border: 1px solid #222;
}

body.menubar-top .site-menu > li:hover, body.menubar-top .site-menu li:hover > .submenu {
    background: #2b2d33;
}

.site-menu .menu-text {
    cursor: pointer;
}

.bg-indigo-500 {
    background-color: #0a1a27 !important;
}
}
