body {
    font-family: "Roboto", sans-serif;
    font-size: 14px;
    line-height: 1.6;
    color: #acacac;
    background-color: #f5f8fa;
}
.form-control {
    display: block;
    width: 100%;
    height: 36px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.6;
    color: #acacac;
    background-color: #191919;
    background-image: none;
    border: 1px solid #f5f8fa;
    border-radius: 4px;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
    transition: border-color ease-in-out 0.15s, box-shadow ease-in-out 0.15s;
}
.breadcrumb:before {
    content: "AVNocturne v1";
    color: #888;
}
/* Ce qui concerne création de topic ou poster un message */
.bbcodes {
    text-align: left;
    margin: 0;
    border: 1px solid #646667;
    background: #1f1f1f;
}
hr {
    margin-top: 22px;
    margin-bottom: 22px;
    border: 0;
    border-top: 1px solid #1f1f1f;
}
.forum-tools .btn {
    background: #1f1f1f;
    border: 1px solid #646667;
    color: #acacac;
    text-transform: uppercase;
    font-size: 0.9em;
}
.form-control {
    display: block;
    width: 100%;
    height: 36px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.6;
    color: #acacac;
    background-color: #1f1f1f;
    background-image: none;
    border: 1px solid #646667;
    border-radius: 4px;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
    transition: border-color ease-in-out 0.15s, box-shadow ease-in-out 0.15s;
}
.pagination > li > a, .pagination > li > span {
    position: relative;
    float: left;
    padding: 6px 12px;
    line-height: 1.6;
    text-decoration: none;
    color: #455863;
    background-color: #1f1f1f;
    border: 1px solid #646667;
    margin-left: -1px;
}
.table > thead > tr > th {
    vertical-align: bottom;
    border-bottom: 2px solid #646667;
}
/* Concerne le chat */
.chat-box .chat-message .chat-message-username a {
    color: #acacac;
}
.chat-box .chat-message {
    color: #676767;
    overflow: hidden;
}
.chat-form {
    background: #1f1f1f;
    padding: 20px;
}
.chat-box {
    height: 500px;
    overflow: auto;
    padding: 0;
    list-style: none;
    font-size: 14px;
    word-wrap: break-word;
}
/* Profile */
.profile .profile-title, .profile .profile-wrapper, .profile .profile-footer, .profile .profile-biography {
    background: #1f1f1f;
    padding: 15px;
    border-radius: 5px;
}
.profile .profile-title {
    color: #acacac;
    text-align: center;
    margin: 0 0 15px 0;
}
/* Barre de navigation */
.navbar-inverse .navbar-nav > li > a {
    color: #acacac;
}
.navbar-inverse .navbar-nav > .active > a, .navbar-inverse .navbar-nav > .active > a:hover, .navbar-inverse .navbar-nav > .active > a:focus {
    color: #acacac;
    background-color: #080808;
}
.navbar-inverse {
    background-color: #1f1f1f;
}
.navbar li.active a {
    background: #1f1f1f !important;
}
body div#navbar {
    background-color: #1f1f1f!important;
}
/* smiley */
.fancybox-skin {
    position: relative;
    background: #1f1f1f;
    color: #acacac;
    text-shadow: none;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
}
.footer {
    border-top: 4px solid #B71C1C;
    position: absolute;
    bottom: 0;
    width: 100%;
    padding: 10px;
    font-size: 0.8em;
    background: #1f1f1f;
    text-align: center;
}

/* citations */

.message-content-quote { 

  color: #459359;
  margin-top: 10px;
  margin-bottom: 10px;
  margin-left: 20px;
  padding-left: 15px;
  border-left: 3px solid #ccc;
  background-color: #1a1a1a;
}

i { 
    font-style: italic;
    background-color: #1a1a1a;
    
}

/* cloche chiante */
.onesignal-bell-svg{visibility: hidden}
.onesignal-bell-launcher-button{visibility: hidden}
.onesignal-bell-launcher-badge{visibility: hidden}
.onesignal-bell-launcher-dialog{visibility: hidden}
.onesignal-bell-launcher-dialog-body{visibility: hidden}
.onesignal-bell-launcher-message{visibility: hidden}
.onesignal-bell-launcher-message-opened{visibility: hidden}

/* Bloc Avatar */

.message-aside {
  width: 120px;
  -webkit-box-flex: 1;
  flex: 1;
}

.message-infos {
  font-size: 12px;
}

/* VERSION MOBILE */

.bottombar {
 opacity: 0.4;
     background-color: transparent;
   filter: grayscale(100%) contrast(30%) opacity(20%) ;
}
 }
