css:
@-moz-document domain("forum.ru-board.com") {
body {
	margin: 0;
}

small {
	font-size: .7rem;
}

br + br {
	font-size: 30%;
}

/* hide */
body > table:nth-of-type(1)[bgcolor="#FFFFFF"],
.post > br:first-child,
.sing,
table[width="80%"][cellspacing="0"][cellpadding="3"][align="center"],
_ {
	display: none;
}

/* title */
.tit {
	padding: 0;
}
.sh1 {
	text-align: left;
	padding: 0;
}
.sh1 > br {
	display: none !important;
}
.sh1 > br + a:before {
	content: " â€¢ ";
}
script + table[width="95%"][align="center"][bgcolor="#999999"] + br {
	display: none;
}
script + table[width="95%"][align="center"][bgcolor="#999999"] {
	width: 100%;
	padding: 10px 20px;
	background: #eee !important;
	margin: 5px 0 9px 0;
}

/* name */
.tb table tr td.dats:first-child br+br,
.tb table tr td.dats:first-child a+br,
_ {
  display: none !important;
}
.tb table tr td.dats:first-child {
  font-size: .6rem;
	color: #bbb;
}
.tb table tr td.dats:first-child b {
  font-weight: normal;
	padding-bottom: 0 !important;
}
.tb table tr td.dats:first-child a {
 	display: inline-block;
  clear: both !important;
	margin-right: 3px;
}
.tb table tr td.dats:first-child img {
  position: absolute;
	display: table !important;
	margin-top: 15px;
	margin-left: -2px;
}

/*  */
td.dats[align="right"] {
	padding: 5px 0 !important;
}
.dats[align="right"] {
	text-align: left
}
.dats + .tpc {
	position: relative;
	top: -16px;
}
.dats {
	width: 150px;
}
.dats + .tpc {
	width: initial;
}
tr [align="center"] {
	text-align: left;
}

/* post */
hr,
table,
td,
tr,
.tb {
	border: none;
	background-color: transparent !important;
	margin-bottom: 0;
	margin-top: 0;
}
.post {
	font-family: Georgia;
	font-size: 13pt;
	line-height: 1.3;
	letter-spacing: 0;
}

/* cite */
.post {
	width: 100%;
}
._post > small,
.post > b > small {
	color: #444;
	font-family: "Open Sans", sans-serif;
	font-size: 7pt;
	font-weight: 100;
	letter-spacing: .3;
	position: relative;
	top: 3px;
	left: 0;
	background: #eee;
	padding: 0 4px;
}
_br + small {
	display: block;
	width: 4em;
}
small + table[width="75%"][bordercolor="#EEEEEE"],
table[width="75%"][bordercolor="#EEEEEE"] td.lgf,
_ {
	font-family: Georgia;
	font-size: 11pt;
	line-height: 1.3;
	letter-spacing: 0;
	background: #eee !important;
	display: block;
}
small + table[width="75%"][bordercolor="#EEEEEE"] {
	padding: 5px 10px;
}

/* post tech info */
.bottomline *,
.tpc a.tpc:link,
.tpc {
	color: transparent;
	font-weight: normal !important;
	font-family: "Open Sans", sans-serif;
	font-size: 7pt;
	letter-spacing: .03;
	text-decoration: none;
}
.tb {
	_border-top: solid 1px #0001;
}
.tb table tr:last-child {
	display: table-header-group;
}
.tb table tr:first-child {
	display: table-row-group;
}
.bottomline {
	padding: 0 5px;
}
.bottomline > .tpc {
	visibility: hidden;
}
.bottomline > .tpc:hover {
	visibility: visible;
}
.bottomline > .tpc > b:last-child,
.bottomline > .tpc > b:last-child > a.tpc {
	color: #aaa !important;
	display: block;
	visibility: visible !important;
}
.bottomline:hover *,
.tpc:hover,
.tpc:hover a.tpc:link {
	color: #aaa;
}
a:hover {
	text-decoration: underline !important;
}

/* paging */
td.small {
	line-height: 1.6;
}
td.small > a {
	display: inline-block;
	_border: solid 1px #efefef;
	padding: 2px 2px;
	margin: 2px;
}
}

@-moz-document regexp("^http://forum\\.ru-board\\.com/(?!(profile|messanger)).*?$") {
table[width="95%"][align="center"][bgcolor="#999999"] + br + table tr:nth-child(1) td {
	display: block;
	width: auto;
}
table[width="95%"][align="center"][bgcolor="#999999"] + br + table tr:nth-child(2) td {
	display: block;
	width: auto;
	text-align: right;
	position: relative;
	top: -1.9rem;
}
}

@-moz-document regexp("^http://forum\\.ru-board\\.com/(profile|messanger).*?$") {
table {
	width: auto;
}

.dats td {
	text-align: left;
}
}
