    body, .container, .panel-rt.dark, .sleaderboard_wrapper, .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus, #rating_widget_desktop .rating_widget_desktop-header span, .post.original, #rating_widget_mobile, .navbar.navbar-rt, #foot, .home-body #videoPanel, .mop-audience-reviews__no-featured, .mop-audience-reviews__no-reviews
  { background-color: var(--bg) }
