  { background-color: var(--bg-odd) }
    input[type="text"], input[type="password"], select, textarea, .form-control, .rating_textbox .rating_textbox-textarea, .textarea-wrapper, .input--text, .rating_textbox-textarea
  { background-color: var(--bg-input); color: var(--fg-input) }
