    #movies_sidebar .nav-tabs > li.active, #movies_sidebar .nav-tabs, .dropdown-menu li > a:hover, a:focus, a:active, .FullscreenSearch__top-section-container, .userDropdown.dropdown .dropdown-menu.roundMenu .userStats:hover, .panel-rt.bannerCaption, li.allTab.active span:hover, .mop-ratings-wrap__title, .container.body_main
  { background-color: transparent }
    body, .navbar, input[type="text"], select, textarea, .form-control, #whatIsTomatometer .title, .rating_textbox-textarea
  { background-image: none }
    .progress, .padding-note, .comment-policy, #quotesList .review_quote
  { background-color: rgba(255, 255, 255, 0.2) }
    .progress-bar.fresh
  { background-color: #f33 }

    .textarea
  { color: var(--fg-input) }
