    .FullscreenSearch--desktop .FullscreenSearch__result-text-line-1, #showtimes .showtimesContainer .showtimes-presentations .presentation .presentation-name, .tab-conversation.active > a, .dropdown-toggle, .cfpItem p, .panel-heading, #rating_widget_desktop h3, .FullscreenSearch--desktop .FullscreenSearch__result-text-line-1, .FullscreenSearch--desktop .FullscreenSearch__result-text-line-2
  { color: var(--fg) !important }
    .brand, h2.title, .trendingBar .header, .freshPicks .cfpItem a, #movies_sidebar .tab-content h3, .mb-movies.list-view .mb-movie .movie_info .other_info, .vote-up .count, #listColumn .list-title, .userDropdown.dropdown .dropdown-menu.roundMenu .userStats .name, .score_panel .audience-panel .scoreTitle, .score_panel .tmeter-panel .scoreTitle, h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6, .media-browser-left h3, .score-modal__icon-wrap, .mop-ratings-wrap__title, .user-media-rating__stars-header, .mop-audience-reviews__review--name
