  { color: var(--fg-link) }
    .articleContentBody .countdown-item-content a, a[style*="color:#0C89CE"], #movieMenu:hover, #tvMenu:hover, #newsMenu:hover, .panel-link a, .showAll a, .col-left-center .panel-link a
  { color: var(--fg-link) !important }
    a:hover, a:visited:hover, .navbar.navbar-rt #menu .menuHeader .dropdown-menu .subnav li a:hover, .navbar.navbar-rt .navbar-header .header_links > :hover, .trendingLink:hover, .trendingLink:hover:before, a.articleLink:hover, a.articleLink:hover *:before, .movie_list tr:hover > td > *, .movie_list tr:hover > th > *, .movie_list tr:hover > td .tMeterIcon, .movie_list tr:hover > th .tMeterIcon, #foot a:hover, #movies_sidebar .nav-tabs > li:hover > a, .navbar.navbar-rt #menu #header-news-guides .clickForMore a:hover, .navbar.navbar-rt #menu #header-news-best-worst .clickForMore a:hover, .navbar.navbar-rt #menu #header-news-rtnews .clickForMore a:hover, .navbar.navbar-rt #menu #tvMenuDropdown .clickForMore a:hover, #browser-navigator .navigator-menu li a:hover, .nav-tab > a:hover, .comment__footer a:hover, .comment__footer .vote-down:hover, .install a:hover, .email a:hover, .privacy a:hover, .comment__header a:hover, .hub-subnav-bar a:hover, .carousel .items .item:hover:not(.active), #listColumn label:hover, .userDropdown.dropdown .dropdown-menu li.header-top-bar-item a, .userDropdown.dropdown .dropdown-menu.roundMenu .userStats .stat:hover, .userDropdown.dropdown .dropdown-menu.roundMenu .userStats .name:hover, #criticHeaders a:hover, .clamp-toggle--show-more:hover
  { color: var(--fg-link-hover) }
    a.articleLink:hover *, .articleContentBody .countdown-item-content a:hover, .label:hover, .dropdown-toggle:hover, .icon-export:hover, a.articleLink:hover, a:hover, .panel-link a:hover, .col-left-center .panel-link a:hover
  { color: var(--fg-link-hover) !important }
    a:visited
  { color: var(--fg-link-visited) }
    .quote_bubble .superreviewer
  { color: #3f3 }

    noborder, .navbar, .navbar.navbar-rt, #main_movie_sidebar_content, .nav-tabs, .nav-tabs > li > a:hover, #FranchiseList .sort .btn, #topbar-container .topbar, #browser-navigator .navigator-menu, #tomatometer-info, .btn-secondary-rt, .countdown-item, .panel-rt.panel-box, .panel-rt.panel.panel-box, .carousel .items .item, .carousel .items, #listColumn .panel, .theater-heading, #mainColumn .showtimes-box, .movie-showtimes-item .row.movie-details-row, .movie-details-row img, table, .btn-primary-border-dropdown .dropdown-menu li, .table > tbody > tr > td, .table > thead > tr > th, .movie_list tr, #FranchiseList .filter .btn, #rating_widget_mobile, .mobile_rating_btn.mobile_rating_btn-ni, .mobile_rating_btn, .mobile_rating_btn.mobile_rating_btn-wts, .movie_list tr:last-of-type
  { border: none }
    custom-border
  { border: var(--border) }
    input, select, textarea, .navbar.navbar-rt .navbar-searchbox .input-group, #miniMOB .rightColumn, .panel-rt, .panel-rt.panel.panel-box,#movies_sidebar .nav-tabs > li:not(:first-of-type), .score_panel .tmeter-panel, #rating_widget_desktop .rating_widget_desktop-header, .rating_textbox .rating_textbox-textarea, .franchiseLink, #FranchiseList .franchiseItemContainer .franchiseItem, #FranchiseList .franchiseItemContainer .franchiseItem:first-of-type, .panel-rt.panel-box, hr, div.article_body hr, #count-link, #topbar-container .topbar .dropdown-menu, .mb-movies.list-view .mb-movie, .nav-primary, .textarea-wrapper, .post-actions, .input--text, .vote-down, .post-content .post-menu .dropdown-toggle, .disqus-footer__wrapper, .modal-header, #signup .modal-dialog .modal-footer, #login .modal-dialog .modal-footer, .post, table, .comments_quote blockquote, #showtimes .showtimesContainer, .review_table .review_table_row, .bottom_divider, .form-control, .userDropdown.dropdown .dropdown-menu.roundMenu .userStats, #filterList li span:not(.active), .mop-ratings-wrap__more-info, .rating_textbox-textarea, #watch-it-now .movie_links .affiliates .logo div.amazon, #watch-it-now .movie_links .affiliates .logo div.amazoninstant, #watch-it-now .movie_links .affiliates .logo div.fandangoNow, #watch-it-now .movie_links .affiliates .logo div.itunes, #watch-it-now .movie_links .affiliates .logo div.netflix, #watch-it-now .movie_links .affiliates .logo div.target, #watch-it-now .movie_links .affiliates .logo div.vudu
