  div[style*="border-bottom: 1px solid"], .otherNews
  { border-color: var(--border-color) !important }
    .review_quote
  { border-color: var(--bg-2) }
    .review_quote::after, .review_quote::before
  { border-right-color: var(--bg-2) }
    #topbar-container .topbar .topbarItem:first-child
  { border-left-color: var(--border-color) }
    .mop-audience-reviews__review-quote::after
  { border-right-color: var(--bg-odd) }
    .caret
  { border-top-color: var(--fg) }
    .btn-primary-rt
  { border-color: var(--fg-link) }
    .btn-primary-rt.disabled
  { border-color: transparent }
  
    .navbar.navbar-rt .navbar-searchbox .input-group, .rating_textbox .rating_textbox-textarea, .rating_textbox-textarea
  { box-shadow: 0 0 3px var(--border-color) }
  
    success, success a
  { background-color: var(--bg-success); color: var(--fg-success) }
    info, info a
  { background-color: var(--bg-info);    color: var(--fg-info) }
    warning, warning a
  { background-color: var(--bg-warning); color: var(--fg-warning) }
    error, error a
  { background-color: var(--bg-error);   color: var(--fg-error) }
    .btn-primary-rt.disabled
  { background-color: var(--bg-disabled);   color: var(--fg-disabled) }

    pre
  { background-color: var(--syntax-bg); border: none }
    pre
  { color: var(--syntax-fg) }

    .social-tools, #social-tools, #social-tools-widget, #trending_bar_social, .comment__share, #foot > .row > .subnav:nth-child(4), #recommend-button, #thread-share-menu, #header_and_leaderboard, #truste-consent-track, a[href*="doubleclick.net"], div[style*="position:absolute"], .adColumn, .sleaderboard_wrapper
