css:
@-moz-document regexp("(https?://www.)?stdcheck.\\w+/(.*?)") {
body {
    color: #000000 !important;
}
.header-bar {
    background-color: #000000 !important;
    border-bottom: 1px solid #122533 !important;
}
.header, .site-header {
    background-color: #000000 !important;
   background: #000000 !important;
    border-bottom: 1px solid #122533 !important;
}
.section.primary {
    background-image: url(https://cdn.sstatic.net/img/hero/pattern.svg), linear-gradient(to right, #121723 0, #182030 100%) !important;
    border-bottom: 1px solid #122533 !important;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #28506c !important;
}
.button.secondary {
    background-color: #142936 !important;
    border: 1px solid #164b80 !important;
}
.section.secondary.invert,
.section.white {
    background-color: rgba(0, 0, 0, 0.4) !important;
}
.callout.secondary {
    background-color: rgba(0, 0, 0, 0.3) !important;
}
.callout {
    background-color: #191919 !important;
    border: 1px solid #246 !important;
    color: #47758d !important;
}
.off-canvas-content,
.off-canvas-content,
#missing-id,
body {
    background-image: url(data:image/png;base64,) !important;
    background-color: #191919 !important;
    color: #47758d !important;
}
#missing-id,
label,
.genesis-nav-menu a {
    color: #47758d !important;
}
.section.white {
    border-bottom: 1px solid #122533 !important;
    border-top: 1px solid #12527d !important;
}
blockquote {
    background: rgba(0, 0, 0, 0.3) !important;
    border-left: 3px solid #000 !important;
}
[type="text"],
[type="password"],
[type="date"],
[type="datetime"],
[type="datetime-local"],
[type="month"],
[type="week"],
[type="email"],
[type="number"],
[type="search"],
[type="tel"],
[type="time"],
[type="url"],
[type="color"],
textarea {
    border: 1px solid #3498db !important;
    color: #131516;
    background-color: transparent !important;
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0) !important;
}
#hellobar-modal.middle {
    background-color: #000 !important;
}
#hellobar-modal .hb-header,
#hellobar-slider .hb-header {
    background-color: #12213c !important;
}
.site-footer,
.colophon {
    background: rgba(0, 0, 0, 0.4) !important;
}
.is-dropdown-submenu {
    background: #050303;
    border: 1px solid #142936;
}
.howitworks {
    background: transparent !important;
}
.howitworks__icon {
    background: rgba(0, 0, 0, 0.3) !important;
}
.header__menu .submenu .is-current {
    background-color: #135 !important;
}
.callout.secondary .callout__title {
    background: #135 !important;
    border-bottom: 1px solid #135 !important;
}
.menu.white {
    background-color: rgba(0, 0, 0, 0.3) !important;
}
.menu-text {
    background-color: #000 !important;
}
.btn {
    background-image: none !important;
    background-color: #4183C4 !important;
    color: #eee !important;
    border: 1px solid #4183C4 !important;
    text-shadow: 0 1px 1px rgba(5, 6, 12, 0.8) !important;
}
select {
    background-color: #000 !important;
    border: 1px solid #000 !important;
}
.content_order_box_in,
.findalab__search {
    background-color: rgba(0, 0, 0, 0.5) !important;
    color: #246 !important;
}
.status-medivo,
.status-active,
.status-reqsent,
.status-ilab,
.status-resulted,
.status-cancelled {
    background-color: rgba(0, 0, 0, 0.4) !important;
    border: 1px solid #246 !important;
}
.status-cancelled {
    color: #b67580 !important;
}
.status-medivo {
    color: #945d00 !important;
}
.status-active {
    color: #fff !important;
}
.status-reqsent {
    color: #33FF33 !important;
}
.status-ilab {
    color: yellow !important;
}
.status-resulted {
   color: #FF5E5E !important;
}
.table th,
.table td {
    border-top: 1px solid #246 !important;
}
.table th,
.table td:first-child {
    border-top: none !important;
}
.dropdown-menu {
    color: #3398db !important;
    background-color: #000 !important;
    border: 1px solid #246 !important;
}
.dropdown-menu > li > a {
    color: #3398db !important;
}
.nav > li > a:hover,
.nav > li > a:focus,
.tabs-title > a:hover {
    background-color: #000 !important;
    color: #246 !important;
}
.findalab,
.findalab-loading,
.findalab__results,
.findalab__total,
.tabs,
.steps,
.content {
    background-color: rgba(0, 0, 0, 0.4) !important;
    border: 1px solid #246 !important;
}
.findalab__search {
    border-bottom: 1px solid #246 !important;
}
findalab__results,
.findalab__total {
    border-top: 1px solid #246 !important;
}
.modal,
.order__header {
    background-color: #131926 !important;
}
.modal-header {
    border-bottom: 1px solid #000 !important;
}
.redesign_notes_title {
    border-top: 1px solid #000 !important;
    border-bottom: 1px solid #000 !important;
    background-color: #131926 !important;
}
hr {
    border-top: 1px solid #246 !important;
    border-bottom: 1px solid #246 !important;
}
.modal-footer {
    background-color: #131926 !important;
    border-top: 1px solid #246 !important;
    box-shadow: inset 0 1px 0 #246 !important;
}
legend,
#tests_results_uploaded li {
    color: #246 !important;
    border-bottom: 1px solid #000 !important;
}
.btn-danger {
    background-color: #da4f49 !important;
    border: 1px solid #802420 !important;
}
.site-inner,
.callout__title:first-child,
.table-striped tbody>tr:nth-child(odd)>td,
.table-striped tbody>tr:nth-child(odd)>th,
.table tbody tr.info>td,
.tabs-title>a:focus,
.tabs-title>a[aria-selected='true'] {
    background-color: #182030 !important
}
.tabs-content,.site-inner {
    background: transparent !important;
}
.callout__title:first-child {
    border-bottom: 1px solid #246 !important;
}
.messages,
.tabs-content,
.order__header,
.order__body {
    border: 1px solid #246 !important;
}
.entry, .page.page-template-page_blog-php .entry {
   border: 5px solid #246 !important;
}
.messages__header {
    background-color: rgba(0, 0, 0, 0.6) !important;
}
.order__body,
.messages__list-item:nth-child(2n) {
    background-color: rgba(0, 0, 0, 0.4) !important;
}
#hellobar-bar .icon-close, #hellobar-modal .icon-close, #hellobar-slider .icon-close, #hellobar-takeover .icon-close {
    color: #3b91cc !important;
}
#hellobar-modal-background {
    background: rgba(0,0,0,0.2) !important;
}
table tfoot, table thead {
    color: #6b6b6b !important;
}
.success-color, .fa-check, .fa-check-circle, .fa-check-circle-o , .price-text{
    color: #709e2e !important;
}
.progress {
background-color: rgba(0,0,0,0.4) !important;
}
.progress.secondary .progress-meter, .steps__step--chevron::before, .steps__step--chevron::after, .steps__title, .callout.secondary .callout__title {
    background-color: #182030 !important;
    background: #182030 !important;
}
}
