css:
@-moz-document domain("www.notion.so") {
.notion-sidebar-container {
    background-color: #121212!important;
    color: #fff!important
}
.notion-frame {
    background-color: #202020!important
}
.notion-scroller>div:nth-child(1) {
    color: #eee!important
}
#notion-app>div>div>div .notion-sidebar-container>div:nth-child(1)>div:nth-child(2)>div>div:nth-child(1),
#notion-app>div>div>div .notion-sidebar-container>div:nth-child(1)>div:nth-child(2)>div>div:nth-child(5)>div:nth-child(1)>span>div>div>div:nth-child(2),
.notion-topbar {
    color: #fff!important;
    background-color: #121212!important
}
.notion-sidebar-container {
    background: linear-gradient(rgb(27, 27, 27), rgb(27, 27, 27) 46px, rgb(27, 27, 27) 120px, rgb(27, 27, 27) 35%, rgb(0, 0, 0))!important;
}
.white-space {
    color: rgb(167, 167, 167)!important;
}
.data-block-id {
    color: rgb(177, 177, 177)!important;
}
.token.punctuation {
    color: #ffffff!important;
}
p {
    color: #ffffff;
}
contenteditable {
    white-space: pre-wrap;
    -webkit-user-modify: read-write-plaintext-only;
    color: #d5d5d5;
}
[contenteditable] {
    -webkit-tap-highlight-color: transparent;
    color: #e1e1e1!important;
}
.notion-cursor-pointer {
    color: #e0e0e0!important;
}
  /* 
  thank you for using this theme. pls rate. TakeoTheSavage 
  */
}
