css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blog.fefe.de") {

blockquote 
{
    background: rgb(252, 252, 252)!important;
    border-top-color:#707070 !Important;
    border-top-style:solid !Important;
    border-top-width:1px !Important;
    border-left-color:#808080 !Important;
    border-left-style:solid !Important;
    border-left-width:1px !Important;

    margin-top: 1em !important;
    margin-bottom: 1em !important;
    padding-left: 1em !important;
    padding-right: 1em !important;
    padding-top: 0.4em !important;
    padding-bottom: 0.4em !important;
    
    
    border-radius: .25rem;
    overflow-x: auto;
    overflow-y: auto;
    padding: 0.25rem;
    abackground: #272822 !important;
    acolor: #f8f8f2 !important;
}

    li {
        margin-top: 0em;
        margin-bottom: 1.5em;
        color:black !important;}
    
  
     ul > li > a:nth-child(1) 
      {  color:rgb(240, 240, 240) !Important; font-size:7px; position:relative; left: -14px; top: 3px;  }
    
    body > p:nth-child(3),  h2 { text-align: center !important; width:100% !important; position:relative !important;}
    body > b:nth-child(2) {visibility: collapse !important; text-align: center !important; width: 100% !important; position:relative !important;}
    
    h3 {margin-top: 5em;  margin-bottom: 0em; font-size: 15px; position:absolute;  left: -40px !important;
    -webkit-transform: rotate(-90deg);
    -moz-transform: rotate(-90deg);
    -ms-transform: rotate(-90deg);
    -o-transform: rotate(-90deg);
    border-top-color:#e0e0e0 !Important;
    border-top-style:solid !Important;
    border-top-width:1px !Important;
    border-left-color:#808080 !Important;
    border-left-style:solid !Important;
    border-left-width:2px !Important;
    border-right-color:#e0e0e0 !Important;
    border-right-style:solid !Important;
    border-right-width:1px !Important;
    border-bottom-color:#808080 !Important;
    border-bottom-style:solid !Important;
    border-bottom-width:2px !Important;
    padding:10px;
     background: rgb(240, 240, 240)!important;
    }
    
    p {
        margin-top: 0.5em;
        margin-bottom: 0.5em;
        color:black !important;
    }
    
    ol {color:black !important;}
    
  a {  color:#101070 !Important; text-decoration-line: none !important;}

        
html
    {   
    line-height: 1.1em !important;
    word-wrap: normal !important;
    }

    
    ul
    {   
     width:70% !important;
     margin-left: 10% !important;
     background: rgb(240, 240, 240)!important;
     padding-top: 1em !important;
     padding-bottom: 1em !important;
     padding-right: 1.5em !important;
    }

}
