css:
@-moz-document domain("asianwiki.com"), domain("blog.asianwiki.com") {
body,.main {background: #252525;}
article.article,#comment-list,#commentform input, #commentform textarea, #commentform textarea[style], #commentform select,#commentform p:nth-of-type(even),#comment-list .comment-item,#comment-list .comment-item:nth-of-type(even),.catlinks,.site-footer,#searchInput,table.wikitable,.wikitable tr:nth-of-type(even) td,#mw-userrights-form1 input#username, #mw-userrights-form1 input#username[size], input#mw-whatlinkshere-target {background: #303030; box-shadow: 0 0 3px #aaaaaa;}
#slidorion,#slidorion2,#slide1,#slide2,.slide2,table,#main article,#accordion {background: #353535 !important;}
#accordion>.link-header {background: #505050 !important; color: #cccccc !important;}
#accordion>.link-content {background: #5790f9 !important; color: #5790f9 !important;}
#accordion>.link-header.active {background: #252525; color: #5790f9 !important;}
html,body,.p-tools li a,h2,button, input, select, textarea,table.wikitable,.wikitable tr:nth-of-type(even) td,aside ul a,footer a,.subtitle a {color: #cccccc;}
.commentmetadata, .commentmetadata a {color: #aaaaaa;}
a,aside ul a:hover,#accordion .link-header:hover {color: #5790f9 !important;}
img:hover, img[height]:hover, img[width]:hover {filter:saturate(1.75); box-shadow: 0 0 3px #cccccc;}
h1 {font-family: arial, tahoma; font-weight: 100;}
h2,h3 {font-family: segoe ui,arial, tahoma; font-weight: 100; color: #cccccc;}
a.new {color: #ff5c5c;}
}
