css:
@-moz-document domain("coinmarketcap.com") {
body, .is-theme-standard .navbar.desktop, .is-theme-standard .global-stats.desktop.outer-container {
    color: #606060;
    background-color: #06111A;
    border-bottom: 0;
}
#title a {
    color: #999;
}
.navbar-default {
    background-color: #081621!important;
    border-color: #0B1B29!important;
}
#top-nav-outer-container {
    padding: 5px 15px 0 15px;
    background-color: #101010;
    border-bottom: 1px solid #06111A;
}
.global-stats.desktop.outer-container {
    background-color: #06111A;
    border-bottom: 1px solid #0B1B29;
}

.language-dropdown, .global-currency-dropdown {
    background-color: #06111A;
	border: 1px solid #0B1B29;
}

a {
    color: #909090;
    text-decoration: none;
}
img.sparkline {
    border: 1px solid #0B1B29;
    width: 166px;
    height: 50px;
    background: #06111A;
}
.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #aaa;
    background-color: #202020;
    background-image: none;
    border: 1px solid #0B1B29;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    color: #555;
    background-color: #202020;
    border: 1px solid #0B1B29;
    border-bottom-color: transparent;
    cursor: default;
}
.pagination>li>a:hover, .pagination>li>span:hover, .pagination>li>a:focus, .pagination>li>span:focus {
    color: #2a6496;
    background-color: #0B1B29;
    border-color: #0B1B29;
}
.pagination>li>a{
    color: #2a6496;
    background-color: #202020;
    border-color: #0B1B29;
}
#charts{
    -webkit-filter: invert(80%); /* Chrome, Safari, Opera */
    filter: invert(80%);
}
table#currencies>tbody>tr>td {
    padding: 6px;
    vertical-align: middle;
    border-top: 0;
    border-bottom: 1px solid #0B1B29;
}

.table>thead>tr>th {
    vertical-align: bottom;
    border-bottom: 1px solid #0B1B29;
}
thead tr {
    border-top: 1px solid #0B1B29;
}


.dataTable td.sorting_1 {
    border: 1px solid #0B1B29;
}

.sorting_asc {
    border-left: 1px solid #0B1B29;
    border-right: 1px solid #0B1B29;
}
.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td {
    border-top: 1px solid #0B1B29;
}
.nav-tabs {
	border-bottom: none;
}
 hr {
    border-top: 1px solid #0B1B29;
 }
  .table thead tr th {
	background-color: #081621!important;
}
 
.table thead tr th:after {
    border-bottom:0;
}
}
