css:
@-moz-document url-prefix("https://www.amazon"){
/* latin-ext */
@font-face {
  font-family: 'Quicksand';
  font-style: normal;
  font-weight: 400;
  src: local('Quicksand Regular'), local('Quicksand-Regular'), url(https://fonts.gstatic.com/s/quicksand/v7/s2PXW4WrV3VLrOUpHiqsfRJtnKITppOI_IvcXXDNrsc.woff2) format('woff2');
  unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Quicksand';
  font-style: normal;
  font-weight: 400;
  src: local('Quicksand Regular'), local('Quicksand-Regular'), url(https://fonts.gstatic.com/s/quicksand/v7/sKd0EMYPAh5PYCRKSryvW1tXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
* {
    font-family: 'Quicksand', sans-serif !important;
    text-decoration:none !important ;
}


@keyframes fadeIn {
  from { opacity: 0}
  to   { opacity: 1}
}

img{  
    animation-name: fadeIn;
    animation-duration: 1s;
}
#navbar #nav-flyout-ewc .nav-flyout-head
{
    border-left: 1px solid #550000;
}


#navbar.nav-bluebeacon 
,#navbar.nav-bluebeacon #nav-main 
,#navbar.nav-bluebeacon #nav-belt
,#navbar.nav-bluebeacon #nav-flyout-ewc .nav-flyout-head
,.nav-bluebeacon #ewc #ewc-checkout.ewc-untabbed-checkout
,.nav-bluebeacon #ewc #ewc-checkout.ewc-untabbed-checkout #ewc-flc-expander .ewc-expander-header
{
    background: linear-gradient(to right, #010101,#2f2f2f, #010101) !important; 
}

#navFooter.navLeftFooter
{
    background-color: #000000;
    background: linear-gradient(to right, #010101,#101010, #010101) !important; 
}

#navFooter.navLeftFooter .navFooterBackToTop:focus, #navFooter.navLeftFooter .navFooterBackToTop:hover
{
    background-color: #660000;
    background: linear-gradient(to right, #010101,#262626, #010101) !important; 
}
#navFooter.navLeftFooter .navFooterBackToTop
{
    background-color: #400000;
    background: linear-gradient(to right, #010101,#141414, #010101) !important; 
}
#navFooter.navLeftFooter .navFooterDescLine
{
    background-color: #300000;
    background: linear-gradient(to right, #010101,#232323, #010101) !important; 
}
#navFooter.navLeftFooter .navFooterCopyright
{
    background-color: #200000;
    background: linear-gradient(to right, #010101,#121212, #010101) !important; 
}

.a-button-primary .a-button-inner
{
    background: ivory;
    background: linear-gradient(to bottom,ivory,tan);
}
.a-button-oneclick .a-button-inner
{
    background: tan;
    background: linear-gradient(to bottom,peachpuff,tan);
}

body
,.hud-white-dashboard
{
    background: linear-gradient(to left, #FFEFDB, #EEDFCC, #FFEFDB)!important;
}

hr.bucketDivider
,hr.bucketDivider:after
,.a-box .a-divider.a-divider-section .a-divider-inner, .a-color-base-background .a-divider.a-divider-section .a-divider-inner, .a-divider.a-divider-section .a-divider-inner
,.a-box .a-divider.a-divider-section .a-divider-inner:after, .a-color-base-background .a-divider.a-divider-section .a-divider-inner:after, .a-divider.a-divider-section .a-divider-inner:after
{
    background: linear-gradient(to left,  #EEDFCC,  #FFEFDB)!important;
}



a:active *
, a *:active
,a:active

,a:link *
,a *:link *
,a:link 
{
    text-decoration: none;
    color: #FF4500 !important;
}

a:visited
,a:visited *
,a *:visited
{
    text-decoration: none;
    color: coral !important;
}
.nav-tpl-discoveryPanelList .nav-text, .nav-tpl-discoveryPanelSummary .nav-text, .nav-tpl-itemList .nav-text
,.nav-text *
{
color:darksalmon;
}

#rhf .rvi-fresh-container .rhf-faceout-hue{
    background-color: rgba( 255,239,219,.1);
}

.nav-catFlyout.nav-flyout
,.nav-coreFlyout.nav-flyout
{
	background: rgba(0, 0, 0, 0.8) !important;
}

.a-section.similarities-widget.bucket
,.a-ordered-list.a-horizontal, .a-unordered-list.a-horizontal, ol.a-horizontal, ul.a-horizontal
,.a-section.askTeaserQuestions
,h1:last-child, h2:last-child, h3:last-child, h4:last-child
{
    	background-color: unset !important;
}

.s-result-card, .s-result-card-for-container .s-item-container
,#rhf #rhf-shoveler
,#rhf .rvi-fresh-footer
,.leftCol
,#rhf .rhf-footer
,.a-carousel-viewport
,.a-section.p13n-sc-list-cells-container
,.a-unordered-list.a-nostyle.a-horizontal.a-spacing-base.a-spacing-top-base.sims-fbt-image-box
,.a-box
,.s-result-item.celwidget 
{
    background: linear-gradient(to right, rgba(255, 255, 255, 0.8),  rgba(255, 255, 255, 0.7))!important;
}


}
