css:
@-moz-document domain("people.planningcenteronline.com") {
div[data-react-class*="Topbar"] {
    height: 60px !important;
}
div[data-react-class*="Topbar"] .simple {
    height: 60px !important;
}
div.simple :nth-child(4) {
    margin: auto !important;
}
div.org-img.mb-2 {
    display: none!important;
}
.people-global-search-container {
    display: none;
}
.widget {
    min-height: 80vh!important;
}
.page-heading-wrap {
    background: #739def;
    margin: 0px!important;
}
h1.page-heading {
    color: white!important;
    padding: 3px 0 0 20px;
    font-weight: 700;
}
.page-heading-wrap button {
    background: white;
    color: white;
}
.btn-wrap .btn.btn--primary,
.btn-wrap .btn.btn--outline,
a[data-modal-url="/workflows/deleted"] {
    background: white!important;
    border-radius: 5px!important;
    border: 2px solid #739DEF!important;
    font-size: 14px!important;
    color: #3F70CB;
}
.btn-wrap .btn.btn--primary:hover,
.btn-wrap .btn.btn--outline:hover {
    background: #eaeaea!important;
    color: #3F70CB;
}
.workflow-item-title {
    font-size: 20px;
    text-transform: uppercase;
}
.breadcrumbs a {
    color: white!important;
}
}
