css:
@-moz-document url-prefix("https://tweetdeck.twitter.com/") {
	/* flat this shit*/
	.app-columns {
		padding-left: 0px !important;
	}

	.column {
		margin-right: 0px !important;
	}

	.column-header {
		border-bottom: 1px solid transparent !important;
	}

	.needs-scroll-bottom-offset.scroll-styled-h {
		bottom: 0px !important;
	}

	.stream-item {
		border-bottom: 0px !important;
	}

	.scroll-styled-v::-webkit-scrollbar-track,
	.scroll-styled-h::-webkit-scrollbar-track {
		border-left: 1px solid transparent !important;
	}

	.activity-header.has-source-avatar {
		margin-top: 0px !important;
	}

	/*collumn move*/
	.column-number {
		display: none !important;
	}

	.column-drag-handle {
		position: absolute !important;
		top: 0px !important;
		left: 0px !important;
		width: 350px !important;
		height: 50px !important;
		background-position: 240px 160px !important;
		cursor: auto !important;
	}

	.column-header-links {
		z-index: 1;
	}

	/* New Tweet header */
	.compose-header {
		height: 50px !important;
		border-bottom: none !important;
	}

	.js-drawer-close {
		display: none !important;
	}

	html.dark .compose-header {
		background-color: #243447;
		box-shadow: -15px 0px 0px 0px #243447, 15px 0px 0px 0px #243447;
	}

	html.dark .is-condensed .attach-compose-buttons .tweet-button {
		width: 36px;
		border-radius: 45px;
	}

	.is-open .drawer:after {
		box-shadow: none !important;
	}

	/* accounts list */
	.drawer-header {
		height: 50px !important;
	}

	html.dark .drawer-header {
		border-bottom: none;
		background-color: #243447;
		color: #e1e8ed;
	}

	html.dark .drawer-header-title {
		padding-top: 13px;
	}

	/* hide show this thread */
	p.color-twitter-blue.txt-link.txt-ellipsis.link-complex-target.margin-b--5 {
		display: none;
	}

	/* transparency */
	html.dark .application {
		background: #000 url(https://www.hdwallpapers.in/walls/deep_space_4k-wide.jpg);
		background-size: cover;
		background-repeat: no-repeat;
		background-position: center center;
	}

	html.dark .app-content {
		background-color: #151f2a00;
	}

	html.dark .app-columns-container {
		background-color: #151f2a00;
	}

	html.dark .column {
		background-color: #1b283600;
	}

	html.dark .stream-item {
		background-color: #1b283600;
	}

	html.dark .scroll-conversation {
		background: #18243000;
	}

	html.dark .tweet-detail-wrapper {
		background: #1b283600;
	}

	html.dark .compose {
		background-color: #3d546680;
	}

	/* scroll */
	html.dark .scroll-styled-v::-webkit-scrollbar-thumb,
	html.dark .scroll-styled-h::-webkit-scrollbar-thumb {
		background-color: #65778680;
	}

	html.dark .scroll-styled-v::-webkit-scrollbar-thumb:hover,
	html.dark .scroll-styled-h::-webkit-scrollbar-thumb:hover {
		background-color: #657786bf;
	}

	/**/
	.column-nav-item {
		border-radius: 0 50px 50px 0;
	}
}
