css:
@-moz-document domain("mediapart.fr") {
body {
    font-family: Spectral, "Times New Roman", Times, serif;
    font-size: 1.1rem;
    background: linear-gradient(to bottom, rgba(240, 237, 238, 1) 0%, rgba(220, 220, 220, 1) 100%);
}
a {
    color: #202222;
}
a:hover {
    color: #6D5AD1;
}
.mkt-banner {
    font-size: 1rem;
    line-height: 1;
    display: none;
}
.header .lang-links {
    float: left;
    display: none;
}
.journal-bundle h1 {
    margin-top: 15px;
}
.prolonger {
    display: none !important;
}
.fractal-10-desktop.collapse-3-desktop {
    width: 30%;
    vertical-align: top;
}
.extra-header{
	background-color: rgba(255,255,255,0);	
}

.read-also {
    padding-bottom: 25px;
    width: 200px;
    display: none;
}
.service,
.sponsorship {
    display: none;
}
header-sticky:not(.fixed) .menuPanelOpen {
    bottom: -.125rem;
    position: absolute;
    width: auto;
    display: none;
}
.header .logo {
    padding-top: 0px;
}
.header form[role="search"] {
    width: 195px;
    display: none;
}
.body,
p,
.content-article {
    font-size: 1.2rem;
}
.content-article .page-pane > p {
    font-size: 1.2rem !important;
}
.sub-header .title,
.sub-header .title .main-title {
    font-size: 3rem;
    line-height: 1.1;
}
.introduction p {
    font-size: 1.3rem !important;
}
.introduction {
    margin-top: 25px;
}
.tools-share {
    display: none;
}
.global-wrapper {
    padding-top: 70px !important;
    padding-bottom: 30px;
}
.global-wrapper {
    position: relative;
    box-sizing: border-box;
    margin-right: auto;
    margin-left: auto;
    padding-right: 80px;
    padding-left: 80px;
    width: 1050px;
    background-color: #FFF;
}
.header .menuPanelLink.menuPanelOpen {
    display: none;
}
.header {
    background-color: transparent;
}
.main-menu {
    display: none;
    background-color: transparent;
}
.main-menu .link-journal {
    background-color: #fff;
    border-bottom: 1px dotted #555;
}
.main-menu .link-studio {
    /*background-color: #3e426f69;*/
    display: none;
}
.main-menu .link-club {
    display: none;
    /*background-color: #3e426f69;*/
}
.journal-bundle .main-menu li:nth-child(6) {
    padding-right: 0px;
}
.journal-bundle .main-menu .link-studio {
    padding-right: 0px;
}
.main-menu .link-journal a:active::after,
.main-menu .link-journal a:focus::after,
.main-menu .link-journal a:hover::after,
.main-menu .link-journal.is-active a::after {
    background-color: #6D5AD1;
}
.journal-bundle .header .logo {
    background-image: none;
}
.header .logo {
    padding-left: 0px;
    background-size: auto 70px;
}
.header .brand {
    margin: 0 auto 5px;
    text-align: center;
    height: 50px;
}
.sub-menu.is-on-top {
    margin-bottom: 40px;
    margin-top: 15px;
    border-top: 1px dotted #6A6D6D;
    border-bottom: none;
    background-color: #fff;
}
.sub-menu.is-on-bottom {
    display: none;
}
.sub-menu .pager {
    margin-top: 0;
    border-bottom: 0;
}
.comments {
    display: none !important;
}
.sub-menu > li::before {
    content: " ";
    position: absolute;
    left: 0;
    bottom: 0;
    width: 1px;
    height: 14px;
    background-color: #6A6D6D;
}
.sub-menu > li:first-child::before {
    content: none;
}
.sub-menu > li:nth-child(2)::before {
    content: none;
}
.sub-menu > li {
    position: relative;
    margin: 0px 0;
    padding: 0 6px 0 10px;
}
.sub-menu > li:first-child {
    padding-left: 0px;
}
.sub-menu > li:last-child {
    display: none;
}
.header .account-links button,
.header .account-links button:visited {
    color: #555;
}
.extra-header .bust{
    margin-bottom: 20px;
    width: 1010px;
    border: 20px solid #6D5AD1;
    background-color: #ffffff;
}
.extra-header .bust h2{
	padding-left: 20px;
    padding-right: 20px;
    padding-top: 20px;
}
.extra-header .bust	.une-block{
	padding-left: 20px;
    padding-right: 20px;
    padding-top: 30px;
    width: 96%;
}
.header .bust {
    padding-top: 25px;
    padding-bottom: 15px;
    width: 1050px;
}
.header .account-links {
    position: absolute;
    float: right;
    padding-top: 80px;
}
.fixed .menuPanelLink {
    display: none;
}
.fixed .article-tools {
    display: none;
}
.fixed .header {
    background-color: white;
    height: 3rem;
}
a.journalist,
span.journalist {
    color: #6D5AD1;
}
.footer .cta {
    display: none;
}
.universe-journal .content-title:active,
.universe-journal .content-title:focus,
.universe-journal .content-title:hover,
.universe-journal .title a:active,
.universe-journal .title a:focus,
.universe-journal .title a:hover,
.universe-journal a.title:active,
.universe-journal a.title:focus,
.universe-journal a.title:hover,
.universe-journal a:active .title,
.universe-journal a:focus .title,
.universe-journal a:hover .title {
    color: #6D5AD1;
}
.badge::after {
    border-left-color: #6D5AD1;
}
.badge::before {
    border-right-color: #6D5AD1;
}
.badge {
    background-color: #6D5AD1;
}
.fixed .account-links {
    display: none;
}
.slide-show-feeds {
    display: none;
}
.additionnal-contents {
    display: none;
}
.editorial-staff {
    display: none;
}
.slide-show-feeds .slide-show-header .title,
.slide-show-feeds .slide-show-header .title a {
    color: #6D5AD1;
}
.slide-show-feeds [class^="slide-show-body"] a {
    border-left: 3px solid #6D5AD1;
}
.slide-show-feeds [class^="slide-show-body"] a:active .title,
.slide-show-feeds [class^="slide-show-body"] a:focus .title,
.slide-show-feeds [class^="slide-show-body"] a:hover .title {
    color: #6D5AD1;
}
.universe-journal .slide-show-menu-pn button:focus::after,
.universe-journal .slide-show-menu-pn button:hover::after {
    color: #6D5AD1 !important;
}
.journal-bundle h1,
.journal-bundle h2,
.journal-bundle h3,
.journal-bundle h4,
.journal-bundle h5,
.journal-bundle h6 {
    font-family: Spectral, "Times New Roman", Times, serif;
}
.extra-header h2.title,
.extra-header h2.title .main-title,
.header-type-rubrique .sub-header .title,
.header-type-rubrique .sub-header .title .main-title,
.journal-bundle .post-list .une-block .teaser .title,
.journal-bundle .post-list .une-block .teaser .title .main-title,
.journal-bundle .post-list .une-block.teaser .title,
.journal-bundle .post-list .une-block.teaser .title .main-title,
.journal-bundle .teaser .title,
.journal-bundle .teaser .title .main-title,
.journal-bundle h1,
.journal-bundle h1 .main-title,
.post-event.first-post [class^="event-articles"] .event-article .title,
.post-event.first-post [class^="event-articles"] .event-article .title .main-title {
    font-size: 3.125rem;
    line-height: 1.11111;
}
.media-image {
    background-color: #6D5AD1;
}
.preview {
    mix-blend-mode: screen;
}
.media .all-width {
    mix-blend-mode: screen;
}
.format-100-pcent{
    background-color: #6D5AD1;
}
.format-100-pcent a{
	mix-blend-mode: screen;
}

.copyright,
.media .legend,
.tweet.legend {
    background-color: white;
    border-left: none;
    padding-left: 0px;
}
.fractal-desktop > div {
    padding-left: 0px !important;
}
.top-multimedia-journal,
a.top-multimedia-journal {
    color: #6D5AD1;
}
.post-list > .block-1third-2third .fractal-desktop > div,
.post-list > .block-2third-1third .fractal-desktop > div,
.post-list > .block-fiftypcent .fractal-desktop > div {
    border-top: 1px solid #E6E6E6;
    padding: 10px;
}
#magasin_journal {
    display: none;
}
.multimedias-menu {
    display: none;
}
.multimedia-region {
    margin-top: 15px;
    background-color: transparent !important;
    border-top: none;
    border-bottom: none;
    box-shadow: none;
}
.journal-bundle .content-journal .title a:active,
.journal-bundle .content-journal .title a:focus,
.journal-bundle .content-journal .title a:hover,
.journal-bundle .content-journal a.title:active,
.journal-bundle .content-journal a.title:focus,
.journal-bundle .content-journal a.title:hover,
.multimedia-container a:active .title,
.multimedia-container a:focus .title,
.multimedia-container a:hover .title {
    color: #6D5AD1;
}
.multimedia-container .accur8-desktop .fractal-desktop {
    padding: 10px;
    border-right: none;
    vertical-align: top;
}
.multimedia-container .media {
    background-color: #6D5AD1;
}
.bullet-list.universe-journal > .title {
    color: #6D5AD1;
}
.time {
    background-color: #6D5AD1;
    color: white;
}
.multimedia-container .ico {
    background-color: #6D5AD1;
    color: white;
    border-radius: 17px;
    box-shadow: none;
}
.universe-journal .slide-show-menu-els button.active,
.universe-journal .slide-show-menu-els button[disabled] {
    background-color: #6D5AD1;
}
.archive form {
    display: none;
}
.editorial-staff img {
    display: none;
}
.slide-show-feeds .slide-show-header a.bt-specific {
    display: none;
}
.slide-show-feeds .slide-show-menu {
    display: none;
}
.slide-show-feeds .slide-show {
    border-top: 1px solid #CCC;
    border-bottom: none;
    padding-bottom: 15px;
    margin-top: 20px;
    margin-bottom: 20px;
}
.page-content .slide-show:last-child {
    margin-top: 20px;
    margin-bottom: 20px;
}
.journal-bundle div.slide-show-feeds {
    margin-bottom: 20px;
}
.header .account-links button,
.header .account-links button:visited {
    color: #fff;
}
.header .account-links button:hover {
    color: #6D5AD1;
}
.blackbox {
    background-color: #000;
    background-image: none !important;
    padding: 40px 0 0 50px;
}
.blackbox-inner {
    background: none;
    padding: 0 50px 50px 0;
}
.blackbox p {
    font-size: 1.1rem;
    color: #FFF;
}
.blackbox ul {
    font-size: 1.1rem;
}
.blackbox a {
    color: #fff;
}
.badge.redaction-choice::after {
    border-left-color: #B3B5C6;
}
.badge.redaction-choice::before {
    border-right-color: #B3B5C6;
}
.badge.redaction-choice {
    background-color: #B3B5C6;
}
.journal-bundle .col-right-content {
    border: 1px solid #E6E6E6;
    padding: 10px;
    padding-right: 10px;
    background-color: #F5F5F5;
}
.universe-club .title a:active,
.universe-club .title a:focus,
.universe-club .title a:hover,
.universe-club a.title:active,
.universe-club a.title:focus,
.universe-club a.title:hover,
.universe-club a:active .title,
.universe-club a:focus .title,
.universe-club a:hover .title,
.universe-club.bullet-list a:active,
.universe-club.bullet-list a:focus,
.universe-club.bullet-list a:hover {
    color: #B3B5C6;
}
a.title,
.redactor-edition a.title,
.redactor-identity .avatar a,
.redactor-identity .social,
.redactor-list ul,
.redactor-menu,
.redactor-overview .title,
.redactor-overview.contribution,
.simple-list .from,
.simple-list .type,
.slide-show-feeds .source,
.sm-link + .see-more,
.small-cap,
.sub-header-article .title,
.sub-header-blog .title,
.sub-header-edition .title,
.sub-menu,
.subhead,
.taxonomy,
.thread .context,
.thread .participant,
.thread .title,
.title.form,
.tools-share,
.view-activity,
.view-article,
.view-more,
label {
    font-family: Spectral, Verdana, sans-serif;
}
a.subscriber {
    color: #6D5AD1;
}
.offre-parrainage {
    display: none;
}
.block-reco {
    display: none;
}
.has-scrolling-col.is-active .col-right-content {
    bottom: none;
}
.page-content .additionnal-contents .col-right {
    display: none;
}
.journal-bundle .col-right .post-list .title {
    font-family: Spectral, Verdana, sans-serif;
}
#colClub {
    display: none;
}
.header .account-links li svg {
    display: none;
}
.fractal-10-desktop.collapse-3-desktop {
    display: none;
}
.footer {
    width: 1050px;
}
.post-list .keywords .une-block ul,
.une-block .bg-highlight,
.une-block .post-list .keywords ul {
    background-color: #fff;
}
.all-width img {
    mix-blend-mode: screen;
}
.content-page-full {
    display: none !important;
}
.sub-menu .pager a {
    color: #aaa;
}
.node-type-article .col-left {
    padding-right: 0px !important;
}
.content-article a {
    text-decoration-style: dotted;
}
.post-event{
	background-color: #FFF;
    border: 1px solid #E6E6E6 !important;
}
.post-event .event-header{
	border-bottom: none !important;
}
.post-list-wrapper .post-event .event-display-by-side div.cartoon-wrapper{
	background-color: #6D5AD1;
}
.post-event .event-article--cartoon-wrapper{
	background-color: #fff;
}
.post-event .event-thumb-item{
	background-color: #fff;
}
.post-list-wrapper .event-thumb-image{
	background-color: #6D5AD1;
}
.post-list-wrapper .post-event [class^="event-articles"] .event-article--cartoon::before{
	border-top: 1px solid #E6E6E6 !important;
}
.post-event .event-title {
    font-family: Open_Sans,Arial,Verdana,sans-serif;
    font-weight: 400;
    text-transform: uppercase;
    text-align: left;
    padding-left: 80px;
    font-size: 1.1rem;
	line-height: 0.9;
}
}
