css:
@-moz-document domain("check-ins.planningcenteronline.com") {
* {
    -webkit-font-smoothing: antialiased !important;
    -moz-osx-font-smoothing: grayscale !important;
}
div[data-react-class*="Topbar"] {
    height: 60px !important;
}
div[data-react-class*="Topbar"] .simple {
    height: 60px !important;
}
div.simple :nth-child(4) {
    margin: auto !important;
}
.main-section {
    padding: 0!important;
}
.page-heading-wrap,
.page-heading-wrap.page-heading-wrap--enclosed {
    background: #a87eb7;
    margin: 0px!important;
    padding: 16px!important;
}
.page-heading2 {
    margin: 0!important;
}
h1.page-heading,
.page-heading2-title {
    color: white!important;
    padding: 3px 0 0 20px!important;
    font-weight: 700;
    font-size: 20px!important;
}
.page-heading2-title {
    color: white!important;
    padding: 0 0 0 10px!important;
    font-weight: 700;
    font-size: 20px!important;
}
h1.page-heading span {
    display: none;
}
.page-heading-wrap .btn.btn--primary,
.page-heading-wrap .btn.btn--outline,
button[data-modal-url="/stations/launch"],
a[data-reactid=".3.0.0"] {
    background: white!important;
    border-radius: 5px!important;
    border: 2px solid #a87eb7!important;
    font-size: 14px!important;
    color: #734D82!important;
    font-weight: 600;
}
a[data-reactid=".3.0.0"] {
    margin: 0 0 60px 30px;
}
.page-heading-wrap .btn.btn--primary:hover,
.page-heading-wrap .btn.btn--outline:hover {
    background: #eaeaea!important;
    color: #3F70CB;
}
.select {
    height: 40px !important;
    font-size: 14px !important;
    font-weight: 600;
    padding: 10px 30px 10px 10px!important;
    color: white!important;
    background: #8C6999;
    border: none;
    border-radius: 4px;
}
span.icon.lh-1 {
    display: none;
}
.btn-group .btn {
    font-size: 14px;
    padding: 11px;
    border: none;
    box-shadow: 0px 0px 19px 0px rgba(128, 92, 158, 0.3);
}
.pane.pane--default {
    margin: 30px;
    border: 0!important;
    border-radius: 5px!important;
    box-shadow: 0px 0px 19px 0px rgba(128, 92, 158, 0.3);
}
@media(min-width:768px){
	.pane--stations-right {
        float:right!important;
   		width: 54%!important;
        margin:30px 30px 30px auto!important;
	}
	.pane--stations-left {
		width:35%!important;
        margin:30px 15px 30px 30px!important;
	}
}
@media(max-width:767px){
	.pane-group--stations .pane--stations-right {
   		width: 100%;
	}
	.pane.pane--default.pane--split.pane--stations-left {
		width:100%;
	}
}
.row.nav-tabs__row {
    padding-top: 30px;
    padding-left: 30px;
    padding-right: 30px;
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:focus {
    background-color: #9878A3!important;
    border: 0!important;
    border-radius: 4px;
    color: white;
    font-weight: 600;
    cursor: default;
}
.nav-tabs__label, .inline-icon{
	color:black;
    
}
.mb0.active a{
	border-bottom-left-radius:0!important;
    border-bottom-right-radius:0!important;
}
.btn--tab-overlap.btn--has-icon {
    background: #9878A3!important;
    border-radius: 3px!important;
    border: 0!important;
    font-size: 14px!important;
    color: white!important;
    font-weight: 600;
    margin-top: 30px;
    margin-left: 30px;
    margin-right: 30px;
}
.person-header {
    margin: 0!important;
    padding-bottom: 0!important;
    padding-left: 30px!important;
}
}
