css:
@-moz-document domain('octodon.social') {
div.ui {
  background: #F0F2F5;
}
.drawer__header {
  /*background: #CCD1D9;*/
  border-radius: 4px;
}
.drawer__header a:hover {
  background: #AAB2BD;
}
.drawer__header a i {
  color: #aab2bd;
}
.drawer__header a:hover i {
  color: #656D78;
}
.search__input {
  background: #656D78;
  border-radius: 4px;
  color: #E6E9ED;
}
.search__input:focus {
  background: #434A54;
}
.search__icon .fa {
  color: #CCD1D9;
}
i.fa.fa-times-circle.active {
  color: #CCD1D9;
}
i.fa.fa-times-circle.active:hover {
  color: #F5F7FA;
}
::-webkit-input-placeholder { /* WebKit, Blink, Edge */
  color: #E6E9ED;
}
::-webkit-textarea-placeholder { /* WebKit, Blink, Edge */
  color: #9caec7;
}
:-moz-placeholder { /* Mozilla Firefox 4 to 18 */
  color: #E6E9ED;
  opacity: 1;
}
::-moz-placeholder { /* Mozilla Firefox 19+ */
  color: #E6E9ED;
  opacity: 1;
}
:-ms-input-placeholder { /* Internet Explorer 10-11 */
  color: #E6E9ED;
}
INPUT:-ms-input-placeholder { /* Internet Explorer 10-11 */
  color: #E6E9ED;
}
::-ms-input-placeholder { /* Microsoft Edge */
  color: #E6E9ED;
}
[type="search"] {
  -moz-appearance: textfield;
  -webkit-appearance: textfield;
  appearance: textfield;
}
.drawer__inner {
  background: #AAB2BD;
  border-radius: 4px;
}
.navigation-bar strong {
  color:#fff;
}
.navigation-bar {
  color: #fff;
}
.emoji-mart-bar:first-child {
  background: #E6E9ED;
}
.account__avatar {
  border-radius: 0;
}
.navigation-bar__profile-account {
  font-size: 14px;
  padding: 10px 10px 0 0;
  display: inline-block;
}
.navigation-bar__profile-edit {
  font-size:12px;
  opacity: .5;
}
.compose-form__publish-button-wrapper .button {
  background-color: rgb(43, 144, 217);
  color: #fff;
}
.compose-form__publish-button-wrapper .button:hover {
  background-color: #49a5fb;
  color: #fff;
}
.privacy-dropdown.active .privacy-dropdown__value {
  background: rgb(43, 144, 217);
}
.privacy-dropdown.active .privacy-dropdown__value button i {
  color: rgba(255,255,255,.5);
}
.privacy-dropdown__option:hover {
  background-color: rgba(43, 144, 217, .25);
}
.privacy-dropdown__option:hover .privacy-dropdown__option__content strong {
  color: #000;
}
.privacy-dropdown__option:hover .privacy-dropdown__option__content {
  color: rgba(0,0,0,.75);
}
.privacy-dropdown__option.active, .privacy-dropdown__option.active:hover {
  background-color: rgba(43, 144, 217, .5);
}
.privacy-dropdown__option__content {
  color: #AAB2BD;
}
.privacy-dropdown__option__content strong {
  color: #656D78;
}
.privacy-dropdown__option__icon i {
  margin-top:-18px;
  color: #000;
}
.privacy-dropdown__option.active .privacy-dropdown__option__icon i {
  margin-top:-18px;
  color: #fff;
}
.privacy-dropdown__option.active:hover .privacy-dropdown__option__icon i {
  margin-top:-18px;
  color: #000;
}
.compose-form__warning {
  background: #E6E9ED;
}
.compose-form__buttons {
  background: #E6E9ED;
}
span.character-counter {
  color: #E6E9ED;
}
.column-header {
  background: #fff;
  border-radius: 4px 4px 0 0;
  color: #434A54;
  font-size:15px;
  border-bottom: solid 1px #656D78;
}
.column-header i, .column-header.active i {
  color: #333;
  font-size:16px;
}
.column-header.active i {
  /*text-shadow: rgba(0,0,0, 0.4) 0 0 10px;*/
}
.column-header__button {
  background-color: #fff;
}
.column-header__button.active {
  background: #fff;
}
.column-header__button.active:hover {
  background: #fff;
}
.column-header__button:hover i, .column-header__button.active:hover i {
  color: #E6E9ED;
  background: #434A54;
  padding: 6px;
  margin: -6px;
  border-radius: 50%;
  width: 17px;
}
.column-header__button i {
  color: #656D78;
  padding: 6px;
  margin: -6px;
  border-radius: 50%;
  width: 17px;
}
.column-header__button.active i {
  color: #E6E9ED;
  background: rgb(43, 144, 217);
  padding: 6px;
  margin: -6px;
  border-radius: 50%;
  width: 17px;
}
.column-header.active {
  -webkit-box-shadow: none;
  box-shadow: none;
}
.column-header.active .column-header__icon {
  color: #000;
  text-shadow: 0 0 10px rgba(0, 0, 0, .25);
}
.column-header__collapsible-inner {
  background: #434A54;
}
.column .scrollable .item-list {
  background: #fff;
}
::-webkit-scrollbar-track {
  background: #CCD1D9;
}
::-webkit-scrollbar-thumb {
  background: #AAB2BD;
}
.status {
  border-bottom: solid 1px #CCD1D9;
}
.status__prepend {
  opacity: .5;
}
.status__prepend-icon-wrapper i {
  color: #AAB2BD;
}
.status__prepend>span {
  color: #AAB2BD;
  font-size: 12px
}
.status__prepend .status__display-name strong {
  color: #AAB2BD;
  font-size: 12px;
  display: inline-block;
  max-width: 75%;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap
}
.account {
  border-bottom: 1px solid #CCD1D9;
}
.account__avatar-overlay-base, .account__avatar-overlay-overlay {
  border-radius: 0;
}
/*
.muted .emojione {
  opacity: 1;
}
*/
.account__display-name strong, .status__display-name strong {
  color: #000;
  font-size: 14px;
}
.display-name__account {
  font-size: 12px;
  color: #656D78;
  opacity: .75;
}
.status.status-direct {
  background: #E6E9ED;
}
.status__relative-time time {
  font-size: 10px;
  color: #656D78;
  opacity: 1;
  padding: 2px 5px;
  margin: -2px 0 2px 5px;
  background-color: #fff;
  border-radius: 0;
}
.reply-indicator__content, .status__content, .status__content.status__content--with-action {
  padding: 15px 0 5px 0;
  margin-right: 0;
  font-size: 13px;
  line-height: 17px;
  color: #333;
  opacity: 1;
  outline: 0;
}
.reply-indicator__content a, .status__content a {
  color: rgb(43, 144, 217);
}
.notification__message>span {
  color: #AAB2BD;
  font-size: 12px;
  opacity: .5;
}
.notification__message .fa {
  opacity: .5;
}
.focusable:focus {
  background: #F0F2F5;
}
.column-header__wrapper.active:before {
  display: block;
  content: "";
  position: absolute;
  top: 35px;
  left: 0;
  right: 0;
  margin: 0 auto;
  width: 100%;
  pointer-events: none;
  height: 28px;
  z-index: 1;
  background: radial-gradient(ellipse, rgba(0, 0, 0, .3) 0, rgba(0, 0, 0, 0) 70%);
}
.reply-indicator__content .status__content__spoiler-link, .status__content .status__content__spoiler-link {
  background: rgb(43, 144, 217);
  color: #fff;
  opacity: .75;
  font-size: 10px;
}
.reply-indicator {
  background: #CCD1D9;
}
.dropdown-menu, .dropdown-menu ul, .dropdown-menu ul li, .dropdown-menu__item a {
  background: rgb(43, 144, 217);
}
.dropdown-menu__arrow.bottom {
  border-bottom-color: rgb(43, 144, 217);
}
.notification.notification-favourite.focusable div {
  outline:0;
}
.column-back-button {
  background: #fff;
  color: #2b90d9;
  cursor: pointer;
  -webkit-box-flex: 0;
  -ms-flex: 0 0 auto;
  flex: 0 0 auto;
  font-size: 15px;
  border: 0;
  text-align: unset;
  padding: 15px;
  margin: 0;
  z-index: 3;
  border-radius: 4px 4px 0 0;
  border-bottom: solid 1px #656D78;
}
.column>.scrollable {
  background: #fff;
}
.account__action-bar {
  border-bottom: solid 1px #656D78;
}
.account__action-bar__tab {
  border-left: 1px solid #656D78;
  text-align: center;
}
.account__action-bar__tab span span {
  color: #CCD1D9;
  opacity: 1;
}
.account__action-bar__tab strong {
  color: #656D78;
  opacity: .5;
  text-align: center;
}
.account__header .account__header__display-name {
  font-size: 18px;
}
.account__header .account__header__username, .account__header__content {
  font-size: 13px;
}
.account--follows-info {
  color: #fff;
  position: absolute;
  top: 10px;
  right: 10px;
  opacity: .75;
  display: inline-block;
  vertical-align: top;
  background-color: rgba(0, 0, 0, .4);
  text-transform: uppercase;
  font-size: 10px;
  font-weight: 500;
  padding: 0 6px;
  border-radius: 2px;
}
.account--action-button button {
  font-size: 20px;
  width: 28px;
  height: 28px;
  line-height: 20px;
}
.column-header__back-button {
  background: #fff;
  font-size: 13px;
}
.column-header__back-button i, .column-header.active div .column-header__back-button i {
  font-size: 13px;
  color: rgb(43, 144, 217);
}
.detailed-status, .detailed-status__action-bar {
  background: #E6E9ED;
}
}
