css:
@-moz-document url-prefix("https://coggle.it/diagram/") {
svg {
    background-color: #333;
}

.node-content{
	color: #e0e0e0  !important;
    text-shadow: none !important;
}

body #xlink-elements {
    opacity: 0 !important;
}

body:hover #xlink-elements {
    opacity: 1 !important;
}

.sagehint {
	text-shadow: 0 0 15px #000000;
    color: #ababab !important;
    opacity: 0 !important;
}

.node-content ul li, .node-content ol li {
	margin: 0.7em 0 0.7em 1.2em;
}

.coggle-workspace #diagram-header #logo {
    background: #fcfcfc;
    padding: 0 5px;
	-webkit-box-shadow: none;
	box-shadow: none;
}
}

