css:
@-moz-document domain("9gag.com")
{
	section#sidebar,
	.post-action-2,
	.post-afterbar,
	.banner-block,
	.what-to-read-next,
	.badge-sticky-button,
	.funoff,
	.featured-tag,
	.outstream-ad-outer-wrapper,
	.instagram-follow,
	.facebook-like,
	.twitter-follow,
	.share,
	.more,
	li.outer,
	.sidebar,
	.col-md-3,
/*[[upload-button]]*/
/*[[tags]]*/
/*[[youtube-post]]*/

	footer
	{
		display: none !important;
	}
	.page
	{
		width: 600px;
		margin: 0 0 32px 600px;
	}
	.main-wrap
	{
		width: 600px;
	}
	section#list-view-2 header 
	{
		margin-left: -50px;
	}
	section#list-view-2 article 
	{
		padding-left: 50px!important;
	}
	.CS3 .collapsed-comment 
	{
		margin-left: 10px;
	}
	#top-nav a.logo 
	{
		padding-left: 16px;
	}
	.nav-wrap 
	{
		width: unset;
	}
	.CS3 .comment-box.indent-1 .payload .textarea-container textarea, .CS3 .comment-box .payload .textarea-container textarea.focus
	{
/*[[comment-box-res]]*/
		transition: all 0.5s cubic-bezier(0, 1, 0, 1);
	}
/*[[section-sidebar]]*/
}

style_setting_options.value:
#top-nav 
{
	height: 48px;
}
div#container, body 
{
	margin-left: 0px;
}
div.section-sidebar 
{
	display: none!important;
	width: 0px!important;
	margin-left: 0px!important;
	padding: 0px!important;
}
body.show-sectionSidebar #container 
{
	margin-left: 0px!important;
}
body.hide-sectionSidebar #container 
{
	margin-left: 0px!important;
}
body.hide-sectionSidebar .section-sidebar 
{
	display: none;
	margin-left: 0px;
}
#top-nav a.drawer-button 
{
	display: none!important;
}
.nav-menu .secondary li 
{
	display: block!important;
	float: left;
}
.nav-menu .secondary li.outer 
{
	display: none!important;
}
