css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.jeuxvideo.com") {
  #content-context {background:pink;}

  .topic-list a {
    color:transparent;
}
  
  .topic-author  a{
    
     color:red;
  }
    .topic-count  {
    
     color:transparent;
  }
  
  
  .topic-list .topic-subject, .topic-list .topic-author, .topic-list .topic-count, .topic-list .topic-count-adm, .topic-list .topic-admin, .topic-list .topic-select, .topic-list .topic-date, text-admin {
    display: table-cell;
    padding: 0.125rem;
     color:transparent;
}
  
 .text-admin, .text-admin:hover {
    color: transparent !important;
}
 
  .topic-list .topic-img {
    position: absolute;
    display: none;
    left: 0.125rem;
    top: 0.125rem;
}
  
  #jv-footer .footer-links {
    background: pink;
    padding: 1.875rem 0;
}
#jv-footer .footer-mentions {
    background-color: pink;
    padding: 1rem 0;
}

 .header-bottom, .header-sticky {
    height: 2.5rem;
    line-height: 2.5rem;
    background-color: pink;
}
 
 .header-top {
    height: 3.375rem;
    background-color: pink;
    vertical-align: middle;
    z-index: 1999999988;
}
 
.container-content {
    background: pink;
    margin: 0 auto;
    padding: 1.25rem 1.25rem 0 1.25rem;
    min-height: 37.5rem;
    min-width: 20rem;
}
  
  
  
  
}
