css:
@-moz-document regexp("^(http://www.routerlogin.net/(?!top)).*$") {


body {
  background: #fff !important;
  font-family: Arial;
  color: #333;
}

/* TOP */

#top {
  left: 0;
  right: 0;
  padding-left: 20px;
  padding-right: 20px;
  background: #222;
}
#top,
#topframe {
  height: 80px;
}

/* MENU */

#menu .basic_button,
#menu .basic_button_purple,
#menu .advanced_purple_button,
#menu .advanced_black_button,
#menu .advanced_white_close_button {
  background: #f5f5f5;
  color: #333;
  transition: background-color .15s, color .15s;
}

#menu span {
  left: 20px !important;
}

#menu .basic_button:hover,
#menu .basic_button_purple:hover,
#menu .advanced_purple_button:hover,
#menu .advanced_black_button:hover,
#menu .advanced_white_close_button:hover,
#menu .basic_button:active,
#menu .basic_button_purple:active,
#menu .advanced_purple_button:active,
#menu .advanced_black_button:active
#menu .advanced_white_close_button:active,

#menu .basic_button_purple,
#menu .advanced_purple_button,
#menu .advanced_white_open_button {
  background: #333;
  color: #f5f5f5;
}

#menu #wps {
  margin-bottom: 30px;
}

.sub_men {
  margin-left: 6px;
  margin-right: 0;
  width: 176px;
}
.sub_men dl,
.sub_men dt {
  width: 100%;
}
.sub_men span {
  text-decoration: none;
}
.sub_men .sub_back_purple,
.sub_men .sub_back_purple_usb,
.sub_men .sub_back_purple_double {
  margin-top: 0;
  background: #333;
  color: #f5f5f5;
}
#readyshare_stor {
  display: none;
}
#usb_basic,
#usb_adv,
#media {
  margin-left: 0;
}
.sub_men .sub_back,
.sub_men .sub_back_purple,
.sub_men .sub_back_purple_usb,
.sub_men .sub_back_purple_double {
  width: 100%;
}

/* LOADING */

.waiting_img_div,
.waiting_img_div:before,
.waiting_img_div:after {
  background: #6e2585;
  -webkit-animation: load1 1s infinite ease-in-out;
  animation: load1 1s infinite ease-in-out;
  width: 1em;
  height: 4em;
}
.waiting_img_div {
  color: #6e2585;
  text-indent: -9999em;
  margin: 88px auto;
  position: relative;
  font-size: 11px;
  -webkit-transform: translateZ(0);
  -ms-transform: translateZ(0);
  transform: translateZ(0);
  -webkit-animation-delay: -0.16s;
  animation-delay: -0.16s;
}
.waiting_img_div:before,
.waiting_img_div:after {
  position: absolute;
  top: 0;
  content: '';
}
.waiting_img_div:before {
  left: -1.5em;
  -webkit-animation-delay: -0.32s;
  animation-delay: -0.32s;
}
.waiting_img_div:after {
  left: 1.5em;
}
@-webkit-keyframes load1 {
  0%,
  80%,
  100% {
    box-shadow: 0 0;
    height: 4em;
  }
  40% {
    box-shadow: 0 -2em;
    height: 5em;
  }
}
@keyframes load1 {
  0%,
  80%,
  100% {
    box-shadow: 0 0;
    height: 4em;
  }
  40% {
    box-shadow: 0 -2em;
    height: 5em;
  }
}

.waiting_img_div img,
html > body > div[align="center"]:not(.waiting_img_div) { /* Loading text */
  display: none;
}

/* HELP */

#help_switch {
  background: #eee;
}
.help_switch_img {
  display: none;
}
#help_switch #help_space,
#help_switch #help_show_hidden,
#help_switch #help_center {
  display: none;
}
.open_help  #help_button,
.close_help #help_button {
  background: none;
  width: 100%;
  height: 30px;
  position: absolute;
  bottom: 0px;
  left: 0px;
  top: 0;
}
.close_help #help_button {
  color: #aaa;
}
.open_help #help_button {
  background: #333;
  color: #fff;
}
.open_help  #help_button::before,
.close_help #help_button::before {
  position: absolute;
  top: 6px;
  left: 20px;
  font-size: 15px;
  content: 'Help';
}

/* FOOTER */

.footer {
  width: calc(100% + 40px);
  left: -20px;
  right: -20px;
  height: 30px;
  background: #eee;
  color: #aaa;
}
.footer b {
  font-weight: normal;
}
.footer_img {
  display: none;
}
.footer #support {
  top: 7px;
  left: 20px;
  line-height: 1;
}
.footer a,
.footer_double a {
  color: inherit;
}
.footer a:hover,
.footer_double a:hover {
  text-decoration: underline;
}
.footer #search {
  top: 3px;
  right: 20px;
}
.footer #search > b {
  display: none;
}
.footer #search input {
  padding: 0 5px;
  line-height: 22px;
  border: 0;
  color: inherit;
}
.footer #search input:hover,
.footer #search input:focus {
  color: #000;
}
#footer #search .search_button {
  position: relative;
  top: 1px;
  background: #f8971d;
  transition: background-color .15s, color .15s;
}
#footer #search .search_button:hover,
#footer #search .search_button:active {
	background: #333;
}

/* CONTENT - GENERAL */

.fix_button {
  background: none;
  height: 28px;
  border-bottom: 2px solid #eee;
}
td[align="center"] {
  text-align: left;
}
img[src="/liteblue.gif"] {
  height: 0;
  margin: 5px 0;
  padding-top: 2px;
  background: #eee;
  overflow: hidden;
}

.page_title {
  color: #6e2585;
}

/* CONTENT - GENERAL - BUTTONS */

.common_bt, 
.common_show_stat_bt, 
.common_el_bt, 
.common_hu_bt,
.apply_bt,
.middle_apply_bt,
.apply_media,
.cancel_bt,
.long_add_bt,
.middle_add_bt,
.add_bt,
.middle_edit_bt,
.edit_bt,
.middle_delete_bt,
.delete_bt,
.common_bt,
.common_big_bt,
.long_common_bt,
.short_common_bt,
.short_refresh_bt,
.common_key_bt,
.speedtest_bt,
.long_add_gray_bt,
.add_gray_bt,
.edit_gray_bt,
.delete_gray_bt,
.long_common_gray_bt,
.common_gray_bt,
.common_big_gray_bt,
.short_common_gr_bt,
.common_key_gray_bt,
.reboot,
.middle_common_bt, 
.common_show_stat_bt {
  width: auto;
  background: #eee;
  color: #333;
  height: 22px;
  line-height: 21px;
  transition: background-color .15s, color .15s, box-shadow .15s;
  padding: 0 20px;
  box-shadow: rgba(0,0,0,.2) 0 0 1px inset;
}

.reboot:hover,
.middle_common_bt:hover, 
.common_show_stat_bt:hover,
.common_bt:hover, 
.common_show_stat_bt:hover, 
.common_el_bt:hover:hover, 
.common_hu_bt:hover,
.apply_bt:hover,
.middle_apply_bt:hover,
.apply_media:hover,
.cancel_bt:hover,
.long_add_bt:hover,
.middle_add_bt:hover,
.add_bt:hover,
.middle_edit_bt:hover,
.edit_bt:hover,
.middle_delete_bt:hover,
.delete_bt:hover,
.common_bt:hover,
.common_big_bt:hover,
.long_common_bt:hover,
.short_common_bt:hover,
.short_refresh_bt:hover,
.common_key_bt:hover,
.speedtest_bt:hover,
.long_add_gray_bt:hover,
.add_gray_bt:hover,
.edit_gray_bt:hover,
.delete_gray_bt:hover,
.long_common_gray_bt:hover,
.common_gray_bt:hover,
.common_big_gray_bt:hover,
.short_common_gr_bt:hover,
.common_key_gray_bt:hover {
  background: #333;
  color: #fff;
  box-shadow: none;
}

.speedtest_bt {
	background: #5eb6e4;
  color: #fff;
}

.apply_bt {
	background: #4ad156;
  color: #fff;
}

/* CONTENT - Basic home */

#form {
  background: none;
  margin-left: 10px;
}
#form_content {
  margin: 0;
}

.basic_icon {
  width: calc(50% - 5px);
  height: 80px;
  margin: 0 5px 5px 0;
  background: #eee !important;
  transition: background-color .15s, color .15s;
}
.basic_icon:hover {
  background: #333 !important;
  color: #fff;
}
.basic_icon::before,
.basic_icon_error::before,
.basic_icon_warning::before {
  display: block;
  background: none;
  width: 0; 
  height: 0; 
  border-bottom: 15px solid transparent;
  border-left: 15px solid #4fc446;
  content: '';
}
.basic_icon_warning::before {
  border-left-color: #f8971d;
}
.basic_icon_error::before {
  border-left-color: #fa0000;
}
.basic_icon .icon_name {
  top: 10px;
  height: auto;
  text-align: left;
  margin-left: 20px;
  color: inherit;
}
.basic_icon .icon_status,
.basic_icon .icon_long_status {
  width: calc(100% - 40px);
  top: 25px;
  text-align: left;
  left: 20px;
  color: inherit;
}
.basic_icon .icon_long_status span {
  top: 9px;
}
.basic_icon .icon_long_status span {
  width: 100%;
}
.basic_icon_warning .icon_status,
.basic_icon_warning .icon_long_status,
.basic_icon_error .icon_status,
.basic_icon_error .icon_long_status {
  background: none;
}
.basic_icon_warning .icon_status b,
.basic_icon_warning .icon_long_status b {
  color: #f8971d;
  font-size: inherit;
}
.basic_icon_error .icon_status b,
.basic_icon_error .icon_long_status b {
  color: #fa0000;
  font-size: inherit;
}
/* CONTENT - Advanced home */

#content_icons {
  width: 100%;
}
.adv_icon {
  margin-bottom: 5px;
  background: #eee;
}
.adv_icon .title {
  margin-bottom: 4px;
  border-bottom: 1px solid #fff;
  background: none;
  transition: background-color .15s, color .15s;
}
.adv_icon .title:hover,
.adv_icon .title:active {
  background: #333;
}
.adv_icon .title b {
  top: 9px;
  color: #333;
  text-decoration: none;
}
.adv_icon .title:hover b,
.adv_icon .title:active b {
  color: #fff;
}
.img_status_good,
.img_status_warning,
.img_status_error {
  background: none;
  margin: 0 5px 0 0;
  width: 0; 
  height: 0; 
  border-bottom: 15px solid transparent;
  border-left: 15px solid #4fc446;
}
.img_status_warning {
  border-left-color: #f8971d;
}
.img_status_error {
  border-left-color: #fa0000;
  border-bottom-width: 15px;
  border-left-width: 15px;
}
.adv_icon tr td {
  width: 50%;
}
.adv_icon tr td b {
  display: inline-block;
  max-width: 130px;
}
.advanced_icon_line {
  width: calc(100% + 4px);
  left: -2px;
  right: -2px;
  background: #fff;
}
.subhead_blue {
  position: relative;
  left: 5px;
  line-height: 13px;
  font: bold 12px arial;
  color: #4fc446;
}

.advanced_page_line {
  display: none;
}

/* CONTENT - Advanced home - BUTTONS */ 

.reboot {
  margin: 7px 6px;
}
.reboot,
.middle_common_bt, 
.common_show_stat_bt {
  background: #ddd;
}


}

@-moz-document url-prefix("http://www.routerlogin.net/help") {


body {
  background: #eee !important;
}

b, strong {
  color: #6e2585;
}


}

@-moz-document url-prefix("http://www.routerlogin.net/top.html") {


body {
  background: #222;
  font-family: Arial;
}

#logo {
  background: transparent !important;
  font-weight: bold;
  font-size: 1.9rem;
  margin-top: 8px;
}
#logo::before {
  color: #743d86;
  content: 'NETGEAR';
}

#labels {
  top: 50px;
}
.label_unclick .label_left,
.label_unclick .label_right,
.label_click .label_left,
.label_click .label_right {
  display: none;
}
.label_unclick .label_middle,
.label_click .label_middle,
.logout_bt {
  background: #333 !important;
  color: #eee;
  padding: 0 15px;
  cursor: pointer;
  transition: background .15s, color .15s;
}
.label_click b, .label_unclick b {
  line-height: 32px;
}
.label_unclick .label_middle:hover,
.label_click .label_middle:hover {
  background: #fff !important;
  color: #000;
}

#basic_label.label_unclick .label_middle,
#advanced_label.label_unclick .label_middle {
	box-shadow: rgba(0,0,0,.15) 0 -2px 2px inset;
}
#basic_label.label_click .label_middle,
#advanced_label.label_click .label_middle {
  background: #fff !important;
  color: #000;
}

.update_info_down {
  top: 53px;
}
#update_info_left,
#update_info_right {
  display: none;
}
#update_info_middle {
  background: none;
  color: #777;
  padding: 0;
}
#firm_version {
  top: -16px;
  line-height: 22px;
  color: #777;
  font-weight: normal;
}
#router-name {
  top: 24px;
  left: auto;
  right: 0;
  color: #777;
  font-size: 15px;
  font-weight: normal;
}

#select_language {
  top: 53px;
  right:80px;
}
#select_language #language {
  background: #000;
  color: #888;
  border: 0;
  transition: color .15s;
}
#select_language #language:hover {
  color: #fff;
}
.logout_bt {
  top: 53px;
  width: auto;
}
.logout_bt:hover {
  background: #c00 !important;
}


}
