css:
@-moz-document domain("wiki.d-addicts.com"), domain("d-addicts.com") {
body,div#content,#mw-page-base,table,div.vectorTabs ul li,.catlinks,table.wikitable > * > tr > th,#wrap,.navbar,ul.forums,div.thumbinner,#toc, .toc, .mw-warning {background: #252525 !important;}
table {border: 0 !important;}
h3,div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited,tbody tr td div,tbody tr td,h2,div#content,div#footer ul li,table.wikitable {color: #cccccc !important;}
a,div.vectorTabs li a,div#mw-panel div.portal div.body ul li a,#mw-panel.collapsible-nav .portal h3 a,#mw-panel.collapsible-nav .portal.collapsed h3 a,.mw-body a.external,.mw-body a.extiw, .mw-body a.extiw:active {color: #4a8cf9}
a:hover {color: #eeeeee !important;}
a:visited {color: #6edaf3;}
div#content h1, div#content #firstHeading {font-family: segoe ui, segoe ui light, arial, tahoma; font-weight: 100; color: #cccccc;}
div.vectorTabs li.new a, div.vectorTabs li.new a:visited {color: #ef5757;}
tbody tr td h2 {background: #0b63af !important; font-family: segoe ui, segoe ui light, arial, tahoma !important; font-weight: 500 !important;}
.mw-body a.external:visited {color: #f575f5;}
a.new, #p-personal a.new {color: #f35151;}
#airing,#SeasonRing table tr,li.row:hover,li.row:hover a {background: #294882 !important; color: #eeeeee !important;}
#SeasonRing table tr b,#DramaRing table tr b {color: #757575;}
#mw-content-text table tbody tr th {color: #757575;}

body,label,dt,dd,dl.details dd {color: #6a96e4;}
ul.topiclist li,.copyright,.postprofile,dd label,dl.faq dt,.post:target .content,.post:target h3 a,fieldset dt,fieldset dd,dl.details dt {color: #cccccc;}
.panel-container .panel,fieldset.quick-login input.inputbox {background: #252525;}
.content,.panel p,.inputbox:focus,#message-box textarea,.search-box .inputbox {color: #cccccc;}
fieldset select,.inputbox {background: #252525; color: #cccccc;}
.search-header .inputbox::placeholder {color: #aaaaaa;}
form fieldset input {color: #757575;}
a.button2, a.button2:link {color: #656565;}
li.row strong,.postprofile strong,p.author,.notice,.signature {color: #aaaaaa !important;}
.bg1,.bg2 {background: #252525 !important;}
.panel {background: #404040;}
blockquote,.codebox,blockquote blockquote blockquote,blockquote blockquote blockquote blockquote blockquote {background: #404040;}
blockquote blockquote,blockquote blockquote blockquote blockquote,blockquote blockquote blockquote blockquote blockquote blockquote {background: #505050;}
}
