css:
@-moz-document domain("www.resetera.com") {
#headerProxy, .nodeList.sectionMain,.footerLinks, #navigation .pageContent, #searchBar, .nodeListNewDiscussionButton
{
display: none !important;
}

#logo, #navigation, .samCodeUnit.aboveTopBreadcrumb
{
display: none !important;
}



.listBlock.stats.pairsJustified, .major, .minor, .lastPost, .postDate, #content > div > div > div.discussionList.section.sectionMain > form.DiscussionList.InlineModForm > dl, #pageDescription, .messageMeta.ToggleTriggerAnchor, .userBanner.bannerHidden.wrapped, .sectionFooter.InlineMod.SelectionCountContainer, #DiscussionListOptionsHandle, .threadListSeparator, .fa.fa-circle, .fa.fa-gamepad, .fa.fa-comments, .fa.fa-arrows-alt, span.video, span.videob, span.startDate, .posterDate > a:first-child, span.itemPageNav span:first-child, .linkGroup > span, .fa.fa-arrow-down
{
display: none !important;
}

.discussionListItem.visible.sticky, #content > div > div > div:nth-child(5) > div:nth-child(1) > label > a
{
display: none !important;
}



div.titleText h3.title > a:nth-child(2)
{
display: none !important;
}

.breadcrumb
{
position: relative !important;
left: -.3rem !important;
}

.titleBar {
display: none !important;
}

div.titleText
{
    padding-left: 0px !important;
}

.secondRow, .titleText h3 {
display: inline-block !important;
vertical-align: top !important;
}

body .itemPageNav a
{
    padding-bottom: 0px !important;
}

.videob
{
    padding-left: 0px !important;
}

.secondRow
{
    line-height: 24px !important;
    padding-left: .6em !important;
}

.discussionList.section.sectionMain
{
    margin-top: 20px !important;
}

.titleBar
{
    margin-bottom: 0px !important;
}

div.titleText h3.title > a:first-child::after
{
content: " -";
padding-left:.6em !important;
}

ol.discussionListItems > li
{
margin-bottom: 10px !important;
border-bottom: 1px solid #999 !important;
}

.pageWidth
{
width: /*[[pagewidth]]*/% !important;
}

.titleText
{
display: flex !important;
}

.itemPageNav a, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, .node .nodeLastPost
{
border-style: none !important;
}

.section
{
    margin: 0px !important;
}

}
