css:
@-moz-document domain("avenoel.org") {
.topic-message {
    background: #f9f7ed;
    padding: 10px;
    padding-bottom: 5px;
    margin: 10px;
    /*border-radius: 0px 15px 15px 15px;*/
    border-radius: 3px;
    border: 1px solid #f7d9d7;
}
.topic-message.odd {
    background: #E8F0F3;
    border: 1px solid #b8e2f2;
}



/* Bloc sur le téco avec l'avatar */
.topic-message .message-aside {
    width: 30px;
    margin-right: 15px;
    flex: auto;
    padding-bottom: 0;
    padding-right: 0;
}
.message-avatar img {
    width: 60%;
    height: 60%;
    border-radius: 0%;
}



/* Les infos nb de messages, points etc... */
.topic-message aside .message-infos {
    font-size: 12px;
    margin-bottom: 0px;
}
.topic-message aside .message-infos u {
    color: #7f7f7f;
    text-decoration: none;
    font-size: 10px;
}
/*
.topic-message aside .message-infos u::after{
    content: "test";
}
*/



/* Header avec le pseudo et la date */
.topic-message .message-header {
    position: relative;
    padding-bottom: 45px;
    border-bottom: 1px dotted #f7d9d7;
}
.odd .message-header {
    padding-bottom: 45px;
    border-bottom: 1px dotted #b8e2f2;
}
.topic-message .message-header .message-date {
    display: block;
    position: absolute;
    bottom: -10px;
    margin-top: 10px;
    font-size: 11px;
    color: #7f7f7f;
}
.topic-message .message-header .message-username a {
    text-decoration: none;
    font-size: 16px;
    font-weight: 500;
    color: #303030;
}
.topic-message .message-header .message-actions {
    float: right;
    margin-left: 10px;
}
.topic-message .message-header .message-actions img, .topic-message .message-header .message-actions input[type="image"] {
    border-radius: 50%;
}



/* Permalink */
.message-permalink {
    position: absolute;
    bottom: 0;
    color: #848484;
    text-decoration: none;
    font-size: 12px;
    font-family: "Roboto", sans-serif;
}


/* Message edité */
.topic-message .message-footer .message-edited {
    color: #848484;
    font-size: 11px;
    line-height: 1;
}

.topic-message .message-content {
    min-height: 170px;
}



/* Supprimer l'espace autour des avatars pour avoir des avatar collés au bord du bloc de message */
.topic-message aside {
    padding-left: 0;
    padding-top: 0;
}
.topic-message {
    padding-left: 0;
    padding-top: 0;
}
.topic-message aside .message-infos {
    margin-left: 10px;
}


/* BOUTONS SPOILER */
.spoiler {
    margin-top: 5px;
}
.spoiler .spoiler-btn {
    visibility: hidden;
    margin-left: -50px;
}
.spoiler .spoiler-btn:after {
    visibility: visible;
    content: "SPOILER";
    background: #f44e4e;
    color: #ffcece;
    font-weight: bold;
    padding: 5px;
    border-radius: 30px;
    box-shadow: 2px 2px 3px #888888;
    text-shadow: -1px -1px white, 1px 1px #444;
    border: 1px solid #ba1818;
}
.spoiler .spoiler-content {
    border: 1px dotted #2f6fab;
    border-radius: 10px;
}


/* Formulaire de réponse */
.bbcodes {
    text-align: left;
    margin: 0;
    border: 1px solid #d8d8d6;
    border-radius: 15px 15px 0px 0px;
    background: #d8d8d6;
}
.post-content {
    border-radius: 0 0 15px 15px;
    border: 1px solid #d8d8d6;
}


/* Overlay risibank */
.closebtn img {
    padding: 5px;
    border-radius: 50%;
    background: white;
    cursor: pointer;
}

.risi-sticker {
    padding: 5px;
    margin: 5px;
    border-radius: 0;
}

.risi-img, .risi-fimg, .risi-rimg {
    border-radius: 2px;
}


/* Citations */
.message-content-quote{
    display: block;
    padding: 5px;
    padding-bottom: 0px;
    padding-top: 0px;
    background: #F9ECE5;
    border-left: 5px solid #f7d9d7;
    color: #F9ECE5;
    color: #f7d9d7;
    font-size: 20px;
    line-height: 0px;
    letter-spacing: normal;
}


.message-content-quote a{
    padding: 10px;
    display: inline-block;
    font-size: 16px;
    color: #7a7877;
    font-weight: 600;
    letter-spacing: normal;
    text-decoration: none;
}

.odd .message-content-quote{
    background: #D6EAF3;
    color: #D6EAF3;
    color: #b8e2f2;
    border-left: 5px solid #b8e2f2;
}
.message-content-quote + br{
    display: none;
}
.message-content-quote i {
    font-size: 12px;
    color: #848484;
    letter-spacing: normal;
    margin-left: 0px;
    border-left: 0px dotted #f7d9d7;
    padding-left: 5px;
    line-height: 20px;
}

.odd .message-content-quote i {
    border-left: 0px dotted #b8e2f2;
}



/* Liste des topics */
.table > thead > tr > th, .table > thead > tr > td, .table > tbody > tr > th, .table > tbody > tr > td, .table > tfoot > tr > th, .table > tfoot > tr > td {
    padding: 2px;
    line-height: 1.3;
    vertical-align: top;
    border-top: 1px solid #ddd;
    font-size: 12px;
    font-weight: 500;
}
.topics-author {
    font-weight: 400;
}

}
