css:
@-moz-document domain("osu.ppy.sh") {
/* 
new.ppy.sh dark theme
Made by donacdum
Contact me at:
osu: https://osu.ppy.sh/users/8705944
discord: donacdum#5061
*/

/* Coppertine's additions */
table
{
    border-collapse: inherit;
}
.beatmap-scoreboard-table__body-row--highlightable:nth-child(odd)
.beatmap-scoreboard-table__cell
{
    background-color: #323232;
}
.beatmap-scoreboard-table__body-row--highlightable 
.beatmap-scoreboard-table__cell {
    background-color: #191919;
}
.beatmap-score-top__username,
.beatmap-score-top__stat-value,
.beatmap-scoreboard-table__user-link,
.bbcode-spoilerbox__link,
.bbcode-spoilerbox__arrow
{
    color:rgb(255,255,255)
}

.beatmap-score-top__section,
.beatmap-score-top__section--bottom,
.beatmap-success-rate,
.beatmapset-panel__panel,
.beatmap-packs__list,
.page-extra,
.changelog-build,
.changelog,
.osu-layout__row,
.js-forum-post,
.forum-post__body,
.forum-post-content,
.forum-post__content--signature,
.forum-post__content--reply-tabs,
.forum-post__content--edit-bar,
.bbcode-size-select__select,
.js-bbcode-btn--size,
.action-forum-forums-index,
.t-section-community,
.u-forum--hover-area,
.osu-page--small,
.stg-quote,
.stg-header__button,
.osu-page--stg-block,
.stg-block,
.stg-block__perks,
.stg-perk__text,
.stg-perk,
.beatmapset-info__description, 
.well, 
.bbcode {
    background: rgb(50, 50, 50);
    color: rgb(255, 255, 255)
}

/* end of Coppertine's additions */

.logo__main {
    background-image: url(https://i.imgur.com/osRlXwf.png)
}
.u-section-bg {
    background: rgb(0, 0, 0);
    color: rgb(0, 0, 0)
}
.bg__triangles--1 {
    background: rgb(0, 0, 0) !important;
    background-color: rgb(0, 0, 0) !important;
    color: rgb(0, 0, 0) !important
}
.bg__triangles--2 {
    background: rgb(0, 0, 0) !important
}
.bg__triangles--3 {
    background: rgb(0, 0, 0) !important
}
.page-tabs__tab--active {
    background: rgb(25, 25, 25);
    color: rgb(255, 0, 0)
}
.bg__gradient-overlay {
    background-image: linear-gradient(rgb(25, 25, 25), rgb(50, 50, 50)) !important
}
.t-section-beatmaps .u-section--gradient-down {
    background-image: linear-gradient(rgb(25, 25, 25), rgb(50, 50, 50)) !important
}
.well,
.beatmapset-info__description,
.beatmapset-scoreboard,
.page-tabs__tab,
.osu-page--generic,
.beatmapset-info__box,
.beatmapset-info,
.beatmapsets,
.beatmap-pack__body,
.beatmap-pack-items__link,
.forum-post__info-panel-extra,
.bbcode-size-select,
.js-store-item-quantity {
    background: rgb(25, 25, 25);
    color: rgb(255, 255, 255)
}
.beatmap-score-top__section,
.beatmap-score-top__section--bottom,
.beatmap-success-rate,
.beatmapset-panel__panel,
.beatmap-packs__list,
.page-extra,
.changelog-build,
.changelog,
.osu-layout__row,
.js-forum-post,
.forum-post__body,
.forum-post-content,
.forum-post__content--signature,
.forum-post__content--reply-tabs,
.forum-post__content--edit-bar,
.bbcode-size-select__select,
.js-bbcode-btn--size,
.action-forum-forums-index,
.t-section-community,
.u-forum--hover-area,
.osu-page--small,
.stg-quote,
.stg-header__button,
.osu-page--stg-block,
.stg-block,
.stg-block__perks,
.stg-perk__text,
.stg-perk {
    background: rgb(50, 50, 50);
    color: rgb(255, 255, 255)
}
.osu-layout__section,
.beatmap-packs__row {
    background: rgb(25, 25, 25);
}
::selection {
    background: rgb(50, 50, 50);
}
.forums {
    background: rgb(50, 50, 50);
    color: rgb(0, 0, 0);
    background-color: rgb(50, 50, 50)
}
.left::focus {
    background: rgb(50, 50, 50);
    color: rgb(0, 0, 0)
}
.left {
    background-color: rgb(50, 50, 50) !important
}
.right {
    background-color: rgb(75, 75, 75) !important
}
.forums__hover-bar {
    z-index: 99 !important
}
.forum-topic-entry::hover,
.forum-topic-entry__bg::hover,
.u-forum--hover-area::hover {
    background-color: rgb(75, 75, 75) !important;
    color: rgb(75, 75, 75) !important;
    background: rgb(75, 75, 75) !important
}
.js-forum-topic-entry::hover {
    background-color: rgb(75, 75, 75) !important;
    color: rgb(75, 75, 75) !important;
    background: rgb(75, 75, 75) !important
}
.t-forum-category-beatmaps .u-forum--link,
.t-forum-category-beatmaps .u-forum--bg,
.forums__hover-bar {
    color: red !important;
}
.t-forum-category-beatmaps .u-forum--bg {
    background: red !important
}
.t-forum-category-beatmaps .u-forum--link::hover {
    color: red !important;
}
.t-forum-category-beatmaps .u-forum--bg-link {
    background-color: red !important
}
.beatmap-scoreboard-table__body-row:nth-child(odd) > td {
    background-color: #131010;
}
.beatmap-scoreboard-table__user-link{
    color: #c5c5c5;
}
.beatmap-score-top__username{
    color: #c5c5c5;
}
.beatmap-score-top__stat-value{
    color: #c5c5c5;
}
.comment-editor__message{
    color: #f7f7f7;
}
.comments {
    background-color: #484848;
}
.comments__items {
    background-color: #484848;
    color: #fff
}
.comment {
    color: #fff;
}
.comment__message {
    color: #fff;
}
}
