css:
@-moz-document domain('trello.com') {
    /* ========================================= */
    @font-face {
        font-family: 'Samim';
        src: local('Samim');
        src: url('https://cdn.rawgit.com/rastikerdar/samim-font/v2.0.1/dist/Samim.eot');
        src: url('https://cdn.rawgit.com/rastikerdar/samim-font/v2.0.1/dist/Samim.eot?#iefix') format('embedded-opentype'),
             url('https://cdn.rawgit.com/rastikerdar/samim-font/v2.0.1/dist/Samim.woff') format('woff'),
             url('https://cdn.rawgit.com/rastikerdar/samim-font/v2.0.1/dist/Samim.ttf') format('truetype');
        font-style: normal;
    }
    /* ========================================= */
    /* font size (cards etc) */
    html,
    body,
    input,
    select,
    textarea {
        font: 12px Samim, tahoma;
    }
    #notification {
        text-align: center !important;
        right: auto;
        left: 1%;
        max-width: 98%;
    }
    .board-list .js-add-board,
    .member-boards-view .window-module .js-add-org-boards-page {
        direction: ltr;
    }
    /** Search **/
    .header-search .header-search-icon,
    .header-search .header-search-icon-close {
        left: 0;
        right: auto;
    }
    .header-search .header-search-icon-open {
        left: 25px;
        right: auto;
    }
    .js-search-boards,
    .header-search .header-search-input {
        right: 0;
        left: auto;
        direction: rtl;
    }
    input.header-search-input.focus[type="text"],
    input.header-search-input.focus[type="text"]:hover {
        padding-right: 7px;
        padding-left: 54px;
    }
    .sidebar-boards-list li.selected .details {
        padding-left: 36px;
        padding-right: 10px;
    }
    .search-with-spinner .js-search-input {
        direction: ltr;
    }
    .board-menu {
        right: auto;
        left: 0;
        transform: translateX(-339px);
    }
    /**************** HOME  ******************/
    /** titles **/
    .window-module {
        direction: rtl;
    }
    .window-module-title {
        margin: 0 38px 4px 0;
    }
    .window-module-title-icon {
        left: auto;
        right: -38px;
    }
    /** boards **/
    .board-list li {
        float: right;
    }
    .board-list-item-name {
        padding-right: 10px;
    }
    .board-list .details {
        padding-right: 9px;
        padding-left: 36px;
    }
    /****************** Search page *****************/
    .search-page .header-search .header-search-icon {
        left: 20px;
    }
    /******************* Profile **********************/
    .has-factoid .tabbed-pane-main-col-wrapper .window-module {
        direction: ltr;
    }
    /**************** BOARD ****************************/
    #board {
        direction: rtl;
    }
    .board-wrapper.is-show-menu .board-canvas {
        margin-left: 371px;
        margin-right: 0;
    }
    .board-wrapper.is-show-menu.sidebar-display-wide .board-canvas {
        margin-left: 500px;
        margin-right: 0;
    }
    /** Board header */
    .board-header .board-header-btn-org-name,
    .board-header .board-header-btn-name,
    .board-header .board-header-btns.mod-left {
        float: right !important;
        direction: rtl;
    }
    .board-header .board-header-btns.mod-right {
        float: left !important;
    }
    .board-wrapper.is-show-menu .board-header {
        margin-left: 371px;
        margin-right: 0;
    }
    .board-wrapper.is-show-menu.sidebar-display-wide .board-header {
        margin-left: 500px;
        margin-right: 0;
    }
    .mod-show-menu .board-header-btn-icon {
        left: auto;
        right: 0;
        transform: scale(-1);
    }
    .mod-show-menu .board-header-btn-text {
        left: 0;
        right: auto;
    }
    .mod-show-menu {
        padding-left: 5px;
        padding-right: 20px;
        margin: 0;
    }
    /** sidebar */
    .sidebar-hide-btn {
        right: -32px;
    }
    .board-wrapper.is-show-menu .board-widgets {
        right: auto;
        left: 0;
    }
    .board-widgets {
        width: 350px;
        transform: translateX(-400px);
        left: 0;
        right: auto;
    }
    .sidebar-hide-btn {
        left: auto;
        right: -32px;
    }
    .sidebar-hide-btn:hover {
        right: -28px;
    }
    .sidebar-hide-btn .icon-sm {
        transform: scale(-1);
        margin: 6px 6px 0 0;
    }
    /**************** LIST container of cards *****************/
    .list-header-menu-icon {
        right: auto;
        left: 0;
    }
    .board-list-header-link {
        float: left;
    }
    .list-header {
        padding: 8px 10px 8px 34px;
    }
    .pop-over-list .js-select-list,
    .pop-over-content input,
    .pop-over-content textarea,
    .js-pos-value,
    .js-board-value {
        direction: rtl;
    }
    .js-close-board:before,
    .js-close-list:before {
        content: "x";
        color: red;
        margin-right: 10px;
    }
    /**************** CARDS *****************/
    .open-card-composer {
        direction: ltr;
    }
    .list-card-operation {
        right: auto;
        left: 0;
    }
    .list-card-labels .card-label {
        float: right;
    }
    /** quick editor **/
    .quick-card-editor {
        direction: rtl;
    }
    /**************** Card Editor ************/
    .window-main-col {
        float: right;
    }
    .window-sidebar {
        float: left;
        position: unset !important;
        padding: 8px 0 12px 12px;
        direction: ltr;
    }
    .window-sidebar h3 {
        direction: ltr;
    }
    .js-url.js-autofocus.inline-input,
    #boardEmail,
    .button-link {
        direction: ltr;
    }
    .dialog-close-button,
    .comment-box-options {
        right: auto;
        left: 6px;
    }
    .window-header {
        direction: rtl;
    }
    .window-header-icon {
        position: absolute;
        right: -28px;
        left: auto;
    }
    .window-archive-banner-icon {
        right: 12px;
        left: auto;
    }
    /** New Comment **/
    .new-comment {
        margin: 0 38px 20px 0;
    }
    .new-comment .member {
        right: -38px;
        left: 0;
    }
    /** Details **/
    .card-detail-item .markeddown,
    .card-detail-edit {
        direction: rtl;
    }
    .card-detail-data {
        margin-right: 30px;
        margin-left: 0;
    }
    /** action log */
    .phenom-action .phenom-desc,
    .phenom-action {
        direction: ltr;
    }
    /** comments */
    .phenom-comment .phenom-meta {
        direction: ltr;
        display: block;
        widows: 100%;
    }
    .phenom-comment .creator {
        left: auto;
        right: 0;
    }
    .phenom-comment .inline-member {
        direction: rtl;
    }
    .phenom-comment .phenom-desc {
        direction: rtl;
    }
    .phenom-comment {
        padding: 10px 42px 10px 0;
    }
    /** checklist */
    .checklist-item {
        padding: 0 38px 4px 0;
    }
    .checklist-item-checkbox {
        left: auto;
        right: 5px;
    }
    .checklist-progress-bar-current {
        left: auto;
        right: 0;
    }
    .window-module-title-options {
        float: left;
        direction: ltr;
    }
    .checklist-new-item {
        margin-left: 0;
        margin-right: 38px;
    }
    /** attachment */
    .js-attachments-section .window-module-title {
        margin: 0 0 4px 38px;
    }
    .js-attachments-section .window-module-title-icon {
        left: -38px;
        right: auto;
    }
    .attachment-viewer-header-close-icon {
        right: auto;
        left: 10px;
    }
    .js-filter-emoji,
    .js-filter-mentionables,
    .js-short-url,
    .datepicker-select-input,
    .js-attachments-section,
    #addLink {
        direction: ltr !important;
    }
    /**************** Settings Page *****************/
    .tabbed-pane-header-image {
        float: right;
    }
    .tabbed-pane-header-factoid {
        right: auto;
        left: 32px;
    }
    .has-factoid .tabbed-pane-header-details {
        direction: rtl;
        margin-left: 0;
        margin-right: 100px;
    }
    .js-profile-form input[name="name"],
    .js-profile-form input[name="website"] {
        direction: ltr;
    }
    .tabbed-pane-header-details-edit {
        float: right;
    }
    /**************** ADD A CARD LINK *****************/
    /* empty list fix */
    /**************** CARD Labels *****************/
    /**************** CARD MEMBERS *****************/
    /**************** CARD CONTENT (back side) *****************/
    /* labels */
    /* card content buttons */
    /* More links */
    /* button for comment etc */
    /* image/attachment icon and background */
    /**************** TOGGLE BAR *****************/
    /* toggle arrow on the right */
    /* toggle bar */
    /**************** WEBKIT SCROLLBARS *****************/
    .extra-large-window.layout-horiz-scroll .board-wrapper.disabled-list-guide .list-guide,
    .large-window.layout-horiz-scroll .board-wrapper.disabled-list-guide .list-guide {
        display: none;
    }
    .extra-large-window.layout-horiz-scroll .board-wrapper.disabled-list-guide #board,
    .large-window.layout-horiz-scroll .board-wrapper.disabled-list-guide #board {
        margin-left: 8px;
        border-radius: 3px;
    }
    .extra-large-window.layout-horiz-scroll #board,
    .large-window.layout-horiz-scroll #board {
        border-top-left-radius: 0;
        border-top-right-radius: 3px;
        border-bottom-right-radius: 3px;
        border-bottom-left-radius: 0;
        margin-left: 155px;
    }
    .extra-large-window.layout-horiz-scroll .list-area-wrapper,
    .large-window.layout-horiz-scroll .list-area-wrapper {
        overflow-x: scroll;
        overflow-y: hidden;
    }
    .extra-large-window.layout-horiz-scroll .list-area-wrapper::-webkit-scrollbar,
    .large-window.layout-horiz-scroll .list-area-wrapper::-webkit-scrollbar {
        height: 13px;
        width: 13px;
    }
    .extra-large-window.layout-horiz-scroll .list-area-wrapper::-webkit-scrollbar-thumb:vertical,
    .large-window.layout-horiz-scroll .list-area-wrapper::-webkit-scrollbar-thumb:vertical,
    .extra-large-window.layout-horiz-scroll .list-area-wrapper::-webkit-scrollbar-thumb:horizontal,
    .large-window.layout-horiz-scroll .list-area-wrapper::-webkit-scrollbar-thumb:horizontal {
        background: #888a85;
        border-color: transparent;
    }
    .extra-large-window.layout-horiz-scroll .list-area-wrapper::-webkit-scrollbar-track-piece,
    .large-window.layout-horiz-scroll .list-area-wrapper::-webkit-scrollbar-track-piece {
        background: rgba(245, 245, 245, 0.16);
    }
    .extra-large-window.layout-horiz-scroll .list-area-wrapper::-webkit-scrollbar-button,
    .large-window.layout-horiz-scroll .list-area-wrapper::-webkit-scrollbar-button {
        display: block;
        height: 2px;
        width: 2px;
    }
    .extra-large-window.layout-horiz-scroll .list-guide,
    .large-window.layout-horiz-scroll .list-guide {
        display: block;
        left: 8px;
        position: absolute;
        top: 42px;
    }
    .extra-large-window.layout-horiz-scroll.window-up .list-guide,
    .large-window.layout-horiz-scroll.window-up .list-guide {
        -webkit-box-shadow: none;
        box-shadow: none;
    }
    /* ========================================= */
    @media only screen and (min-width:751px) and (max-width:900px),
    only screen and (min-width:901px) and (max-width:1280px),
    only screen and (min-width:1281px) {
        .board-wrapper.is-show-menu .board-main-content {
            margin-right: 0px;
        }
    }
    .js-list-actions {
        direction: ltr;
    }
    .list-wrapper {
        width: 300px;
    }
    .list-wrapper:first-child {
        margin-left: 5px;
    }
    /* ========================================= */
    body.body-board-view {
        background-color: rgb(0, 98, 154)!important
    }
    .board-menu {
        margin-top: 0!important;
        position: fixed!important;
        z-index: 60!important;
    }
    .list {
        border-radius: 5px!important;
    }
    .list-card-title {
        font-size: 13px;
        line-height: 1.7em;
    }
    .list-card-operation {
        right: auto;
        left: 3px;
        padding: 2px;
    }
    .checklist-item-details-text {
        direction: rtl;
    }
    .card-label.mod-card-front {
        float: right;
    }
    .board-wrapper.is-show-menu .board-header-btns.mod-right {
        visibility: visible;
        right: -303px;
    }
    .header-logo-default:after {
        content: "RTL";
        color: white;
        top: 21px;
        position: absolute;
    }
    .checklist-item-checkbox {
        right: 5px !important;
        left: inherit;
    }
    .checklist-item {
        padding-right: 26px!important;
    }
    .comment-container p {
        direction: rtl;
    }
}
