css:
@-moz-document domain("blockchair.com") {
html, body{
     background: #2d2d2d;   
}

a {
    color: #A42EFF;
    font-weight: 400;
}

a:hover{
    color: #e5f3ff;    
}

li, span{
    font-weight: 400;
	color: #e0e0e0;   
}


.navbar{
 background: black !important;   
}

.jumbotron>div>div>div>img, .jumbotron>div>div>div>a>img, .logo-container>a>img{
    content:url(https://cdn.discordapp.com/attachments/372492967895629836/379129665647804426/TB_blockchair2-01invert.png) !important;
}

.jumbotron-pattern { background:url(data:image/svg+xml;utf8,%3Csvg%20xmlns%3D%27http%3A//www.w3.org/2000/svg%27%20width%3D%2756%27%20height%3D%27100%27%3E%0A%3Crect%20width%3D%2756%27%20height%3D%27100%27%20fill%3D%27%23000%27%3E%3C/rect%3E%0A%3Cpath%20d%3D%27M28%2066L0%2050L0%2016L28%200L56%2016L56%2050L28%2066L28%20100%27%20fill%3D%27none%27%20stroke%3D%27%23222%27%20stroke-width%3D%272%27%3E%3C/path%3E%0A%3Cpath%20d%3D%27M28%200L28%2034L0%2050L0%2084L28%20100L56%2084L56%2050L28%2034%27%20fill%3D%27none%27%20stroke%3D%27%23222%27%20stroke-width%3D%272%27%3E%3C/path%3E%0A%3C/svg%3E);
}

.bc-logo-chair{
 content:url(https://cdn.discordapp.com/attachments/214201445132664832/389698100031127564/blockchair_logo_invert.png);   
}

.chessboard, p, h3, h2, .text-center{
	color: #e0e0e0;       
}

.btn-default {
    color: #fff;
    background-color: #000;
    border-color: #6d6d6d;
}

.btn-group>.btn:hover {
    z-index: 2;
    color: white;
    border-color: blueviolet;
}

tr, th, .table-striped>tbody>tr:nth-of-type(odd),.input-group-addon:first-child, .input-group .form-control:last-child, .input-group-addon:last-child, .input-group-btn:first-child>.btn-group:not(:first-child)>.btn, .input-group-btn:first-child>.btn:not(:first-child), .input-group-btn:last-child>.btn, .input-group-btn:last-child>.btn-group>.btn, .input-group-btn:last-child>.dropdown-toggle {
    background-color: #131313;
    color: #e0e0e0;
}

.table-hover>tbody>tr:hover{
	background-color: #131313;
    color: #e0e0e0;
}

.th-simple>th, h5 {
    font-weight: 500;
    color: #d56bff;
}

.c34-stripes, .c4-stripes, .c3-stripes{
    background: black !important;
    border-color: red;
}

.open>.dropdown-toggle.btn-default{
	background: none;   
}

.dropdown-menu, .dropdown-menu>li>a{
	background-color: black;
    color: #e0e0e0;   
}

.divider::after{
    display: none;
    background-color: black;
    background: none !important;
    color: #d56bff;
}

.dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover{
    color: #e8e8e8;
    text-decoration: none;
    background-color: #317c98;  
}

.mark, mark{
    background: none !important;
    color: #d56bff;
}

.color3 {
    background-color: #54dc1c;
    color: black;
    font-weight: bold;
}

.search-time {
    font-size: 1em;
    margin-bottom: 5px;
    color: #e0e0e0;
    font-weight: 500;
}

.remainder {
    font-size: 0.8em;
    color: #8c8c8c;
}

.colorW {
    background-color: #83f796;
    color: black;
    font-weight: bold;
    border: 1px #d4d4d4 dotted;
}

.color2 {
    background-color: #ff0078;
    color: black;
    font-weight: bold;
}

.close{
 opacity: .5;
    color:white;
}

.panel-default{
	border: none;
}

.panel-default>.panel-heading, .panel-body, .panel-footer {
    color: #000;
    background-color: #000000;
    border-color: red;
}

.tooltip-inner{
       border: 1px solid red; 
    padding: 4px 1px;
}

.form-control{
    background-color: black;
    border: 2px solid;
    color: white;
}

#chartA{
 filter:invert(100%);   
}

.glyphicon {
    font-size: 1em;
}

.icolor1 {
    color: red;
}
.icolor2 {
    color: #2ee479;
}

#chart11, #chart12, #chart13, #chart21, #chart22, #chart23, #chart31, #chart32, #chart33{
    filter: invert(100%) hue-rotate(30deg) brightness(2);
}

#status, #f7931a{
 color: #e0e0e0;   
}

.search-cat {
    color: #ffffff;
}

.search-kbd2 {
    background-color: #000000;
    color: #ccc;
}

.search-brd-bitcoin-cash {
    border-left: 6px solid rgba(3, 255, 28, 0.47);
}

.search-brd-bitcoin {
    border-left: 6px solid rgba(247, 147, 26, 0.8);
}

.search-brd-wp {
    border-left: 6px solid rgba(164, 46, 255, 0.72);
}

.timeline-Header-title {
    color: #ffffff;
}

.timeline-Header {
    background-color: black;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
}

.timeline-Widget {
    background-color: transparent;
}

.timeline-TweetList-tweet {
    border-top: 1px solid rgba(164, 46, 255, 0.85) !important;
    background: black;
}

.timeline-Footer{
    background-color: black;
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
}

.panel{
 background-color: transparent;   
}

.panel-body {
    color: #e0e0e0;
    background-color: rgba(23, 23, 23, 0.8);
    border: 1px solid rgba(164, 46, 255, 0.55);
    border-radius: 10px;
}
}
