css:
@-moz-document domain("fork.lol") {
html{
	background: black;
}

#app{
	background: black;   
}

#canvas-dari, #canvas-reward, #canvas-fees, #canvas-feepct, #canvas-inflation, #canvas-blocktime, #canvas-blocksize, #canvas-height, #canvas-cdd, #canvas-txs, #canvas-txfee-sat, #canvas-hashrateabs, #canvas-work, #canvas-retarget, #canvas-speed, #canvas-difficulty, #canvas-attack, #canvas-attack-full, #canvas-hashrate, #canvas-difficulty-expected, #canvas-rdari, #canvas-feemarket, #canvas-algo-BTC{
    filter: invert(100%) hue-rotate(0deg) contrast(200%) brightness(2);
;
}

a {
    color: rgb(104, 177, 255);
}

.bgdarilead {
    background-color: rgba(58,201,56,.1);
    border: 1px dashed green;
}

.thead-inverse th {
    color: #fff;
    background-color: #244656;
}

.trfirs{
	color: white;   
}

.advantage_sm {
    color: #16ff16;
}

.card-block {
	background: black;
	color: #e0e0e0;
}

.lol-window-inner {
    padding: 0;
    padding-top: 0;
}

.navbar-light .navbar-brand, .navbar-light .navbar-toggler {
    color: white;
    background: black;
}

.navbar-brand>img{
 filter: contrast(10%);   
}

.bg-faded {
    background-color: #000000;
    border-bottom: 1px solid rgba(255, 255, 255, 0.28);
}

code {
    color: #dc565d;
    background-color: #1d1d1d;
}

.card-footer {
    background-color: #000000;
    border-top: 1px solid grey;
}

.hidari, .green {
    color: #52ff52;
}

.toggle-stat {
    color: #5eabff;
}

.times-norm {
    background-color: rgba(0, 255, 59, 0.46);
}

.times-high {
    background-color: rgba(255, 0, 0, 0.6);
}
}
