css:
@-moz-document url-prefix("https://coinmarketcap.com/") {
body
{
	background-color: #0c0c0c;   
}

h2, td, th, .text-large, .pull-left, .text-center, #conversion-result-right, #conversion-result-left, .form-horizontal
{
	color: #e0e0e0;    
}

a
{
	color: #67a3d8;    
}

.navbar-default .navbar-nav>li>a:hover, .navbar-default .navbar-nav>li>a:focus
{
	color: #67a3d8;    
}

.navbar-default
{
	background-color: #2d2d2d;
    color: #e0e0e0;
    border:none;
}

#top-nav-outer-container, .navbar-default .navbar-nav>.open>a, .dropdown-menu
{
	background-color: #2d2d2d;
    color: #e0e0e0;
}

.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:hover, .navbar-default .navbar-nav>.open>a:focus {
    background-color: rgba(0, 0, 0, 0);
    color: white;
}

.nav>li>a:hover, .nav>li>a:focus {
    text-decoration: none;
    background-color: #2d2d2d !important;
    color: white;
}

img.sparkline {
    -webkit-filter: invert(100%) hue-rotate(-40deg) saturate(300%); /* Chrome, Safari, Opera */
    filter: invert(100%) hue-rotate(-40deg) saturate(300%);
    border: 1px solid #e6e6e6;
}

#charts{
    -webkit-filter: invert(80%) hue-rotate(-180deg) saturate(300%); /* Chrome, Safari, Opera */
    filter: invert(100%) hue-rotate(-120deg) saturate(100%);
}

.currency-logo {
    width: 20px;
    height: 20px;
}

.coin-summary-item h3
{
    background-color: #000;
    color: white;
}

.coin-summary-item-detail, .text-large2
{
    color: #e0e0e0;
}

.details-text-medium{
 color: grey;   
}

#title a
{
	color: white;   
}

.dropdown-menu>li>a
{
	color: white;   
}

table#currencies>tbody>tr>td {
    padding: 6px;
    vertical-align: middle;
    border-top: 0;
    border-bottom: 1px solid #303030;
}

.table>thead>tr>th {
    vertical-align: bottom;
    border-bottom: 2px solid #303030;
}
thead tr {
    border-top: 1px solid #303030;
}

.nav-tabs>li.active>a
{
    color: #fff;
    background-color: #303030;
    border: 1px solid #383838 !important;    
}

.pagination>li>a:hover, .pagination>li>span:hover, .pagination>li>a:focus, .pagination>li>span:focus {
	color: #67a3d8;    
    background-color: #303030;
    border-color: #303030;
}
.pagination>li>a{
    color: white;
    background-color: #202020;
    border-color: #303030;
}

.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus
{
	color: #67a3d8;    
    
}

/*Removes ads*/
#icobanner-wrapper, .skyscraper, #leaderboard, #leaderboard-bottom
{
    display: none !important;
}

.form-control, select.form-control + .chosen-container.chosen-container-single .chosen-single {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #aaa;
    background-color: #202020;
    background-image: none;
    border: 1px solid #303030;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}

.well {
    background-color: #000000;
}

.dataTable td.sorting_1 {
    border: none;
    /*border-right: 1px solid #333333;*/
}

.sorting_asc {
    border: 1px solid #333333;
}
