    white-space: inherit;
}

.highcharts-background{
 fill: black !important;   
}*/
#ct_70unrF{
    display: none;
}

.global-stats.desktop.outer-container {
    background-color: #000000;
    color: white;
    border-bottom: 1px solid #e7e7e7;
}

.global-currency-dropdown, .language-dropdown {
    background-color: #000;
}

.s-s-iota {
    filter: invert(100%);
}

.table thead tr th{
    background-color: #000;
}
}
