css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vdiplomacy.net"), domain("vdiplomacy.com") {

* {
    font-family:"Open Sans", Arial;
  }
  
  body {
    background-image:none;
    background-color:#d9d9d9;
    font-size:13px;
    padding:0;
    margin:0;
  }
  
  div.content, 
  div.content-bare, 
  #header-container {
     width:100%;
     max-width : none !important;
     padding:0;
  }

  div.content > div, 
  div.content-bare > div, 
  #header-container {
    width:100%; 
    max-width:1400px;
    margin: 10px auto;
  }
  #seperator-fixed {
    display:none;
  }
  
  #mapstore {
    max-width:none;
  }
  
  .map {
     width:100%;
  }

  iframe#mapImage {
    width:99% !important;
    height:50vw;
    border:none !important;
  }
  .shrinkToFit {
    margin:0 auto;
    display:block;
  }
  
  div.chatbox {
    max-height: 360px;
    border:none;
  }
  
  TD {
    padding:3px;
  }
  TD.time {
    width:8% !important;
  }
  
  td.send, td.left {
    width: 7% !important;
  }
  
  div.hr, div.hrthin {
    border:none;
  }
  
  
  TABLE.orders {
    border:none;
    width:100%;
  }
  TABLE.orders tr {
    display:inline-block;
    width:50%;
  }
  
  .memberLeftSide br, .memberUserDetail br, .memberGameDetail br {
    content: "" !important;
    display: none !important;
  }
  
  .memberLeftSide span, .memberUserDetail span, .memberGameDetail span {
    display: inline-block;
    margin-right: 5px;
  }
  .memberLeftSide strong, .memberUserDetail strong, .memberGameDetail strong {
    display: inline-block;
    margin-right: 5px;
  }
  
  .memberProgressBar, .memberProgressBarBlank {
    border:none;
  }

}
