css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://m.ruliweb.com/") {
  #header .banner{
    display: none;
  }
  #content #___gcse_0{
    display: none;
  }
  #content #m_category{
    border-top: 0px solid #fff;
  }
  
  #content .list table {
    width: 100%;
  }
  
  #content .list tr {
    border-bottom: 1px solid #eee;
  }
  
  #content .list td {
    padding: 5px 5px 5px 5px !important;;
    height: 47px;
    font-size: 12px;
    color: #999;
  }
  
  #content .list td:first-child{
    position: absolute;
    vertical-align: top;
  }
  #content .list td:last-child{    
    vertical-align: bottom;
    text-align: right;
    width: 100%;
  }
  
  #content .list td a {
    white-space: nowrap;
    width: 450px;
    height: 100%;
    font-weight: bold;
    font-size: 14px;
    display: block;
  }
  
  .ad_go_wrapper, 
  .ad_wrapper,
  .mo_ad_main_ti_wrapper,
  .nbp_container,
  .fixed_ad_wrapper{
    display: none;
  }
  
  .fixed_layer1 {
  	bottom: 10px;
  }
}
