css:
@-moz-document domain("trello.com") {
/*
	Theme Name:     Dark Matter - Trello
	Theme URI:      https://twistedjackal.com
	Description:    A custom Trello theme designed by Twisted Jackal.
	Author:         Malachi Draven
	Author URI:     http://www.newennead.com
	Version:        1.0.0
*/
@font-face {
  font-family: edoregular;
  src: url("/wp-includes/fonts/edo-webfont.eot");
  src: url("/wp-includes/fonts/edo-webfont.eot?#iefix") format("embedded-opentype"), url("/wp-includes/fonts/edo-webfont.woff") format("woff"), url("/wp-includes/fonts/edo-webfont.ttf") format("truetype"), url("/wp-includes/fonts/edo-webfont.svg#edoregular") format("svg");
  font-weight: normal;
  font-style: normal; }
body {
  color: #666; }

a {
  color: #666; }
  a:hover {
    color: #999; }

textarea {
  color: #999999; }

.u-fancy-scrollbar::-webkit-scrollbar-track-piece, ::-webkit-scrollbar-track {
  background: #111;
  border: 1px solid #000; }

::-webkit-scrollbar-thumb {
  background: #0b0b0b !important;
  border: 1px solid #000; }

::-webkit-scrollbar {
  width: auto; }

.header-btn {
  background: #222;
  color: #999; }
  .header-btn:hover {
    color: #999; }

.icon-lg.light, .icon-lg.light-hover:hover, .icon-sm.light, .icon-sm.light-hover:hover, .light-hover:hover .icon-lg, .light-hover:hover .icon-sm {
  color: #999; }

.board-header-btn {
  color: #999; }
  .board-header-btn:hover {
    color: #999; }

input[type=text].header-search-input {
  background-color: #222; }

input[type=text].header-search-input:hover {
  background-color: #333; }

input[type=text].header-search-input.focus, input[type=text].header-search-input.focus:hover {
  background-color: #333; }

.board-menu-tab-content {
  background-color: #0b0b0b;
  border: 1px solid #000;
  box-shadow: 0 0 5px #000; }

.board-menu-navigation-item-link {
  color: #666; }
  .board-menu-navigation-item-link:hover {
    color: #999;
    background-color: #222; }

.board-menu-navigation-item-link:not(.disabled):hover .board-menu-navigation-item-link-icon {
  color: #999; }

#surface {
  background-image: url("https://i.imgur.com/AU4yGdu.png"); }

#board {
  padding-right: 0;
  padding-left: 10px;
  overflow: visible; }

.list-wrapper.mod-add {
  display: flex;
  position: absolute;
  top: -38px;
  margin-left: 33.25%;
  width: auto;
  height: 30px;
  line-height: 30px;
  padding: 0;
  text-align: center;
  background-color: transparent;
  z-index: 999; }

.list-wrapper.mod-add form {
  display: flex; }

.list-wrapper.mod-add.is-idle {
  background: transparent;
  padding-left: 7px; }

.list-wrapper.mod-add .placeholder {
  color: #999;
  padding: 0; }

.list-wrapper.mod-add .list-name-input {
  background: #0b0b0b;
  border-color: #050505; }

.list-wrapper.mod-add .list-add-controls {
  margin: 0;
  margin-left: 10px;
  overflow: visible; }

.list-wrapper.mod-add.is-idle .list-add-controls {
  overflow: hidden; }

.list-wrapper {
  padding-top: 3px;
  width: 300px;
  margin: 0;
  margin-right: 20px; }
  .list-wrapper:first-child {
    margin-left: 0; }
  .list-wrapper:nth-last-child(2) {
    margin-right: 0; }

.list {
  background: #0b0b0b;
  box-shadow: 0 1px 5px 2px #000, inset 0 0 2px 0px rgba(255, 255, 255, 0.05); }

textarea.list-header-name {
  color: #009999; }

.list-header-extras-menu:hover {
  background: #171717; }

.list-header .icon-sm {
  color: #009999 !important; }

.pop-over {
  background: #0b0b0b;
  border: 1px solid #000;
  border-bottom-color: #000;
  box-shadow: 0 1px 6px #000; }

.pop-over-header-title {
  border-bottom: 1px solid #222; }

hr {
  background: #222; }

.pop-over-list li > a:hover {
  background-color: #111;
  color: #999; }

.open-card-composer:hover {
  background-color: #111111;
  color: #999999; }

.icon-overflow-menu-horizontal:hover {
  background-color: #171717; }

.list-card {
  background-color: #111111;
  border-bottom: 1px solid #000;
  box-shadow: 0 1px 5px 1px rgba(0, 0, 0, 0.65), inset 0px 0px 3px 0px rgba(255, 255, 255, 0.05); }

.list-card-title {
  color: #999999; }

.list-card.active-card {
  background-color: #171717;
  border-bottom-color: #111111; }

.list-card-operation {
  background-color: #171717; }
  .list-card-operation:hover {
    background-color: #252525; }

.icon-edit {
  color: #999999 !important; }

.checklist-item-checkbox {
  background: #333;
  border: 1px solid #111; }

.aj-item-name {
  color: #666; }

.checklist-new-item.focus .checklist-new-item-text, .checklist-new-item.focus .checklist-new-item-text:focus, .checklist-new-item.focus .checklist-new-item-text:hover {
  background: #222;
  border-color: #000;
  box-shadow: inset 0 0 3px #000;
  color: #999; }
  .checklist-new-item.focus .checklist-new-item-text::placeholder, .checklist-new-item.focus .checklist-new-item-text:focus::placeholder, .checklist-new-item.focus .checklist-new-item-text:hover::placeholder {
    color: #666; }

.icon-lg, .icon-sm {
  color: #666; }

.badge {
  color: #666; }

.badge.is-complete {
  background-color: transparent;
  color: #666; }
  .badge.is-complete .badge-icon {
    color: #548c38; }

.list-card.placeholder {
  background: #050505;
  border: none;
  box-shadow: none; }

.window {
  background-color: #111;
  border: 1px solid #000;
  box-shadow: 0 0 5px #000; }

.window-header-icon {
  color: #009999;
  top: 1px; }

.window-title textarea {
  color: #009999; }

.dialog-close-button:hover {
  color: #999; }

.card-detail-item-add-button {
  background: #222;
  color: #666; }
  .card-detail-item-add-button:hover {
    background: #333;
    color: #999; }

.quiet-button {
  color: #666; }

.quiet-button:hover {
  background: #222;
  color: #666; }

.quiet, .quiet a {
  color: #666; }
  .quiet:hover, .quiet a:hover {
    color: #999; }

.button-link {
  background: #222;
  box-shadow: 0 1px 0 0 #0b0b0b;
  color: #666; }

.button-link:hover {
  background: #333;
  box-shadow: 0 1px 0 0 #0b0b0b;
  color: #999; }

.trello-tb-wrapper .toggl-button {
  color: #666; }

.trello-tb-wrapper:hover .toggl-button {
  color: #999; }

.checklist-progress-bar {
  background: #222; }

.checklist-progress-bar-current {
  background: #006666; }

.checklist-item {
  transition: all .2s; }

.checklist-item:hover {
  background: #0c0c0c;
  color: #999; }

.checklist-item-checkbox.enabled:hover {
  background-color: #222; }

.checklist-item-fade-out .checklist-item-details-text, .checklist-item-state-complete .checklist-item-details-text {
  color: #333; }

.checklist-item-fade-out .checklist-item-checkbox, .checklist-item-state-complete .checklist-item-checkbox {
  background-color: #222;
  border-color: #111; }

.checklist-item-fade-out .checklist-item-checkbox.enabled:hover, .checklist-item-state-complete .checklist-item-checkbox.enabled:hover {
  background-color: #333; }

.toggl-button.min {
  padding-left: 25px;
  top: 4px; }

.checklist-new-item-text:hover {
  background: #222; }

.comment-frame {
  background-color: #222; }

.button.disabled, .button.disabled:active, .button.disabled:hover, button.disabled, button.disabled:active, button.disabled:hover, input[type=button].disabled, input[type=button].disabled:active, input[type=button].disabled:hover, input[type=submit].disabled, input[type=submit].disabled:active, input[type=submit].disabled:hover, input[type=submit]:disabled {
  background: #222;
  color: #444; }

.button.primary, button.primary, input[type=button].primary, input[type=submit].primary {
  background: #222;
  box-shadow: 0 1px 0 #050505;
  color: #999; }

.button.primary:hover, button.primary:focus, button.primary:hover, input[type=button].primary:focus, input[type=button].primary:hover, input[type=submit].primary:focus, input[type=submit].primary:hover {
  background: 100% #333; }

.action-comment {
  background-color: transparent;
  box-shadow: none; }

.phenom {
  border-bottom: 1px solid #222; }

.pop-over-content .iframe-input, button, .pop-over-content input:not([type=file]), .pop-over-content textarea {
  background-color: #222 !important;
  border-color: #111 !important;
  box-shadow: none !important;
  color: #666; }

.pop-over-section h4 {
  border-bottom: 1px solid #222; }

.empty, .empty-list {
  background: #222;
  border: none; }

.pop-over-emoji-list.collapsed li.active a:hover, .pop-over-emoji-list.collapsed li.active.selected a .member, .pop-over-emoji-list.collapsed li.selected a, .pop-over-emoji-list:not(.collapsed) li.selected a, .pop-over-member-list:not(.collapsed) li.selected a {
  background-color: #333; }

.card-label-edit-button:hover {
  background: #333;
  color: #999; }

.pika-single {
  background: #222; }

.pika-single .pika-table td {
  background: #222; }

table, td, th {
  border: none !important; }

thead {
  background: #333;
  color: #999; }

.button.negate, button.negate, input[type=button].negate, input[type=submit].negate {
  color: #999 !important; }

.uploader:hover .fakefile:not(.button-link) {
  background: #111;
  border-color: #111;
  color: #999; }

}
