css:
@-moz-document url-prefix("http://forums.sploder.com") {
body {
    background:none;
}
html {
    background:#111 url("/*[[forumbackgroundurl]]*/") !important ;
    background-size: 100%;
}
#border-wrap {
    background: rgba(0, 0, 0, 0.52);
}
#border-body {
    background-color:transparent ;
    background:none;
}
#header, #header div.frame, #content_section div.frame { 
    background:none;
}
.information {
    background-color:rgba(0, 0, 0, 0.3);
}
#content_section {
    border-radius:35px;
    background:  rgba(0, 0, 0, 0.2);
}
.stickybg, .stickybg2, .inner blockquote {
      background:  rgba(0, 0, 0, 0.5);        
 }
.windowbg2, .windowbg, .poster, div.windowbg2 .poster {
      background:  rgba(0, 0, 0, 0.15);        
}
.windowbg span.botslice span, .windowbg span.botslice, .windowbg span.topslice, .windowbg span.topslice span, .roundframe, span.upperframe, span.lowerframe, span.upperframe span, span.lowerframe span, #forumposts span.botslice, .windowbg2 span.botslice span, .windowbg2 span.topslice span, .windowbg2 span.topslice span, .windowbg2 span.topslice {
    background:none;
}
div.cat_bar_boardindex {
    padding: 2px;
    padding-left:20px;
}
}
