css:
@-moz-document url-prefix("https://mastodon.social/web"), url-prefix("https://mastodon.social/settings"), url-prefix("https://mastodon.social/tags"), url-prefix("https://mastodon.social/oauth"), url-prefix("https://mastodon.social/auth"), url-prefix("https://writing.exchange/")
{
/* MastoUI */

/* UI */

  .drawer__header a:hover
  {
    background: #56a3db;
    -webkit-transition: background .2s ease-out;
    transition: background .2s ease-out;
    color: #fff !important;
  }

  .drawer__header
  {
    background-color: #f0f0f0;
  }

  .drawer__inner
  {
    background-color: #f0f0f0 !important;
  }

  .scrollable
  {
    background-color: #f0f0f0 !important;
  }

  .column-link:hover
  {
    background: #56a3db;
    color: #fff !important;
  }

  .account__header
  {
    background-color: inherit;
  }

  .notification__message
  {
    margin-left: 30px;
    padding: 8px 0;
    padding-bottom: 0;
    cursor: default;
    color: #9baec8;
    font-size: 13px;
    position: relative;
  }

  .status__relative-time
  {
    font-size: 12px;
    color: #2b90d9 !important;
  }

  .status__content p a
  {
    border-bottom: 0px !important;
    color: #2b90d9 !important;
  }

  .status__avatar .account__avatar:hover
  {
    border-radius: 30px;
    -webkit-transition: all .4s ease-out;
    transition: all .4s ease-out;
  }

  .status__avatar .account__avatar-overlay .account__avatar-overlay-base:hover
  {
    border-radius: 30px;
    -webkit-transition: all .4s ease-out;
    transition: all .4s ease-out;
  }

  .status__avatar .account__avatar-overlay .account__avatar-overlay-overlay:hover
  {
    border-radius: 30px;
    -webkit-transition: all .4s ease-out;
    transition: all .4s ease-out;
  }

  .scrollable div
  {
    border-bottom: none!important;
  }

  .status__video-player-mute
  {
    bottom: 4px;
    top: inherit;
  }

  .counter-label,
.bio .account__header__content,
.bio .account__header__content .ellipsis,
.next_page,
.footer,
.footer a,
.form-footer a,
input.file .block-button
  {
    color: #fff!important;
  }

  .admin-wrapper .simple_form
  {
    background: #f0f0f0;
    border-radius: 5px;
    padding: 10px;
  }

  .compose-form__buttons
  {
    background-color: #fff!important;
    box-shadow: none!important;
  }

  .simple_form a.block-button
  {
    color: #fff !important;
  }

  .admin-wrapper .content hr
  {
    border-bottom: 0px solid #282c37;
  }

  ::-webkit-scrollbar-track:hover
  {
    background: rgba(0, 0, 0, 0.1);
  }

  .reply-indicator__content
  {
    white-space: normal;
  }

  .status__content p,
.reply-indicator__content p
  {
    margin-bottom: 5px;
  }

  div.media-spoiler-video-play-icon
  {
    text-align: center;
    width: 36px;
    left: 50%;
  }

  div.media-spoiler-video-play-icon >i.fa-play
  {
    margin-left: 6px;
  }

  .notification-bar-message,
.notification-bar-title,
.follow-prompt,
.about-body div,
.about-body a,
.pagination .prev,
.pagination .next
  {
    color: #FFF!important;
  }

  .muted .status__content p,
.muted .status__content a,
.drawer__inner>div>div>strong,
.account__header span,
div,
.detailed-status__display-name strong,
.setting-text:focus,
.setting-text:active,
.navigation-bar__profile-account
  {
    color: #000!important;
  }

  .status__content,
.display-name,
.status__display-name strong,
.account__display-name strong,
a,
.notification,
label span,
.column-settings__section>span,
.status-card p,
.status-card strong,
.getting-started p,
.admin-wrapper .simple_form input,
.admin-wrapper .simple_form textarea,
.admin-wrapper .simple_form label,
.admin-wrapper .content p,
.admin-wrapper .content strong,
.admin .content h2
  {
    color: #323232!important;
  }

  .sidebar a.selected,
.react-toggle .react-toggle-track,
.react-toggle-track div
  {
    background-color: #2b90d9!important;
  }

  .sidebar a:hover,
.react-autosuggest__container input,
.app-holder>div,
.admin-wrapper,
.sidebar-wrapper,
.admin-wrapper .sidebar-wrapper ul ul
  {
    background-color: #fff!important;
  }

  input.search__input,
input.search__input:focus,
.flash-message,
.load-more:hover,
.column,
.landing-strip,
.search-results__header
  {
    background: #f0f0f0;
  }

  .search__icon .fa,
.search__input,
.account__action-bar__tab span
  {
    color: #000;
  }
  
@media screen and (max-width: 1024px)
  {
    .column:last-child, 
      .drawer:last-child 
    {
        margin-right: 0;
  }

  .column:first-child,
    .drawer:first-child
  {
    margin-left: 0;
  }
}

@media (min-width: 1025px)
{
  .column
  {
    margin-bottom: 10px;
    margin-right: 5px;
    margin-top: 10px;
  }
}

@media screen and (min-width: 1900px)
{
  .column, .drawer
  {
    margin-top: 2vh;
    padding-bottom: 0px;
    padding-left: 5px;
    padding-right: 5px;
    padding-top: 0px;
  }
}

.table>tbody>tr:nth-child(odd)>td,
.table>tbody>tr:nth-child(odd)>th
{
  color: white;
}

.spoiler-button,
.status__video-player-spoiler
{
  z-index: 100;
  top: 4px;
  left: 4px;
  position: absolute;
}

.media-gallery__item-thumbnail
{
  background-size: auto;
}

.column-header__button.active,
.column-header__button.active:hover
{
  background: #f0f0f0 !important;
  color: #9baec8 !important;
}

.column-header__button,
.column-header__back-button
{
  background: #f0f0f0;
}

.column-header__collapsible div
{
  background: #f0f0f0 !important;
}

.reply-indicator__content .status__content__spoiler-link:hover,
.status__content .status__content__spoiler-link:hover
{
  background: #419bdd;
  text-decoration: none;
}

.reply-indicator__content .status__content__spoiler-link,
.status__content .status__content__spoiler-link
{
  background: #2588d0;
  -webkit-transition: all .2s ease-out;
  transition: all .2s ease-out;
}

.muted .status__avatar
{
  opacity: 1;
}

.reply-indicator__content,
.status__content
{
  font-size: 13px;
  line-height: 20px;
  word-wrap: break-word;
  font-weight: 400;
  overflow: hidden;
  white-space: pre-wrap;
}

.status__content--no-action p
{
  font-size: 17px !important;
}

.status__content__spoiler-link
{
  border-radius: 2px;
  background: transparent;
  border: 0;
  color: #fff;
  font-weight: 300;
  font-size: 10px !important;
  text-transform: lowercase;
  line-height: inherit;
  cursor: pointer;
  float: right;
  margin-top: 2px;
}

.compose-form__buttons-wrapper
{
  background-color: #fff;
}

.character-counter__wrapper
{
  line-height: 36px;
  margin-right: 16px;
  padding-top: 10px;
}

.character-counter
{
  font-size: 13px !important;
}

.button.button--block
{
  display: block;
  width: 36px;
  height: 36px;
  border-radius: 90px;
  padding: 0px !important;
  font-size: 10px;
  margin-right: 10px;
  margin-bottom: 10px;
}

.compose-form__publish-private
{
  font-size: 8px !important;
}

.compose-form__buttons
{
  display: -webkit-inline-box;
  background: white!important;
  box-shadow: none!important;
  margin-bottom: 0px;
  margin-top: 6px;
}

.column-header
{
  margin-top: -10px;
  margin-left: -5px;
  margin-right: -10px;
  background-color: #f0f0f0 !important;
}

#Getting-started
{
  margin-top: -10px;
  margin-left: -5px;
  margin-right: -10px;
  background-color: #f0f0f0 !important;
}

::-webkit-scrollbar-thumb
{
  background: #2588d0 !important;
}

::-webkit-scrollbar-thumb:hover
{
  background: #419bdd !important;
}

.column-subheading
{
  background: #fff !important;
  margin-left: -5px;
  margin-right: -10px;
  border-left: 3px solid #2588d0;
}

.column-link:hover
{
  background: #419bdd !important;
}

.column-link
{
  margin-left: -5px;
  margin-right: -10px;
  background-color: #f0f0f0 !important;
}

.column-back-button
{
  margin-top: -10px;
  margin-left: -5px;
  margin-right: -10px;
  background-color: #f0f0f0 !important;
}

.detailed-status
{
  background-color: #f0f0f0 !important;
}

.detailed-status__action-bar
{
  border-top: 0px solid #393f4f;
  background-color: #f0f0f0 !important;
}

.dropdown--active .dropdown__content>ul>li>a:hover
{
  background: #419bdd !important;
  color: #fff !important;
}

.dropdown--active .dropdown__content>ul>li>a
{
  color: #000;
}

.dropdown--active .dropdown__content>ul,
.dropdown__content-list-link
{
  background: #fff !important;
}

.empty-column-indicator
{
  background: #f0f0f0 !important;
  color: #323232 !important;
}

div.scrollable
{
  margin-left: -5px;
}

div.scrollable > div
{
  margin-left: 5px;
}

div.account-timeline__header
{
  margin-left: -10px;
  margin-right: -10px;
}

.spoiler-button.spoiler-button--visible
{
  margin-left: 3px;
}

.account__header .account__header__content
{
  color: #e3e3e3 !important;
}

span.account__header__username,
span.account__header__display-name
{
  color: #e3e3e3 !important;
}

.account__action-bar__tab
{
  border-left: 0px;
}

.compose-form__warning
{
  -webkit-box-shadow: 0 0px 0px rgba(0, 0, 0, 0);
  box-shadow: 0 0px 0px rgba(0, 0, 0, 0);
  border-radius: 0px;
}

.react-toggle-track-check,
.react-toggle-track-x
{
  display: none;
}

.react-toggle > .react-toggle-track
{
  background-color: #ccc!important;
}

.react-toggle--checked > .react-toggle-track
{
  background-color: #2b90d9!important;
}

.react-toggle-thumb
{
  border: 0px !important;
}

.account__header__content .hashtag,
.account__header__content .mention span
{
  color: #e3e3e3 !important;
  border-bottom: 0px !important;
  text-decoration: none !important;
}

.u-url
{
  border-bottom: 0px !important;
  color: #2b90d9 !important;
}

.status__content.status__content--with_action .permalink.mention
{
  border-bottom: 0px;
  color: #2b90d9 !important;
}

.account
{
  border-bottom: 0px;
}

.search-results__header
{
  border-bottom: 0px;
}

.fa-chevron-left:before
{
  content: "\F137";
}

.fa-chevron-right:before
{
  content: "\F138";
}

.column-header__wrapper.active:before
{
  top: 25px !important;
}

.account--follows-info
{
  margin-right: 3px;
}

.account--follows-info span
{
  color: #fff !important;
}

.account__avatar-wrapper
{
  float: left;
  margin-left: 0px;
  margin-right: 12px;
}

.account__avatar-wrapper .account__avatar:hover
{
  border-radius: 30px;
  -webkit-transition: all .4s ease-out;
  transition: all .4s ease-out;
}

.status__content.status__content--with-action p a
{
  border-bottom: 0px !important;
  color: #2b90d9 !important;
}

.account__header__content p a,
.account__header__content p a span
{
  text-decoration: none;
  color: #2b90d9 !important;
}

.dialog div h1
{
  color: #f0f0f0;
}

div.drawer__pager > div:nth-child(1):after
{
  color: #282c37;
  position: absolute;
  bottom: 5px;
  margin-left: 7px;
  content: "Theme by sl_tty@mastodon.social";
}

.status__info .status__display-name .display-name .display-name__account
{
  display: none;
}

div:nth-child(4) > div.scrollable
{
  margin-right: -4px;
}

.status__prepend
{
  margin-left: 30px;
}

.status__prepend-icon-wrapper i
{
  color: #2b90d9;
}

.notification .status .status__action-bar
{
  display: none;
}

.status:hover
{
  background: #fff;
  margin-left: -5px;
  -webkit-transition: all .2s ease-out;
  transition: all .2s ease-out;
}

.compact-header h1 a
{
  color: #d9e1e8 !important;
}

.admin-wrapper .sidebar ul a.selected
{
  color: #fff !important;
}

.admin-wrapper .sidebar ul a.selected:hover
{
  color: #323232 !important;
}

.simple_form span.hint
{
  color: #323232;
}

.simple_form .warning,
.table-form .warning
{
  background: #9baec8;
  text-shadow: 0px 0px 0 rgba(0, 0, 0, .3);
  -webkit-box-shadow: 0 0px 0px rgba(0, 0, 0, .4);
  box-shadow: 0 0px 0px rgba(0, 0, 0, .4);
  border-radius: 0px;
}
}

@-moz-document url("https://mastodon.social/")
{
  .dialog div h1
  {
    color: #f0f0f0;
  }
}

@-moz-document url-prefix("https://mastodon.social/@"), url-prefix("https://mastodon.social/user")
{
  body
  {
    background-image: url(https://assets.mastodon.social/packs/fluffy-elephant-friend-cc7707eb7d15da8501a56e0c5a4fb238.png);
    background-repeat: no-repeat;
    background-position: 130px 180px;
    background-size: 400px;
    background-color: #f0f0f0;
  }

  .status__content__spoiler-link
  {
    background: #2588d0 !important;
    -webkit-transition: all .2s ease-out;
    transition: all .2s ease-out;
    border: 0;
    color: #fff;
    font-weight: 300;
    font-size: 10px !important;
    text-transform: lowercase;
    line-height: inherit;
    cursor: pointer;
    float: right;
    margin-top: 2px;
  }

  .status__content__spoiler-link:hover
  {
    background: #419bdd !important;
    text-decoration: none;
  }

  .status__relative-time
  {
    font-size: 12px;
    color: #9baec8 !important;
  }

  .reply-indicator__content p,
.status__content p
  {
    font-size: 13px;
  }

  .activity-stream .pre-header
  {
    margin-bottom: -8px;
  }

  .activity-stream .pre-header span,
.activity-stream .pre-header .status__display-name.muted strong
  {
    color: #3d4455 !important;
    font-size: 13px;
  }

  .activity-stream .entry:first-child,
.entry
  {
    border-radius: 0px 0px 0 0;
    margin-top: 3px;
    margin-bottom: 3px;
    border-left: 2px solid #d9e1e8 !important;
  }

  .activity-stream .entry
  {
    border-left: 2px solid #d9e1e8;
    margin-bottom: 3px;
  }

  .activity-stream .entry:hover
  {
    background: #f0f0f0;
    margin-left: 5px;
    margin-right: -5px;
    -webkit-transition: all .2s ease-out;
    transition: all .2s ease-out;
  }

  .activity-stream .entry:last-child
  {
    border-radius: 0px 0px 0 0;
    margin-bottom: 0px;
  }

  .account__header__content
  {
    margin-top: -5px;
    color: #e3e3e3;
    font-size: 13px;
    font-weight: 300;
  }

  .card .name small
  {
    font-size: 14px;
    color: #e3e3e3;
  }

  .footer .domain:before
  {
    content: "Theme by sl_tty@mastodon.social | ";
  }

  .pagination .next,
.pagination .prev
  {
    text-transform: uppercase;
    color: #8aa2b8;
  }

  .pagination .current,
.pagination .gap,
.pagination .next,
.pagination .page,
.pagination .prev,
.pagination a
  {
    color: #8aa2b8;
  }

  ::-webkit-scrollbar-thumb
  {
    background: #2588d0 !important;
  }

  ::-webkit-scrollbar-thumb:hover
  {
    background: #419bdd !important;
  }

  ::-webkit-scrollbar-track:hover
  {
    background: rgba(0, 0, 0, .1);
  }

  .activity-stream
  {
    clear: both;
    -webkit-box-shadow: 0 0 0px rgba(0, 0, 0, .2);
    box-shadow: 0 0 0px rgba(0, 0, 0, .2);
  }

  .activity-stream .entry .detailed-status.light,
.activity-stream .entry .status.light
  {
    border-bottom: 0px;
  }
}
